mapper.select:
select * ${sql1} from (select ${sql2} ch.code , ch.name, concat(ch.start_date,' ~ ',ch.end_date) as yyyxq, ch.admin_name AS adminName, ch.admin_phone AS adminPhone from center_hospital ch left join center_function_authorization cf on ch.code = cf.hosp_code where ch.is_valid ='1'
and (ch.name like '%${hospName}%' or ch.code like '%${hospName}%')
and cf.order_type_code = #{orderTypeCode}
and cf.id is not null
and cf.audit_status = #{auditStatus}
GROUP by ch.code , ch.name, concat(ch.start_date,' ~ ',ch.end_date), ch.admin_name , ch.admin_phone ) aa order by aa.code desc

select cf.id, (case when cf.id is null then '0' else '1' end) as openFlag , ch.code as hospCode, ch.name as hospName, concat(ch.start_date, ' ~ ', ch.end_date) as yyyxq, cf.start_date as startDate , cf.end_date as endDate, cf.audit_status as auditStatus, ch.admin_name as adminName, ch.admin_phone as adminPhone, cd.value as orderTypeCode, cd.name, cf.remark, (date(now()) <=date(cf.start_date) and date(now()) >= date(cf.end_date) ) as sfgq from center_hospital ch left join center_code_detail cd on 1 = 1 left join center_function_authorization cf on ch.code = cf.hosp_code and cd.value = cf.order_type_code where cd.c_code ='QXDJ' and ch.is_valid ='1'
and cd.value = #{orderTypeCode}
and ch.code = #{hospCode} order by ch.code,cd.value
