mapper.select:
select * ${sql1} from (select ${sql2} ch.code , ch.name, concat(ch.start_date,' ~ ',ch.end_date) as yyyxq, ch.admin_name AS adminName, ch.admin_phone AS adminPhone from center_hospital ch left join center_function_authorization cf on ch.code = cf.hosp_code where ch.is_valid ='1'
and (ch.name like '%${hospName}%' or ch.code like '%${hospName}%')
and cf.order_type_code = #{orderTypeCode}
and cf.id is not null
and cf.audit_status = #{auditStatus}
GROUP by ch.code , ch.name, concat(ch.start_date,' ~ ',ch.end_date), ch.admin_name , ch.admin_phone ) aa order by aa.code desc

select group_concat(cf.id) as id, (case when max(cf.id) is null then '0' else '1' end) as openFlag , ch.code as hospCode, cd.code as serviceCode, ch.name as hospName, concat(ch.start_date, ' ~ ', ch.end_date) as yyyxq, cf.start_date as startDate , cf.end_date as endDate, cf.audit_status as auditStatus, ch.admin_name as adminName, ch.admin_phone as adminPhone, group_concat(cf.order_type_code order by cf.order_type_code) as orderTypeCode, cd.name, cf.remark, (date(now()) <=date(cf.start_date) and date(now()) >= date(cf.end_date) ) as sfgq from center_hospital ch left join center_function cd on 1 = 1 left join center_function_authorization cf on ch.code = cf.hosp_code and cd.code = cf.service_code where ch.is_valid ='1' and ch.code = #{hospCode}
and cd.code = #{serviceCode}
group by ch.code, cd.code, ch.name, cf.start_date, cf.end_date, cf.audit_status , ch.admin_name, ch.admin_phone, cd.name, cf.remark, cf.start_date, cf.end_date order by ch.code,cf.order_type_code
