mapper.update:
update center_hospital
code = #{code}, name = #{name}, pro_code = #{proCode}, city_code = #{cityCode}, area_code = #{areaCode}, disease_code = #{diseaseCode}, oper_code = #{operCode}, buli_image_path = #{buliImagePath}, address = #{address}, phone = #{phone}, fax = #{fax}, email = #{email}, post_code = #{postCode}, hosp_image_path = #{hospImagePath}, introduce = #{introduce}, website = #{website}, legal_name = #{legalName}, legal_phone = #{legalPhone}, admin_name = #{adminName}, admin_phone = #{adminPhone}, level_code = #{levelCode}, pym = #{pym}, wbm = #{wbm}, long_lat = #{longLat}, start_date = #{startDate}, end_date = #{endDate}, encrypt_end_date = #{encryptEndDate}, encrypt_start_date = #{encryptStartDate}, nation_code =#{nationCode}
where id = #{id}

mapper.select:
select
id, code, name, pro_code, city_code, disease_code, oper_code, area_code, buli_image_path, address, phone, fax, email, post_code,
hosp_image_path, introduce, website, legal_name, legal_phone, admin_name, admin_phone, level_code, pym, wbm, long_lat, start_date,
end_date, is_valid, crte_id, crte_name, crte_time,service_status
from center_hospital


        and id = #{id}


        and code = #{code}


        and name = #{name}


        and is_valid = #{isValid}

mapper.delete:
delete from center_hospital where
id in
#{id}

mapper.select:
select count(code) from center_hospital
where
code = #{code}

    and id != #{id}

update.foreach:
UPDATE center_sync_flow

    status_code = #{flow.statusCode},
    message = #{flow.message},
    creat_time = #{flow.creatTime}


    hosp_code = #{flow.hospCode} and type = #{flow.type}

mapper.update:
UPDATE center_root_database a
a.type = #{type}, a.jdbc_driver = #{jdbcDriver} , a.root_user = #{rootUser} , a.root_password = #{rootPassword} , a.root_url = #{rootUrl} , a.hosp_url = #{hospUrl}
id = #{id}
