mapper.select:
select
id, code, password, is_in_job, name, age, birthday, gender_code, nation_code, native_place, marry_code,
cert_code, cert_no, photo_image, address, email, phone, pswd_err_cnt, last_login_time, last_login_ip,
status_code, pym, wbm, crte_id, crte_name, crte_time
from center_user where 1=1

    and id = #{id}


    and code = #{code}


    and is_in_job = #{isInJob}


    and cert_no = #{certNo}


    and email = #{email}


    and pswd_err_cnt = #{pswdErrCnt}


    and last_login_time = #{lastLoginTime}


    and last_login_ip = #{lastLoginIp}


    and status_code = #{statusCode}


    and ( name like concat('%',#{keyword},'%')
    or code like concat('%',#{keyword},'%')
    or phone like concat('%',#{keyword},'%')
    )

order by crte_time desc

mapper.update:
update center_user set
status_code = #{statusCode}
where id in

    #{item}

mapper.select:
select * from center_user where 1=1

    and code = #{code}


    and id !=#{id}

select * from center_user
where id in

    #{item}

select e.code id,e.name label,ifnull(e.up_code,'-1') parentId,e.url url,'2' as isMenuOrBtn from center_user a join center_user_role b on a.code=b.user_code join center_role c on b.role_code=c.code join center_role_menu d on c.code=d.role_code join center_menu e on d.menu_code=e.code where c.is_valid='1'
and a.code=#{userCode}
order by e.seq_no

select count(id) from center_user where 1=1

    and id !=#{id}


    and cert_no=#{certNo}

mapper.update:
update center_user set
password = #{newPasswordByMd5}
where
id = #{id}
