mapper.select:
select
id,  code, name, type_code, technology_code, price, unit_code, biz_type, biz_code, container_code, specimen_code, use_scope_code, doctor_level_code, opeartion_code, insure_list_code, gender_code, min_age, max_age, is_cost, is_stop_same, is_stop_same_not, is_stop_myself, pym, wbm, is_valid, crte_id, crte_name, crte_time
from sync_advice


        and id = #{id}


        and code = #{code}


        and name = #{name}


        and type_code = #{typeCode}


        and technology_code = #{technologyCode}


        and price = #{price}


        and unit_code = #{unitCode}


        and biz_type = #{bizType}


        and biz_code = #{bizCode}


        and container_code = #{containerCode}


        and specimen_code = #{specimenCode}


        and use_scope_code = #{useScopeCode}


        and doctor_level_code = #{doctorLevelCode}


        and opeartion_code = #{opeartionCode}


        and insure_list_code = #{insureListCode}


        and gender_code = #{genderCode}


        and min_age = #{minAge}


        and max_age = #{maxAge}


        and is_cost = #{isCost}


        and is_stop_same = #{isStopSame}


        and is_stop_same_not = #{isStopSameNot}


        and is_stop_myself = #{isStopMyself}


        and is_valid = #{isValid}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and (name like concat('%',#{keyword},'%') or pym like concat('%',#{keyword},'%') or wbm like concat('%',#{keyword},'%'))


        and type_code in

            #{item}



order by crte_time desc

mapper.update:
update sync_advice
code = #{code}, name = #{name}, type_code = #{typeCode}, technology_code = #{technologyCode}, price = #{price}, unit_code = #{unitCode}, biz_type = #{bizType}, biz_code = #{bizCode}, container_code = #{containerCode}, specimen_code = #{specimenCode}, use_scope_code = #{useScopeCode}, doctor_level_code = #{doctorLevelCode}, opeartion_code = #{opeartionCode}, insure_list_code = #{insureListCode}, gender_code = #{genderCode}, min_age = #{minAge}, max_age = #{maxAge}, is_cost = #{isCost}, is_stop_same = #{isStopSame}, is_stop_same_not = #{isStopSameNot}, is_stop_myself = #{isStopMyself}, pym = #{pym}, wbm = #{wbm}, is_valid = #{isValid},
where id = #{id}

update.foreach:
update sync_advice
code = #{item.code}, name = #{item.name}, type_code = #{item.typeCode}, technology_code = #{item.technologyCode}, price = #{item.price}, unit_code = #{item.unitCode}, biz_type = #{item.bizType}, biz_code = #{item.bizCode}, container_code = #{item.containerCode}, specimen_code = #{item.specimenCode}, use_scope_code = #{item.useScopeCode}, doctor_level_code = #{item.doctorLevelCode}, opeartion_code = #{item.opeartionCode}, insure_list_code = #{item.insureListCode}, gender_code = #{item.genderCode}, min_age = #{item.minAge}, max_age = #{item.maxAge}, is_cost = #{item.isCost}, is_stop_same = #{item.isStopSame}, is_stop_same_not = #{item.isStopSameNot}, is_stop_myself = #{item.isStopMyself}, pym = #{item.pym}, wbm = #{item.wbm}, is_valid = #{item.isValid},
where id = #{item.id}

mapper.delete:
update  sync_advice

    is_valid = #{isValid}

where id in

    #{id}

mapper.select:
select code,name from
    base_advice
where code in(
    select advice_code from base_advice_detail where  item_code = #{code}
) and is_valid = '1'
