mapper.select:
select
id, type_code, code, attach_code, name, nation_code, is_add, pym, wbm, is_valid, crte_id, crte_name,
crte_time
from sync_disease


        and id = #{id}


        and type_code = #{typeCode}


        and attach_code = #{attachCode}


        and nation_code = #{nationCode}


        and is_add = #{isAdd}


        and is_valid = #{isValid}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and (code like '%${keyword}%'
        or name like '%${keyword}%'
        or wbm like '%${keyword}%'
        or pym like '%${keyword}%')


        and type_code in

            #{item}



order by crte_time desc

select
id, type_code, code, attach_code, name, nation_code, is_add, pym, wbm, is_valid, crte_id, crte_name,
crte_time
from sync_disease


        and id = #{id}


        and type_code = #{typeCode}


        and attach_code = #{attachCode}


        and nation_code = #{nationCode}


        and is_add = #{isAdd}


        and is_valid = #{isValid}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and (code like '%${keyword}%'
        or name like '%${keyword}%'
        or wbm like '%${keyword}%'
        or pym like '%${keyword}%')


order by crte_time desc

mapper.update:
update sync_disease
type_code = #{typeCode}, code = #{code}, attach_code = #{attachCode}, name = #{name}, nation_code = #{nationCode}, is_add = #{isAdd}, pym = #{pym}, wbm = #{wbm},
where id = #{id}

update sync_disease set is_valid = #{isValid} where
id in
#{id}

mapper.select:
select count(id)
from sync_disease


        and code = #{code}


        and nation_code = #{nationCode}


        and attach_code = #{attachCode}


        and id != #{id}
