mapper.select:
select
id, type_code, big_type_code, bfc_code, code, usual_name, good_name, spec, prep_code, dosage, dosage_unit_code,
out_unit_code, in_unit_code, rate_code, price, unit_code, last_buy_price, last_split_buy_price, avg_buy_price,
avg_sell_price, split_ratio, split_unit_code, split_price, is_out, is_in, is_lvp, usage_code, insure_code,
nation_code, drug_remark, drug_img_path, max_dosage, min_dosage, gender_code, min_age, max_age, ddd, durg_code,
ph_code, antibacterial_code, is_skin, is_basic, basic_code, dan, ndan, prod_code, usual_pym, usual_wbm,
good_pym, good_wbm, is_valid, crte_id, crte_name, crte_time
from sync_drug


        and id = #{id}


        and type_code = #{typeCode}


        and big_type_code = #{bigTypeCode}


        and bfc_code = #{bfcCode}


        and code = #{code}


        and spec = #{spec}


        and prep_code = #{prepCode}


        and dosage = #{dosage}


        and dosage_unit_code = #{dosageUnitCode}


        and out_unit_code = #{outUnitCode}


        and in_unit_code = #{inUnitCode}


        and rate_code = #{rateCode}


        and price = #{price}


        and unit_code = #{unitCode}


        and last_buy_price = #{lastBuyPrice}


        and last_split_buy_price = #{lastSplitBuyPrice}


        and avg_buy_price = #{avgBuyPrice}


        and avg_sell_price = #{avgSellPrice}


        and split_ratio = #{splitRatio}


        and split_unit_code = #{splitUnitCode}


        and split_price = #{splitPrice}


        and is_out = #{isOut}


        and is_in = #{isIn}


        and is_lvp = #{isLvp}


        and usage_code = #{usageCode}


        and insure_code = #{insureCode}


        and nation_code = #{nationCode}


        and drug_remark = #{drugRemark}


        and drug_img_path = #{drugImgPath}


        and max_dosage = #{maxDosage}


        and min_dosage = #{minDosage}


        and gender_code = #{genderCode}


        and min_age = #{minAge}


        and max_age = #{maxAge}


        and ddd = #{ddd}


        and durg_code = #{durgCode}


        and ph_code = #{phCode}


        and antibacterial_code = #{antibacterialCode}


        and is_skin = #{isSkin}


        and is_basic = #{isBasic}


        and basic_code = #{basicCode}


        and dan = #{dan}


        and ndan = #{ndan}


        and prod_code = #{prodCode}


        and is_valid = #{isValid}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and (good_name like concat('%',#{keyword},'%')
        or usual_name like concat('%',#{keyword},'%')
        or good_pym like concat('%',#{keyword},'%')
        or good_wbm like concat('%',#{keyword},'%')
        or usual_pym like concat('%',#{keyword},'%')
        or usual_wbm like concat('%',#{keyword},'%'))

select
id, type_code, big_type_code, bfc_code, code, usual_name, good_name, spec, prep_code, dosage, dosage_unit_code,
out_unit_code, in_unit_code, rate_code, price, unit_code, last_buy_price, last_split_buy_price, avg_buy_price,
avg_sell_price, split_ratio, split_unit_code, split_price, is_out, is_in, is_lvp, usage_code, insure_code,
nation_code, drug_remark, drug_img_path, max_dosage, min_dosage, gender_code, min_age, max_age, ddd, durg_code,
ph_code, antibacterial_code, is_skin, is_basic, basic_code, dan, ndan, prod_code, usual_pym, usual_wbm,
good_pym, good_wbm, is_valid, crte_id, crte_name, crte_time,
(select name from sync_finance_classify where bfc_code = code ) as bfcName
from sync_drug


        and id = #{id}


        and type_code = #{typeCode}


        and big_type_code = #{bigTypeCode}


        and bfc_code = #{bfcCode}


        and code = #{code}


        and spec = #{spec}


        and prep_code = #{prepCode}


        and dosage = #{dosage}


        and dosage_unit_code = #{dosageUnitCode}


        and out_unit_code = #{outUnitCode}


        and in_unit_code = #{inUnitCode}


        and rate_code = #{rateCode}


        and price = #{price}


        and unit_code = #{unitCode}


        and last_buy_price = #{lastBuyPrice}


        and last_split_buy_price = #{lastSplitBuyPrice}


        and avg_buy_price = #{avgBuyPrice}


        and avg_sell_price = #{avgSellPrice}


        and split_ratio = #{splitRatio}


        and split_unit_code = #{splitUnitCode}


        and split_price = #{splitPrice}


        and is_out = #{isOut}


        and is_in = #{isIn}


        and is_lvp = #{isLvp}


        and usage_code = #{usageCode}


        and insure_code = #{insureCode}


        and nation_code = #{nationCode}


        and drug_remark = #{drugRemark}


        and drug_img_path = #{drugImgPath}


        and max_dosage = #{maxDosage}


        and min_dosage = #{minDosage}


        and gender_code = #{genderCode}


        and min_age = #{minAge}


        and max_age = #{maxAge}


        and ddd = #{ddd}


        and durg_code = #{durgCode}


        and ph_code = #{phCode}


        and antibacterial_code = #{antibacterialCode}


        and is_skin = #{isSkin}


        and is_basic = #{isBasic}


        and basic_code = #{basicCode}


        and dan = #{dan}


        and ndan = #{ndan}


        and prod_code = #{prodCode}


        and is_valid = #{isValid}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and (good_name like concat('%',#{keyword},'%')
        or usual_name like concat('%',#{keyword},'%')
        or good_pym like concat('%',#{keyword},'%')
        or good_wbm like concat('%',#{keyword},'%')
        or usual_pym like concat('%',#{keyword},'%')
        or usual_wbm like concat('%',#{keyword},'%'))

mapper.update:
update sync_drug
type_code = #{typeCode}, big_type_code = #{bigTypeCode}, bfc_code = #{bfcCode}, code = #{code}, usual_name = #{usualName}, good_name = #{goodName}, spec = #{spec}, prep_code = #{prepCode}, dosage = #{dosage}, dosage_unit_code = #{dosageUnitCode}, out_unit_code = #{outUnitCode}, in_unit_code = #{inUnitCode}, rate_code = #{rateCode}, price = #{price}, unit_code = #{unitCode}, last_buy_price = #{lastBuyPrice}, last_split_buy_price = #{lastSplitBuyPrice}, avg_buy_price = #{avgBuyPrice}, avg_sell_price = #{avgSellPrice}, split_ratio = #{splitRatio}, split_unit_code = #{splitUnitCode}, split_price = #{splitPrice}, is_out = #{isOut}, is_in = #{isIn}, is_lvp = #{isLvp}, usage_code = #{usageCode}, insure_code = #{insureCode}, nation_code = #{nationCode}, drug_remark = #{drugRemark}, drug_img_path = #{drugImgPath}, max_dosage = #{maxDosage}, min_dosage = #{minDosage}, gender_code = #{genderCode}, min_age = #{minAge}, max_age = #{maxAge}, ddd = #{ddd}, durg_code = #{durgCode}, ph_code = #{phCode}, antibacterial_code = #{antibacterialCode}, is_skin = #{isSkin}, is_basic = #{isBasic}, basic_code = #{basicCode}, dan = #{dan}, ndan = #{ndan}, prod_code = #{prodCode}, usual_pym = #{usualPym}, usual_wbm = #{usualWbm}, good_pym = #{goodPym}, good_wbm = #{goodWbm},
where id = #{id}

update sync_drug set is_valid = #{isValid} where
id in
#{id}
