mapper.select:
select
id, nation_code, type_code, bfc_code, code, name, abbr, spec, price, one_price, two_price, three_price,
unit_code, intension, prompt, except, remark, is_out, is_in, is_cg, is_ms, is_supp_curtain, out_dept_code,
in_dept_code, name_pym, name_wbm, abbr_pym, abbr_wbm, is_valid, crte_id, crte_name, crte_time
from sync_item


        and id = #{id}


        and nation_code = #{nationCode}


        and type_code = #{typeCode}


        and bfc_code = #{bfcCode}


        and abbr = #{abbr}


        and spec = #{spec}


        and price = #{price}


        and one_price = #{onePrice}


        and two_price = #{twoPrice}


        and three_price = #{threePrice}


        and unit_code = #{unitCode}


        and intension = #{intension}


        and prompt = #{prompt}


        and except = #{except}


        and remark = #{remark}


        and is_out = #{isOut}


        and is_in = #{isIn}


        and is_cg = #{isCg}


        and is_ms = #{isMs}


        and is_supp_curtain = #{isSuppCurtain}


        and out_dept_code = #{outDeptCode}


        and in_dept_code = #{inDeptCode}


        and abbr_pym = #{abbrPym}


        and abbr_wbm = #{abbrWbm}


        and is_valid = #{isValid}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and (code like concat('%',#{keyword},'%')
        or name like concat('%',#{keyword},'%')
        or name_pym like concat('%',#{keyword},'%')
        or name_wbm like concat('%',#{keyword},'%')


order by crte_time desc

select
id, nation_code, type_code, bfc_code, code, name, abbr, spec, price, one_price, two_price, three_price,
unit_code, intension, prompt, except, remark, is_out, is_in, is_cg, is_ms, is_supp_curtain, out_dept_code,
in_dept_code, name_pym, name_wbm, abbr_pym, abbr_wbm, is_valid, crte_id, crte_name, crte_time
from sync_item


        and id = #{id}


        and nation_code = #{nationCode}


        and bfc_code = #{bfcCode}


        and abbr = #{abbr}


        and spec = #{spec}


        and price = #{price}


        and one_price = #{onePrice}


        and two_price = #{twoPrice}


        and three_price = #{threePrice}


        and unit_code = #{unitCode}


        and intension = #{intension}


        and prompt = #{prompt}


        and except = #{except}


        and remark = #{remark}


        and is_out = #{isOut}


        and is_in = #{isIn}


        and is_cg = #{isCg}


        and is_ms = #{isMs}


        and is_supp_curtain = #{isSuppCurtain}


        and out_dept_code = #{outDeptCode}


        and in_dept_code = #{inDeptCode}


        and abbr_pym = #{abbrPym}


        and abbr_wbm = #{abbrWbm}


        and is_valid = #{isValid}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and (code like concat('%',#{keyword},'%')
        or name like concat('%',#{keyword},'%')
        or name_pym like concat('%',#{keyword},'%')
        or name_wbm like concat('%',#{keyword},'%'))


        and type_code in

            #{item}



order by crte_time desc

mapper.update:
update sync_item
nation_code = #{nationCode}, type_code = #{typeCode}, bfc_code = #{bfcCode}, code = #{code}, name = #{name}, abbr = #{abbr}, spec = #{spec}, price = #{price}, one_price = #{onePrice}, two_price = #{twoPrice}, three_price = #{threePrice}, unit_code = #{unitCode}, intension = #{intension}, prompt = #{prompt}, except = #{except}, remark = #{remark}, is_out = #{isOut}, is_in = #{isIn}, is_cg = #{isCg}, is_ms = #{isMs}, is_supp_curtain = #{isSuppCurtain}, out_dept_code = #{outDeptCode}, in_dept_code = #{inDeptCode}, name_pym = #{namePym}, name_wbm = #{nameWbm}, abbr_pym = #{abbrPym}, abbr_wbm = #{abbrWbm},
where id = #{id}

update sync_item set is_valid = #{isValid} where
id in
#{id}

mapper.select:
select count(id)
from sync_item


        and code = #{code}


        and nation_code = #{nationCode}


        and id != #{id}
