mapper.select:
select id, hosp_code, type_code, bfc_code, code, name, spec, price, unit_code, split_ratio, split_unit_code, split_price, is_supp_curtain, use_code, remark, prod_code, reg_cert_no, pym, wbm, is_valid, crte_id, crte_name, crte_time,last_buy_price,last_split_buy_price,avg_buy_price,avg_sell_price,model, (select name from base_product where base_product.hosp_code = #{hospCode} and prod_code = code) as prodName from base_material where id in
#{item}
and hosp_code = #{hospCode}

select
a.id, a.type_code, a.bfc_code, a.code, a.name, a.spec, a.price, a.unit_code, a.split_ratio, a.split_unit_code, a.split_price,
a.is_supp_curtain, a.use_code, a.remark, a.prod_code, a.reg_cert_no, a.pym, a.wbm, a.is_valid, a.crte_id, a.crte_name, a.crte_time,model,
b.name as bfcName, c.name as prodName,'2' as itemCode
from sync_material a
left join sync_finance_classify b on a.bfc_code = b.code
left join sync_product c on a.prod_code = c.code


        and a.id = #{id}


        and a.type_code = #{typeCode}


        and a.bfc_code = #{bfcCode}


        and a.code = #{code}


        and a.name = #{name}


        and a.spec = #{spec}


        and a.price = #{price}


        and a.unit_code = #{unitCode}


        and a.split_ratio = #{splitRatio}


        and a.split_unit_code = #{splitUnitCode}


        and a.split_price = #{splitPrice}


        and a.is_supp_curtain = #{isSuppCurtain}


        and a.use_code = #{useCode}


        and a.remark = #{remark}


        and a.prod_code = #{prodCode}


        and a.reg_cert_no = #{regCertNo}


        and a.is_valid = #{isValid}


        and a.crte_id = #{crteId}


        and a.crte_name = #{crteName}


        and a.crte_time = #{crteTime}


        and (a.name like concat('%',#{keyword},'%') or a.pym like concat('%',#{keyword},'%') or a.wbm like concat('%',#{keyword},'%'))


        and a.bfc_code in

            #{item}



order by a.crte_time desc

mapper.update:
update sync_material
type_code = #{typeCode}, bfc_code = #{bfcCode}, code = #{code}, name = #{name}, spec = #{spec}, price = #{price}, unit_code = #{unitCode}, split_ratio = #{splitRatio}, split_unit_code = #{splitUnitCode}, split_price = #{splitPrice}, is_supp_curtain = #{isSuppCurtain}, use_code = #{useCode}, remark = #{remark}, prod_code = #{prodCode}, reg_cert_no = #{regCertNo}, pym = #{pym}, wbm = #{wbm}, model = #{model}
where id = #{id}

update  sync_material

    is_valid = #{isValid}

where id in

    #{id}

mapper.select:
select count(code)
from sync_material
where code = #{code}

    and id != #{id}
