mapper.select:
select iv.*, bb.name as bbBedName, bb.status_code as bbStatusCode, bb.id as bbId, c.id as diseaseId, c.name as diseaseName, c.nation_code as diseaseIcd10, iv.critical_value_code as criticalValueCode,iv.patient_code, d.name wardName, iv.nursing_code, (select sum(reality_price) from inpt_cost c where c.baby_id is not null and c.baby_id !='' and c.hosp_code =#{hospCode} and c.visit_id = iv.id) as babyCost, case when ifnull(datediff(ifnull(iv.out_time, now()),iv.in_time),0) = 0 then 1 else datediff(ifnull(iv.out_time, now()),iv.in_time) end inDays, (select id from inpt_baby ib where ib.visit_id = iv.id and ib.hosp_code =#{hospCode} limit 1) as babyId from base_bed bb left join inpt_visit iv on bb.visit_id = iv.id and bb.hosp_code = iv.hosp_code left join ( select visit_id, hosp_code, max(disease_id) disease_id from inpt_diagnose a where type_code in('204','303') and is_main = '1' group by visit_id, hosp_code ) diagnose on diagnose.visit_id = bb.visit_id and diagnose.hosp_code = iv.hosp_code left join base_disease c on diagnose.disease_id = c.id and diagnose.hosp_code = c.hosp_code left join base_dept d on iv.in_ward_id = d.id and iv.hosp_code = d.hosp_code where bb.hosp_code = #{hospCode}
and exists ( select 1 from base_dept bd where bb.hosp_code = bd.hosp_code and bd.id = #{inDeptId} and bd.code = bb.dept_code ) and bb.is_valid = '1'
and ( bb.name like concat('%',#{keyword},'%') or iv.name like concat('%',#{keyword},'%') or iv.in_no like concat('%',#{keyword},'%') )
and (iv.Illness_code = '1' or iv.Illness_code = '2')
and exists ( select 1 from inpt_advice ia where bb.hosp_code = ia.hosp_code and ia.visit_id = iv.id and is_submit = '1' and submit_time > CURDATE() )
and exists ( select 1 from inpt_advice ia where bb.hosp_code = ia.hosp_code and ia.visit_id = iv.id and sign_code = '1' )
and iv.zg_doctor_id = #{crteId}
and iv.zz_doctor_id = #{crteId}
and iv.jz_doctor_id = #{crteId}
and iv.resp_nurse_id = #{crteId}
order by bb.seq_no, bb.crte_time

select * from inpt_visit iv
where iv.hosp_code = #{hospCode}
and iv.in_dept_id = #{inDeptId}
and iv.status_code = '1'
and ifnull(iv.bed_id, '') = ''
ORDER BY iv.in_time asc

