mapper.select:
select
  id, hosp_code, list_id, stage_id, detail_id, is_must, is_valid, group_no, group_seq_no, type_code, sign_code, start_exec_num, end_exec_num, bfc_id, item_code, item_id, item_name, spec, prep_code, dosage, dosage_unit_code, usage_code, rate_id, speed_code, num, unit_code, price, total_price, total_num, total_num_unit_code, herb_num, exec_dept_id, use_days, use_code, is_skin, herb_note_code, remark, herb_use_code, is_give, is_long, crte_id, crte_name, crte_time
from his_v2.clinical_path_stage_detail_item
where hosp_code = #{hospCode}

        and list_id = #{listId}


        and stage_id = #{stageId}


        and detail_id = #{detailId}


        and is_must = #{isMust}


        and is_valid = #{isValid}


        and group_no = #{groupNo}


        and group_seq_no = #{groupSeqNo}


        and type_code = #{typeCode}


        and sign_code = #{signCode}


        and start_exec_num = #{startExecNum}


        and end_exec_num = #{endExecNum}


        and bfc_id = #{bfcId}


        and item_code = #{itemCode}


        and item_id = #{itemId}


        and item_name = #{itemName}


        and spec = #{spec}


        and prep_code = #{prepCode}


        and dosage = #{dosage}


        and dosage_unit_code = #{dosageUnitCode}


        and usage_code = #{usageCode}


        and rate_id = #{rateId}


        and speed_code = #{speedCode}


        and num = #{num}


        and unit_code = #{unitCode}


        and price = #{price}


        and total_price = #{totalPrice}


        and total_num = #{totalNum}


        and total_num_unit_code = #{totalNumUnitCode}


        and herb_num = #{herbNum}


        and exec_dept_id = #{execDeptId}


        and use_days = #{useDays}


        and use_code = #{useCode}


        and is_skin = #{isSkin}


        and herb_note_code = #{herbNoteCode}


        and remark = #{remark}


        and herb_use_code = #{herbUseCode}


        and is_give = #{isGive}


        and is_long = #{isLong}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}

select a.* from ( select a.id, a.hosp_code, a.bfc_code, a.code, a.good_name,a.usual_name as bName, a.spec, a.prep_code, a.dosage, a.dosage_unit_code, a.unit_code, a.rate_code, case when a.in_unit_code = a.unit_code then a.price when a.in_unit_code = a.split_unit_code then a.split_price end price, a.split_ratio, a.split_unit_code, a.split_price, a.is_lvp, a.usage_code, a.ddd, a.durg_code,
case when a.ph_code = '5' then '1' when a.ph_code = '1' or a.ph_code = '2' or a.ph_code = '3' then '4' when a.ph_code = '6' then '5' when a.ph_code = '4' then '6' else '1' end ph_code, a.antibacterial_code, a.is_skin, a.is_basic, a.basic_code, a.good_pym, a.good_wbm, null use_code, case when a.big_type_code = '2' then '3' else '1' end type, '1' itemCode, ifnull(c.id,'') rateId, d.id bfcId, ifnull(e.exec_dept_id, #{loginDeptId}) execDeptId,
'1' typeCode, #{loginDeptId} outDeptId, a.out_unit_code outUnitCode, a.in_unit_code inUnitCode, null isStopSame, null isStopSameNot, null isStopMyself, a.big_type_code bigTypeCode, a.drug_remark drugRemark, a.drug_img_path drugImgPath, f.name prodName, '0' isWz, a.usual_name,a.usual_wbm, a.usual_pym, '' as technology_code from base_drug a left join base_rate c on a.rate_code = c.code and a.hosp_code = c.hosp_code left join base_finance_classify d on a.bfc_code = d.code and a.hosp_code = d.hosp_code left join base_outpt_exec e on find_in_set(a.usage_code , e.usage_codes) and FIND_IN_SET(#{loginDeptId} ,e.dept_ids) and a.hosp_code = e.hosp_code left join base_product f on a.prod_code = f.code and a.hosp_code = f.hosp_code
left join outpt_visit visit on visit.id = #{visitId} and a.hosp_code = visit.hosp_code
left join inpt_visit visit on visit.id = #{visitId} and a.hosp_code = visit.hosp_code
where a.hosp_code = #{hospCode}
and '2' = #{yzmlType}
and ( ((#{operType} = '1' and ifnull(a.is_out,'1') = '1' and (a.out_unit_code = a.unit_code or a.out_unit_code = a.split_unit_code or ifnull(a.out_unit_code,'')='') ) or (#{operType} = '2' and ifnull(a.is_in,'1') = '1' and (a.in_unit_code = a.unit_code or a.in_unit_code = a.split_unit_code or ifnull(a.in_unit_code,'')='')))
and visit.age between ifnull(a.min_age,0) and ifnull(a.max_age,1000) and (visit.gender_code = a.gender_code or ifnull(visit.gender_code,'0') in ('0','9') or ifnull(a.gender_code,'0') in ('0','9') or a.gender_code = '' or visit.gender_code = '')
) and a.is_valid = '1' union all
select a.id, a.hosp_code, a.bfc_code, a.code, a.name good_name,a.other_name as bName, a.spec, null prep_code, null dosage, null dosage_unit_code, a.unit_code,null rate_code,a.split_price price, '1' split_ratio, a.split_unit_code split_unit_code, a.split_price split_price, null is_lvp,null usage_code, null ddd, null durg_code, '1' ph_code,null antibacterial_code, null is_skin, null is_basic, null basic_code, a.pym good_pym, a.wbm good_wbm, a.use_code, '2' type, '2' itemCode, null rateId, d.id bfcId, null execDeptId, '15' typeCode,#{loginDeptId} outDeptId, a.split_unit_code ouUtnitCode, a.split_unit_code inUnitCode, null isStopSame, null isStopSameNot, null isStopMyself, '3' bigTypeCode, '' drugRemark, '' drugImgPath, f.name prodName, '0' isWz, a.name usual_name,a.wbm usual_wbm, a.pym usual_pym ,'' as technology_code from base_material a left join base_finance_classify d on a.bfc_code = d.code and a.hosp_code = d.hosp_code left join base_product f on a.prod_code = f.code and a.hosp_code = f.hosp_code where a.hosp_code = #{hospCode} and a.is_valid = '1'
and '3' = #{yzmlType}
union all
select a.id, a.hosp_code, null bfc_code, a.code, a.name good_name,a.other_name as bName, null spec, null prep_code, null dosage, null dosage_unit_code, a.unit_code, null rate_code, a.price,'1' split_ratio, a.unit_code split_unit_code, a.price split_price, null is_lvp, null usage_code, null ddd, null durg_code,'1' ph_code,null antibacterial_code,null is_skin, null is_basic, null basic_code, a.pym good_pym, a.wbm good_wbm, null use_code, case when a.type_code = '3' then '4' when a.type_code = '12' then '5' else '6' end type, '4' itemCode, '' rateId, '' bfcId, b.id execDeptId, a.type_code typeCode,c.id outDeptId, a.unit_code outUnitCode, a.unit_code inUnitCode, a.is_stop_same isStopSame, a.is_stop_same_not isStopSameNot, a.is_stop_myself isStopMyself, '4' bigTypeCode, '' drugRemark, '' drugImgPath, '' prodName, case when e.id is null then '0' else '1' end isWz, a.name usual_name,a.wbm usual_wbm, a.pym usual_pym, a.technology_code from base_advice a left join base_dept b on a.out_dept_code = b.code and a.hosp_code = b.hosp_code left join base_dept c on a.in_dept_code = c.code and a.hosp_code = c.hosp_code left join sys_parameter e on e.code = 'WZYZ_YZCODE' and a.code = e.value and a.hosp_code = e.hosp_code and e.is_valid = '1'
left join outpt_visit visit on visit.id = #{visitId} and a.hosp_code = visit.hosp_code
left join inpt_visit visit on visit.id = #{visitId} and a.hosp_code = visit.hosp_code
left join base_dept f on f.id = #{loginDeptId} and a.hosp_code = f.hosp_code where a.hosp_code = #{hospCode}
and '1' = #{yzmlType}
and e.id is not null
and '1' = #{yzmlType}
and (FIND_IN_SET(f.code ,a.dept_code) or ifnull(a.dept_code,'') = '') and a.is_valid = '1' and ( (#{operType} = a.use_scope_code or ifnull(a.use_scope_code,'0') = '0')
and visit.age between ifnull(a.min_age,0) and ifnull(a.max_age,1000) and (visit.gender_code = a.gender_code or ifnull(visit.gender_code,'0') in ('0','9') or ifnull(a.gender_code,'0') in ('0','9') or ifnull(visit.gender_code,'0') = '' or ifnull(a.gender_code,'0') = '')
) )a where 1=1
and ( a.good_pym like concat('%',#{keyword},'%') or a.good_wbm like concat('%',#{keyword},'%') or a.good_name like concat('%',#{keyword},'%') or a.usual_name like concat('%',#{keyword},'%') or a.usual_pym like concat('%',#{keyword},'%') or a.usual_wbm like concat('%',#{keyword},'%') or a.bName like concat('%',#{keyword},'%') )
