mapper.select:
select
a.id, a.hosp_code, a.list_id, a.stage_id, a.code, a.item_type, a.item_id, a.item_supply_type, a.is_must, a.classify,
a.sort_no, a.remarke, a.is_operation_day, a.crte_id, a.crte_name, a.crte_time,b.name as itemName,c.describe,l.name as listName
from clinical_path_stage_detail a
join clinical_path_item b on a.item_id = b.id and a.hosp_code = b.hosp_code
join clinical_path_stage c on c.id = a.stage_id and a.hosp_code = c.hosp_code
join clinical_path_list l on l.id = a.list_id and a.hosp_code = l.hosp_code
where a.hosp_code = #{hospCode}

    and a.list_id = #{listId}


    and a.stage_id = #{stageId}


    and a.item_type = #{itemType}


    and a.item_id = #{itemId}

order by a.sort_no

select a.id as visitId, a.hosp_code,a.in_profile, a.in_no, a.name, a.gender_code, a.age, a.age_unit_code, a.preferential_type_id, a.patient_code, a.bed_id, a.bed_name,a.cert_no,a.in_disease_name,a.in_disease_id,a.in_time, (select name from base_dept where id = a.in_ward_id and hosp_code = a.hosp_code) as wardName,a.in_dept_id, (case when date(now()) = date(in_time) then 1 else to_days(date(now())) - to_Days(date(in_time)) end)as inNum, cp.name as listName,d.name as stageName,a.in_time as inTimestring,c.list_id,c.stage_id,c.id,a.out_time as outTime, CONCAT( a.name,'/', IFNULL((select name from sys_code_detail where hosp_code = #{hospCode} and c_code = 'XB' and value = a.gender_code),'-'),'/', IFNULL(a.age,'-'), IFNULL((select name from sys_code_detail where hosp_code = #{hospCode} and c_code = 'NLDW' and value = a.age_unit_code),'') ) AS keyword from inpt_clinical_path_state c join inpt_visit a on a.hosp_code = c.hosp_code and c.visit_id = a.id join clinical_path_list cp on c.list_id = cp.id and c.hosp_code = cp.hosp_code join clinical_path_stage d on c.stage_id = d.id and c.hosp_code = d.hosp_code left join base_bed b on a.hosp_code = b.hosp_code and a.bed_id = b.id where c.path_state = '1' and c.hosp_code = #{hospCode} and ifnull(a.bed_id,'') != '' and a.in_dept_id = #{inDeptId} and a.status_code = '2'
and a.id = #{id}
/*开始日期*/
and date(a.in_time) >= date(#{startTime})
/*结束时间*/
and date(a.in_time) <= date(#{endTime})
and (a.name like concat('%',#{keyword},'%') or a.cert_no like concat('%',#{keyword},'%') or a.in_no like concat('%',#{keyword},'%') or a.bed_name like concat('%',#{keyword},'%'))
and a.zg_doctor_id = #{zgDoctorId}
order by b.seq_no asc

select A.* from ( select iv.id, iv.hosp_code, iv.profile_id, iv.in_profile, iv.in_no, iv.name, iv.gender_code, iv.age, iv.age_unit_code, iv.birthday, iv.nationality_cation, iv.occupation_code, iv.education_code, iv.contact_name, iv.contact_rela_code, iv.contact_address, iv.contact_phone, iv.nation_code, iv.cert_code, iv.cert_no, iv.marry_code, iv.phone, iv.address, iv.preferential_type_id, iv.patient_code, iv.receive_hosp_name, iv.bed_id, iv.bed_name, iv.nursing_code, iv.diet_type, iv.Illness_code, iv.status_code, iv.in_ward_id, iv.in_dept_id, iv.in_dept_name, iv.in_time, iv.zz_doctor_id, iv.zz_doctor_name, iv.jz_doctor_id, iv.jz_doctor_name, iv.zg_doctor_id, iv.zg_doctor_name, iv.in_remark, iv.in_mode_code, iv.in_disease_id, iv.in_disease_name, iv.in_disease_icd10, iv.in_situation_code, iv.outpt_visit_no, iv.outpt_doctor_id, iv.outpt_doctor_name, iv.outpt_disease_id, iv.outpt_disease_name, iv.outpt_disease_icd10, iv.out_ward_id, iv.out_dept_id, iv.out_dept_name, iv.out_time, iv.out_disease_id, iv.out_disease_name, iv.out_disease_icd10, iv.out_oper_id, iv.out_oper_name, iv.out_oper_time, iv.out_situation_code, iv.out_remark, iv.out_mode_code, iv.is_archive, iv.archive_time, iv.archive_id, iv.archive_name, iv.insure_code, iv.insure_org_code, iv.insure_no, iv.insure_biz_code, iv.insure_treat_code, iv.insure_patient_id, iv.insure_remark, iv.total_advance, iv.total_cost, iv.total_balance, iv.crte_id, iv.crte_name, iv.crte_time, (select name from base_dept where in_ward_id = base_dept.id) as wardName, if((ic.path_state is null),"0","1") as isClinicalList,ic.list_id, (select a.name from clinical_path_list a where a.id = ic.list_id and a.hosp_code = ic.hosp_code ) as clinicalListName, CONCAT( iv.name,'/', IFNULL((select name from sys_code_detail where hosp_code = #{hospCode} and c_code = 'XB' and value = iv.gender_code),'-'),'/', IFNULL(iv.age,'-'), IFNULL((select name from sys_code_detail where hosp_code = #{hospCode} and c_code = 'NLDW' and value = iv.age_unit_code),'') ) AS keyword from inpt_visit iv left join inpt_clinical_path_state ic on ic.path_state = '1' and ic.visit_id = iv.id and ic.hosp_code = iv.hosp_code where iv.hosp_code = #{hospCode} and iv.status_code = '2'
and iv.in_dept_id = #{inDeptId}
and iv.id = #{id}
/*开始日期*/
and date(iv.in_time) >= date(#{startDate})
/*结束时间*/
and date(iv.in_time) <= date(#{endDate})
and (iv.name like concat('%',#{keyword},'%') or iv.cert_no like concat('%',#{keyword},'%') or iv.in_no like concat('%',#{keyword},'%'))
) A
where A.isClinicalList = #{isClinicalList}
order by A.crte_time desc

select a.visit_id,a.path_crte_time,a.id,a.hosp_code from inpt_clinical_path_state a
where a.hosp_code = #{hospCode} and a.path_state = '1' and a.visit_id in

  #{item}

update.foreach:
update inpt_clinical_path_state a, (
select cost.visit_id, cost.hosp_code, sum(ifnull(reality_price,0)) realityPrice from inpt_cost cost where
cost.visit_id = #{item.visitId} and cost.hosp_code = #{item.hospCode} and (cost.baby_id is null or cost.baby_id = '')
and cost.cost_time >= #{item.pathCrteTime}
)b set a.total_price= b.realityPrice
where a.id=#{item.id} and a.hosp_code=#{item.hospCode} and a.visit_id = b.visit_id and a.hosp_code = b.hosp_code

mapper.select:
    select a.item_id,c.name as itemName from clinical_path_stage_detail a
		left join clinical_path_item c on a.item_id = c.id and a.hosp_code = c.hosp_code
		where a.hosp_code = #{hospCode} and a.list_id = #{listId} and a.stage_id = #{stageId}
		and a.id not in (
		  select b.detail_id from clinic_path_exec b where b.clinical_path_stage_id = #{clinicalPathStageId}
			and list_id = #{listId} and stage_id = #{stageId} and is_exec = '1'
		 )
