mapper.update:
update inpt_consultation_apply
consul_state = #{consulState}, consul_type = #{consulType}, consul_time = #{consulTime}, consul_addr = #{consulAddr}, apply_deptid = #{applyDeptid}, apply_userid = #{applyUserid}, apply_username = #{applyUsername}, illness = #{illness}, diagno_treat = #{diagnoTreat}, consul_reason = #{consulReason}, consul_objective = #{consulObjective}, consul_deptid_list = #{consulDeptidList}, consul_userid_list = #{consulUseridList}, remark = #{remark}, finish_userid = #{finishUserid}, finish_username = #{finishUsername}, finish_time = #{finishTime}
where hosp_code = #{hospCode} and id = #{id}

update.foreach:
update inpt_consultation_apply
set advice_id = #{dto.id}
where hosp_code = #{dto.hospCode}
  and id = #{dto.consulApplyInfo.id}

mapper.select:
select id, hosp_code, visit_id, baby_id, advice_id, consul_no, consul_state, consul_type, consul_time, consul_addr, apply_deptid, apply_userid, apply_username, illness, diagno_treat, consul_reason, consul_objective, consul_deptid_list, consul_userid_list, remark, finish_userid, finish_username, finish_time, crte_id, crte_name, crte_time from inpt_consultation_apply where hosp_code = #{hospCode}
and id = #{id}
and advice_id = #{adviceId}

select if(a.baby_id is null or a.baby_id = '', concat( iv.name, '/', (case iv.gender_code when '1' then '男' when '2' then '女' else '' end), '/', iv.age, (case iv.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' when '4' then '天' else '' end )), concat( iv.name, '/', (case iv.gender_code when '1' then '男' when '2' then '女' else '' end), '/', iv.age, (case iv.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' when '4' then '天' else '' end ), '/', ib.name) ) as patientInfo, iv.bed_name, iv.in_no, a.apply_deptid, (select bd.name from base_dept bd where a.hosp_code = bd.hosp_code and a.apply_deptid = bd.id) as apply_deptname, a.apply_userid, a.apply_username, a.consul_no, a.consul_time, a.consul_state, a.consul_type, a.consul_deptid_list, (SELECT GROUP_CONCAT(bd.name) from base_dept bd where a.hosp_code = bd.hosp_code and FIND_IN_SET(bd.id, a.consul_deptid_list) GROUP BY a.id) as consul_deptid_list_name, a.consul_userid_list, (SELECT GROUP_CONCAT(su.name) from sys_user su where a.hosp_code = su.hosp_code and FIND_IN_SET(su.id, a.consul_userid_list) GROUP BY a.id) as consul_userid_list_name, a.consul_addr, a.illness, a.diagno_treat, a.consul_reason, a.consul_objective from inpt_consultation_apply a join inpt_visit iv on a.hosp_code = iv.hosp_code and a.visit_id = iv.id join base_bed bb on iv.hosp_code = bb.hosp_code and iv.bed_id = bb.id left join inpt_baby ib on a.hosp_code = ib.hosp_code and a.baby_id = ib.id where a.hosp_code = #{hospCode}
and a.apply_deptid = #{applyDeptid}
and a.apply_userid = #{applyUserid}
and (iv.name like concat('%', #{keyword}, '%') or iv.bed_name like concat('%', #{keyword}, '%') or iv.in_no = #{keyword} or a.consul_no = #{keyword})
and a.consul_type = #{consulType}
and a.consul_state = #{consulState}
and date_format(a.consul_time, '%Y-%m-%d %H:%i:%s') >= date_format(#{startTime}, '%Y-%m-%d %H:%i:%s')
and date_format(a.consul_time, '%Y-%m-%d %H:%i:%s') <= date_format(#{endTime}, '%Y-%m-%d %H:%i:%s')
order by bb.seq_no asc, a.consul_no asc, a.consul_time asc

mapper.delete:
delete
from inpt_consultation_apply
where id = #{id} and hosp_code = #{hospCode}
