mapper.update:
update inpt_cost set attribution_code = #{attributionCode}
where hosp_code = #{hospCode}
and id in

  #{item}

mapper.select:
select drug.good_name as name,drug.code as code,drug.spec as spec,cost.numUnitCode as unitCode,cost.price, cost.num,cost.realityPrice,cost.valuationPrice,cost.preferentialPrice, cost.crteName, cost.sourceDeptId, cost.itemCode, cost.itemId, (select name from base_finance_classify where id = cost.bfcId and hosp_code = #{hospCode}) as bfcName, (select name from base_dept where id = cost.sourceDeptId and hosp_code = #{hospCode}) as sourceDeptName from base_drug drug inner join( select max(inpt_cost.source_dept_id) as sourceDeptId, max(inpt_cost.visit_id) as visitId, max(inpt_cost.bfc_id) as bfcId, inpt_cost.item_id as itemId, inpt_cost.hosp_code, max(inpt_cost.price) as price, max(inpt_cost.crte_name) as crteName, sum(inpt_cost.total_num ) as num, max(inpt_cost.total_num_unit_code) as numUnitCode, sum(inpt_cost.total_price ) as valuationPrice, sum(inpt_cost.preferential_price ) as preferentialPrice, sum(inpt_cost.reality_price ) as realityPrice, max(inpt_cost.item_code) as itemCode from inpt_cost where hosp_code = #{hospCode} and item_code = '1' and visit_id = #{visitId} and status_code = '0'
and source_dept_id = #{sourceDeptId}
and (iat_id is not null or iat_id != '')
and (iat_id is null or iat_id = '')
and baby_id =#{babyId}
and (baby_id is null or baby_id = '')
AND attribution_code = #{attributionCode}
AND (attribution_code = '0' or attribution_code is null or attribution_code = '') and inpt_cost.id not in (select b.cost_id from insure_individual_cost b where b.visit_id = #{visitId and b.hosp_code = #{hospCode})
group by itemId ) cost on drug.id = cost.itemId and drug.hosp_code = cost.hosp_code where drug.hosp_code = #{hospCode}
and drug.good_name like concat('%',#{keyword},'%')
union select item.name as name, item.code, item.spec, cost.numUnitCode as unitCode, cost.price, cost.num,cost.realityPrice,cost.valuationPrice,cost.preferentialPrice,cost.crteName,cost.sourceDeptId, cost.itemCode,cost.itemId, ( select name from base_finance_classify where id = cost.bfcId and hosp_code = #{hospCode} ) as bfcName, ( select name from base_dept where id = cost.sourceDeptId and hosp_code = #{hospCode} ) as sourceDeptName from base_item item inner join ( select max(inpt_cost.source_dept_id) as sourceDeptId, max(inpt_cost.visit_id) as visitId, max(inpt_cost.bfc_id) as bfcId, inpt_cost.item_id as itemId, inpt_cost.hosp_code, max(inpt_cost.price) as price, max(inpt_cost.crte_name) as crteName, sum(inpt_cost.total_num ) as num, max(inpt_cost.total_num_unit_code) as numUnitCode, sum(inpt_cost.total_price ) as valuationPrice, sum(inpt_cost.preferential_price ) as preferentialPrice, sum(inpt_cost.reality_price ) as realityPrice, max(inpt_cost.item_code) as itemCode from inpt_cost where hosp_code = #{hospCode} and item_code = '3' and visit_id = #{visitId} and status_code = '0'
and source_dept_id = #{sourceDeptId}
and (iat_id is not null or iat_id != '')
and (iat_id is null or iat_id = '')
and baby_id =#{babyId}
and (baby_id is null or baby_id = '')
AND attribution_code = #{attributionCode}
AND (attribution_code = '0' or attribution_code is null or attribution_code = '') and inpt_cost.id not in (select b.cost_id from insure_individual_cost b where b.visit_id = #{visitId and b.hosp_code = #{hospCode})
group by itemId ) cost on item.id = cost.itemId and item.hosp_code = cost.hosp_code where item.hosp_code = #{hospCode}
and item.name like concat('%',#{keyword},'%')
union select material.name as name,material.code, material.spec,cost.numUnitCode as unitCode, cost.price, cost.num,cost.realityPrice, cost.valuationPrice, cost.preferentialPrice,cost.crteName,cost.sourceDeptId, cost.itemCode,cost.itemId, ( select name from base_finance_classify where id = cost.bfcId and hosp_code = #{hospCode} ) as bfcName, ( select name from base_dept where id = cost.sourceDeptId and hosp_code = #{hospCode} ) as sourceDeptName from base_material material inner join( select max(inpt_cost.source_dept_id) as sourceDeptId, max(inpt_cost.visit_id) as visitId, max(inpt_cost.bfc_id) as bfcId, inpt_cost.item_id as itemId, inpt_cost.hosp_code, max(inpt_cost.price) as price, max(inpt_cost.crte_name) as crteName, sum(inpt_cost.total_num ) as num, max(inpt_cost.total_num_unit_code) as numUnitCode, sum(inpt_cost.total_price ) as valuationPrice, sum(inpt_cost.preferential_price ) as preferentialPrice, sum(inpt_cost.reality_price ) as realityPrice, max(inpt_cost.item_code) as itemCode FROM inpt_cost where hosp_code = #{hospCode}and item_code = '2' and visit_id = #{visitId} and status_code = '0'
and source_dept_id = #{sourceDeptId}
and (iat_id is not null or iat_id != '')
and (iat_id is null or iat_id = '')
and baby_id =#{babyId}
and (baby_id is null or baby_id = '')
and attribution_code = #{attributionCode}
and (attribution_code = '0' or attribution_code is null or attribution_code = '') and inpt_cost.id not in (select b.cost_id from insure_individual_cost b where b.visit_id = #{visitId and b.hosp_code = #{hospCode})
group by itemId ) cost on material.id = cost.itemId and material.hosp_code = cost.hosp_code where material.hosp_code = #{hospCode}
and material.name like concat('%',#{keyword},'%')
order by bfcName
