mapper.select:
select a.id, a.hosp_code, a.profile_id, a.in_profile, a.in_no, a.name, a.gender_code, a.age, a.age_unit_code, a.birthday, a.nation_code, a.cert_code, a.cert_no, a.marry_code, a.phone, a.address, a.preferential_type_id, a.patient_code, a.receive_hosp_name, a.bed_id, a.bed_name, a.nursing_code, a.diet_type, a.Illness_code, a.status_code, a.in_ward_id, a.in_dept_id, a.in_dept_name, a.in_time, a.zz_doctor_id, a.zz_doctor_name, a.jz_doctor_id, a.jz_doctor_name, a.zg_doctor_id, a.zg_doctor_name, a.in_remark, a.in_mode_code, a.in_disease_id, a.in_disease_name, a.in_disease_icd10, a.in_situation_code, a.outpt_visit_no, a.outpt_doctor_id, a.outpt_doctor_name, a.outpt_disease_id, a.outpt_disease_name, a.outpt_disease_icd10, a.out_ward_id, a.out_dept_id, a.out_dept_name, a.out_time, a.out_disease_id, a.out_disease_name, a.out_disease_icd10, a.out_oper_id, a.out_oper_name, a.out_oper_time, a.out_situation_code, a.out_remark, a.out_mode_code, a.is_archive, a.archive_time, a.archive_id, a.archive_name, a.insure_code, a.insure_org_code, a.insure_no, a.insure_biz_code, a.insure_treat_code, a.insure_patient_id, a.insure_remark, a.total_advance, a.total_cost, a.total_balance, a.crte_id, a.crte_name, a.crte_time, (select name from base_dept where id = a.in_ward_id and hosp_code = a.hosp_code) as wardName, (case when date(now()) = date(in_time) then 1 else to_days(date(now())) - to_Days(date(in_time)) end)as inNum, (a.total_advance - (select sum(reality_price) from inpt_cost ic where ic.hosp_code=#{hospCode} and ic.visit_id = a.id)) as totalMergeBalance, (select sum(reality_price) from inpt_cost ic where ic.hosp_code=#{hospCode} and ic.visit_id = a.id) as totalMergeCost
, t.is_consule, t.apply_deptid
from inpt_visit a left join base_bed b on a.hosp_code = b.hosp_code and a.bed_id = b.id
join ( SELECT '1' as is_consule, ss.apply_deptid, ss.visit_id, ss.hosp_code FROM inpt_consultation_apply ss join inpt_advice ia on ss.hosp_code = ia.hosp_code and ss.advice_id = ia.id WHERE ss.hosp_code = #{hospCode} and ss.consul_state = '1' and ia.is_check = '1' AND FIND_IN_SET(#{zgDoctorId}, ss.consul_userid_list) GROUP BY ss.visit_id, ss.hosp_code ) t on a.hosp_code = t.hosp_code and a.id = t.visit_id
where a.hosp_code = #{hospCode} and ifnull(a.bed_id,'') != '' and a.status_code = '2'
and a.in_dept_id = #{inDeptId}
and a.id = #{id}
and (a.name like concat('%',#{keyword},'%') or a.cert_no like concat('%',#{keyword},'%') or a.in_no like concat('%',#{keyword},'%') or a.bed_name like concat('%',#{keyword},'%'))
and a.zg_doctor_id = #{zgDoctorId}
order by b.seq_no asc

select a.id, a.hosp_code, a.visit_id, a.baby_id, a.order_no, a.iat_id, a.iatd_group_no, a.iatd_group_seq_no, a.iatd_id, a.in_dept_id, a.exec_dept_id, a.dept_id, a.group_no, a.group_seq_no, a.type_code, a.sign_code, a.start_exec_num, a.end_exec_num, a.bfc_id, a.item_code, a.item_id, a.item_name, a.content, a.spec, a.prep_code, a.dosage, a.dosage_unit_code, a.usage_code, a.rate_id, a.speed_code, a.num, a.unit_code, a.price, a.total_price, a.total_num, a.total_num_unit_code, a.herb_num, a.use_days, a.use_code, a.is_skin, a.is_positive, a.herb_note_code, a.phar_id, a.remark, a.plan_stop_time, a.technology_no, a.herb_use_code, a.is_give, a.exec_id, a.exec_name, a.second_exec_id, a.second_exec_name, a.long_start_time, a.teach_doctor_id, a.teach_doctor_name, a.is_check, a.check_id, a.check_name, a.check_time, a.stop_doctor_id, a.stop_doctor_name, a.stop_time, a.stop_check_id, a.stop_check_name, a.stop_check_time, a.is_long, is_stop, a.is_reject, a.reject_remark, a.crte_id, a.crte_name, a.crte_time, (select et.exec_time from inpt_advice_exec et where et.advice_id = a.id and et.hosp_code = a.hosp_code and et.exec_time is not null order by exec_time limit 1) as last_exec_time, case when a.item_code = '4' then ( case when b.type_code = '3' then '4' when b.type_code = '12' then '5' else '6' end ) when a.item_code = '1' then ( case when c.big_type_code = '2' then '3' else '1' end ) when a.item_code = '2' then '2' end type, case when c.id is not null then c.big_type_code when a.item_code = '2' then '3' when a.item_code = '4' then '4' end bigTypeCode, case when a.item_code = '1' then c.split_unit_code when a.item_code = '2' then d.split_unit_code else a.total_num_unit_code end splitUnitCode, case when a.item_code = '1' then c.unit_code when a.item_code = '2' then d.unit_code else a.usage_code end bigUnitCode, a.is_submit, (select name from base_dept where hosp_code = #{hospCode} and id = a.dept_id) as deptName, (select name from base_dept where hosp_code = #{hospCode} and id = a.exec_dept_id) as execDeptName, if(a.item_code = '4', b.is_stop_same,null ) isStopSame,if(a.item_code = '4', b.is_stop_same_not,null ) isStopSameNot, if(a.item_code = '4', b.is_stop_myself,null ) isStopMyself, a.source_ia_id source_ia_id, a.advice_prefix, a.advice_suffix, a.collect_blood_time, case when e.id is null then '0' else '1' end isWz, a.cancel_id, a.cancel_name, a.cancel_time, a.skin_durg_id, ef.good_name skinDurgName, a.skin_phar_id, a.skin_unit_code, case when a.item_code = '1' then '2' when a.item_code = '2' then '3' when a.item_code = '4' and e.id is not null then '4' when a.item_code = '4' and e.id is null then '1' end yzmlType, a.yylx, b.technology_code, b.container_code, b.specimen_code, (case when a.item_code = '1' then c.nation_code when a.item_code = '2' then d.nation_code when a.item_code = '4' then b.union_nation_code else '' end) as nation_code, (case when a.item_code = '1' then c.nation_name when a.item_code = '2' then d.nation_name when a.item_code = '4' then b.union_nation_name else '' end) as nation_name from inpt_advice a left join base_advice b on a.item_id = b.id and a.hosp_code = b.hosp_code and a.item_code = '4' left join base_drug c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = '1' left join base_drug ef on a.skin_durg_id = ef.id and a.hosp_code = ef.hosp_code left join base_material d on a.item_id = d.id and a.hosp_code = d.hosp_code and a.item_code = '2' left join sys_parameter e on e.code = 'WZYZ_YZCODE' and b.code = e.value and a.hosp_code = e.hosp_code and e.is_valid = '1' where a.hosp_code = #{hospCode} and a.visit_id = #{visitId}
and a.is_long = #{isLong}
and a.is_stop = #{isStop}
and a.type_code = #{typeCode}
and a.baby_id = #{babyId}
and (a.baby_id is null or LENGTH(TRIM(a.baby_id)) < 1)
order by a.long_start_time asc, a.id asc

