select a.id,a.visit_id visitId, a.baby_id babyId,a.type_code typeCode,a.group_no groupNo,b.name visitName,b.age visitAge, b.gender_code visitSex,b.cert_no visitCardNo,a.spec,a.dosage,a.dosage_unit_code as dosageUnitCode,a.num,a.unit_code as unitCode,a.crte_name as crteName,a.use_code useCode,a.stop_time AS stopTime,a.end_exec_num AS endExecNum, a.usage_code usageCode,a.rate_id rateId,a.use_days useDays,a.remark, a.total_num totalNum,a.total_num_unit_code totalNumUnitCode,a.speed_code speedCode,a.is_long isLong,a.is_stop isStop,a.is_reject isReject,a.is_check isCheck, a.price,a.total_price totalPrice,a.long_start_time longStartTime,a.content content,a.start_exec_num startExecNum, IF( a.baby_id , CONCAT(ib.name, '/', (ifnull((case ib.gender_code when '1' then '男' when '2' then '女' when '0' then '未知' when '9' then '未说明的性别' else '' end), '')), '/', DATE_FORMAT(ib.birth_time,'%Y-%m-%d %H:%i:%s')), concat(b.NAME,'/', (ifnull((case b.gender_code when '1' then '男' when '2' then '女' when '0' then '未知' when '9' then '未说明的性别' else '' end), '')),'/', b.age,(ifnull((case b.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' when '4' then '周' else '' end), '岁' ))) ) as visitInfo, (select name from base_rate where hosp_code=a.hosp_code and id=a.rate_id) rateName, (select name from base_dept where hosp_code=a.hosp_code and id=a.exec_dept_id) execDeptName, (case c.big_type_code when '2' then '3' end) type,b.bed_name bedName, (case when a.item_code = '1' then c.nation_code when a.item_code = '2' then bm.nation_code else '' end) as nation_code, (case when a.item_code = '1' then c.nation_name when a.item_code = '2' then bm.nation_name else '' end) as nation_name from inpt_advice a join inpt_visit b on a.visit_id=b.id and a.hosp_code = b.hosp_code left join base_drug c on a.item_id=c.id and a.item_code='1' and c.hosp_code=a.hosp_code left join base_material bm on a.item_id=bm.id and a.item_code='2' and bm.hosp_code=a.hosp_code left join inpt_baby ib on a.baby_id = ib.id and a.hosp_code = ib.hosp_code where a.is_submit='1' and a.hosp_code=#{hospCode} and (
((a.is_stop='0' or a.is_stop is null) and (a.is_check='0' or a.is_check is null)) or
(a.is_stop='1' and (a.is_stop_check='0' or a.is_stop_check is null)) ) and (a.is_reject='0' or a.is_reject is null)
and a.is_long=#{isLong}
and a.is_stop=#{isStop}
and a.dept_id=#{deptId}
and b.id=#{visitId}
and ( b.NAME LIKE CONCAT( '%', #{keyword}, '%' ) OR b.bed_name LIKE CONCAT( '%', #{keyword}, '%' ))
order by a.crte_time, a.visit_id, a.baby_id, group_no, group_seq_no asc, a.long_start_time desc

