mapper.select:
select * from stro_stock a left join ( select a.biz_id,a.item_id, a.hosp_code,sum(totalNum) totalNum from ( select receive.phar_id biz_id, a.item_id, a.hosp_code, case when a.item_code = '1' and a.unit_code = b.split_unit_code then a.num when a.item_code = '1' and a.unit_code = b.unit_code then a.num*b.split_ratio when a.item_code = '2' and a.unit_code = c.split_unit_code then a.num when a.item_code = '2' and a.unit_code = c.unit_code then a.num*c.split_ratio else 0 end totalNum from phar_in_receive receive join phar_in_receive_detail a on receive.id = a.receive_id and receive.hosp_code = a.hosp_code left join base_drug b on a.item_id = b.id and a.hosp_code = b.hosp_code and a.item_code = '1' left join base_material c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = '2' where receive.hosp_code = #{hospCode} and receive.status_code in ('0','1') and receive.phar_id = #{pharId} and a.item_id = #{itemId}
and DATE_ADD(receive.crte_time, INTERVAL #{wjsykc} hour) > now()
union all select a.biz_id, a.item_id, a.hosp_code, ifnull(a.stock_occupy, 0) totalNum from stro_stock a where a.hosp_code = #{hospCode} and a.biz_id = #{pharId} and a.item_id = #{itemId} union all select #{pharId}, a.id, a.hosp_code, case when a.split_unit_code = #{unitCode} then #{totalNum} when a.unit_code = #{unitCode} then #{totalNum}*a.split_ratio else #{totalNum} end totalNum from base_drug a where a.hosp_code = #{hospCode} and a.id = #{itemId} union all select #{pharId}, a.id, a.hosp_code, case when a.split_unit_code = #{unitCode} then #{totalNum} when a.unit_code = #{unitCode} then #{totalNum}*a.split_ratio else #{totalNum} end totalNum from base_material a where a.hosp_code = #{hospCode} and a.id = #{itemId} )a group by a.biz_id,a.item_id, a.hosp_code )b on a.hosp_code = b.hosp_code and a.item_id = b.item_id and a.biz_id = b.biz_id where a.hosp_code = #{hospCode} and a.biz_id = #{pharId} and a.item_id = #{itemId} and (a.split_num - ifnull(b.totalNum,0)) >= 0

