    and st.item_name like concat('%',#{itemName,jdbcType=VARCHAR},'%')


    and (st.baby_id is not null and st.baby_id != '')


    and (st.baby_id is null or st.baby_id = '')


    AND st.baby_id = #{babyId}


    AND ia.is_long = #{isLong}

