ost ic where ic.hosp_code=#{hospCode} and ic.visit_id = iv.id) as totalMergeCost from inpt_visit iv left join base_bed bb on iv.bed_id=bb.id where iv.hosp_code = #{hospCode}
and iv.id = #{id}
and (iv.name like concat('%',#{keyword},'%') or iv.cert_no like concat('%',#{keyword},'%') or iv.in_no like concat('%',#{keyword},'%') or iv.bed_name like concat('%',#{keyword},'%'))
