and iv.in_time <= DATE_ADD(STR_TO_DATE(#{endTime},'%Y-%m-%d'),INTERVAL 1 day)
and iv.status_code = #{statusCode}
and iv.in_dept_id = #{inDeptId}
and iv.in_time BETWEEN #{Stime} and #{Etime}
