FROM inpt_visit visit LEFT JOIN ( SELECT visit_id, baby_id, hosp_code, max(settle_time) as settle_time, sum( total_price ) AS total_price, sum( self_price ) AS self_price, sum( mi_price ) AS mi_price, sum( actual_price ) AS actual_price, sum( hosp_jm_price ) AS hosp_jm_price FROM inpt_settle GROUP BY hosp_code,visit_id ) settle ON visit.hosp_code = settle.hosp_code and visit.id = settle.visit_id and (settle.baby_id is null or settle.baby_id ='') left join insure_individual_visit iiv on iiv.visit_id = visit.id and iiv.hosp_code = settle.hosp_code left join insure_individual_basic iib on iib.id = iiv.mib_id and iiv.hosp_code = iib.hosp_code WHERE visit.hosp_code =#{hospCode}
