and
FIND_IN_SET(#{item},iiv.psn_idet_type)
and visit.in_dept_id = #{in_dept_id}
and visit.in_ward_id = #{in_ward_id}
and visit.status_code = #{status_code}
and visit.patient_code = #{patient_code}
and visit.in_time >= date(#{in_start_time})
and visit.in_time < DATE_ADD(#{in_end_time},INTERVAL 1 DAY)
and visit.out_time >= date(#{out_start_time})
and visit.out_time < DATE_ADD(#{out_end_time},INTERVAL 1 DAY)
and date(settle.settle_time) >= date(#{js_start_time})
and date(settle.settle_time) <= date(#{js_end_time}) ) union all (SELECT visit.id, visit.in_no, ib.`name` name, visit.bed_name, visit.gender_code, visit.age, visit.patient_code, visit.preferential_type_id, DATE_FORMAT( visit.in_time, '%Y-%m-%d %H:%i:%s' ) AS in_time, TO_DAYS(ifnull(out_time,now()))- TO_DAYS( visit.in_time )+ 1 AS zyts, visit.in_remark, visit.out_remark, visit.in_disease_name, visit.cert_no, visit.phone, visit.address, (select sum(reality_price) from inpt_cost it where it.hosp_code =visit.hosp_code and it.visit_id = ib.visit_id and it.baby_id = ib.id) as total_cost, 0 total_advance, 0 total_advance, 0 total_price, settle.hosp_jm_price as hosp_jm_price, settle.mi_price as mi_price, settle.self_price as self_je, visit.outpt_doctor_name, visit.jz_doctor_name, visit.zg_doctor_name, visit.in_profile, visit.in_dept_name, visit.out_disease_name, (select count(1) FROM inpt_visit a where a.cert_no = visit.cert_no and a.cert_code = visit.cert_code and a.name = visit.name and a.hosp_code =#{hospCode}) as zycs, DATE_FORMAT( visit.out_time, '%Y-%m-%d %H:%i:%s' ) AS out_time, visit.status_code, visit.crte_name AS czr, DATE_FORMAT( settle.settle_time, '%Y-%m-%d %H:%i:%s' ) AS jssj,2 as px, iiv.psn_idet_type as psnIdetType, ifnull(iiv.psn_idet_type_name,'-') as psnIdetTypeName, (select admdvs_name from insure_admdvs i where iiv.hosp_code = i.hosp_code and i.admdvs_code=iiv.insuplc_admdvs limit 1) as insuplcAdmdvs, iib.bka008, (select sum(total_price) from inpt_cost c where c.hosp_code = #{hospCode} and c.item_code = '1' and visit.id = c.visit_id and c.baby_id = ib.id) as ypfy, (select sum(total_price) from inpt_cost c where c.hosp_code = #{hospCode} and c.item_code = '1' and visit.id = c.visit_id and c.baby_id = ib.id)/visit.total_cost as fyb
FROM inpt_visit visit JOIN inpt_baby ib on ib.hosp_code =visit.hosp_code and ib.visit_id = visit.id LEFT JOIN ( SELECT visit_id,baby_id, hosp_code, max(settle_time) as settle_time, sum( total_price ) AS total_price, sum( self_price ) AS self_price, sum( mi_price ) AS mi_price, sum( actual_price ) AS actual_price, sum( hosp_jm_price ) AS hosp_jm_price FROM inpt_settle GROUP BY hosp_code,visit_id ) settle ON ib.hosp_code = settle.hosp_code and ib.id = settle.baby_id left join insure_individual_visit iiv on iiv.visit_id = visit.id and iiv.hosp_code = settle.hosp_code left join insure_individual_basic iib on iib.id = iiv.mib_id and iiv.hosp_code = iib.hosp_code WHERE visit.hosp_code =#{hospCode}
and (visit.name like concat('%',#{keyword},'%') or visit.in_profile like concat('%',#{keyword},'%') or visit.in_no like concat('%',#{keyword},'%'))
and
FIND_IN_SET(#{item},iiv.psn_idet_type)
and visit.in_dept_id = #{in_dept_id}
and visit.in_ward_id = #{in_ward_id}
and visit.status_code = #{status_code}
and visit.patient_code = #{patient_code}
and visit.in_time >= date(#{in_start_time})
and visit.in_time < DATE_ADD(#{in_end_time},INTERVAL 1 DAY)
and visit.out_time >= date(#{out_start_time})
and visit.out_time < DATE_ADD(#{out_end_time},INTERVAL 1 DAY)
and date(settle.settle_time) >= date(#{js_start_time})
and date(settle.settle_time) <= date(#{js_end_time})
) order by in_time desc, px asc

mapper.update:
update inpt_visit


		is_uplod_cost = #{isUplodCost},


		is_uplod_dise = #{isUplodDise},


		cplt_flag = #{cpltFlag},


where hosp_code = #{hospCode}
and id = #{id}

mapper.select:
select iv.id, iv.hosp_code, iv.profile_id, iv.in_profile, iv.in_no, iv.name, iv.gender_code, iv.age, iv.age_unit_code, iv.birthday, iv.nationality_cation, iv.occupation_code, iv.education_code, iv.contact_name, iv.contact_rela_code, iv.contact_address, iv.contact_phone, iv.nation_code, iv.cert_code, iv.cert_no, iv.marry_code, iv.phone, iv.address, iv.preferential_type_id, iv.patient_code, iv.receive_hosp_name, iv.bed_id, iv.bed_name, iv.nursing_code, iv.diet_type, iv.Illness_code, iv.status_code, iv.in_ward_id, iv.in_dept_id, iv.in_dept_name, iv.in_time, iv.zz_doctor_id, iv.zz_doctor_name, iv.jz_doctor_id, iv.jz_doctor_name, iv.zg_doctor_id, iv.zg_doctor_name, iv.in_remark, iv.in_mode_code, iv.in_disease_id, iv.in_disease_name, iv.in_disease_icd10, iv.in_situation_code, iv.outpt_visit_no, iv.outpt_doctor_id, iv.outpt_doctor_name, iv.outpt_disease_id, iv.outpt_disease_name, iv.outpt_disease_icd10, iv.out_ward_id, iv.out_dept_id, iv.out_dept_name, iv.out_time, iv.out_disease_id, iv.out_disease_name, iv.out_disease_icd10, iv.out_oper_id, iv.out_oper_name, iv.out_oper_time, iv.out_situation_code, iv.out_remark, iv.out_mode_code, iv.is_archive, iv.archive_time, iv.archive_id, iv.archive_name, iv.insure_code, iv.insure_org_code, iv.insure_no, iv.insure_biz_code, iv.insure_treat_code, iv.insure_patient_id, iv.insure_remark, iv.total_advance, iv.total_cost, iv.total_balance, iv.crte_id, iv.crte_name, iv.crte_time, (select name from base_dept where in_ward_id = base_dept.id) as wardName,bb.seq_no, CONCAT( iv.name,'/', IFNULL((case iv.gender_code when '1' then '男' when '2' then '女' when '0' then '未知' when '9' then '未说明的性别' else '' end),'-'),'/', IFNULL(iv.age,'-'), IFNULL((case iv.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' when '4' then '天' else '' end), '') ) AS keyword, iv.total_balance as totalMergeBalance, iv.total_cost as totalMergeCost from inpt_visit iv left join base_bed bb on iv.bed_id=bb.id where iv.hosp_code = #{hospCode}
and iv.id = #{id}
and (iv.name like concat('%',#{keyword},'%') or iv.cert_no like concat('%',#{keyword},'%') or iv.in_no like concat('%',#{keyword},'%') or iv.bed_name like concat('%',#{keyword},'%'))
and iv.in_time > STR_TO_DATE(#{startTime},'%Y-%m-%d')
and iv.in_time <= DATE_ADD(STR_TO_DATE(#{endTime},'%Y-%m-%d'),INTERVAL 1 day)
and iv.status_code = #{statusCode}
and iv.in_dept_id = #{inDeptId}
and iv.in_time BETWEEN #{Stime} and #{Etime}
order by iv.out_time desc
order by bb.seq_no asc
