mapper.select:
select
id, hosp_code, code, name, up_code, page_print_code, type_code, doc_code, gender_code, pym, wbm,seq,
is_del_nullline, is_common, is_end, is_valid, is_unique, is_page_print, is_audit, is_hosp, dept_id, crte_id,
crte_name, crte_time
from emr_classify

	hosp_code = #{hospCode}

		and id = #{id}


		and code = #{code}


		and name = #{name}


		and up_code = #{upCode}


		and page_print_code = #{pagePrintCode}


		and type_code = #{typeCode}


		and doc_code = #{docCode}


		and gender_code = #{genderCode}


		and pym = #{pym}


		and wbm = #{wbm}


		and seq = #{seq}


		and is_del_nullline = #{isDelNullline}


		and is_common = #{isCommon}


		and is_end = #{isEnd}


		and is_valid = #{isValid}


		and is_unique = #{isUnique}


		and is_page_print = #{isPagePrint}


		and is_audit = #{isAudit}


		and is_hosp = #{isHosp}


		and dept_id = #{deptId}


		and dept_id is null


		and crte_id = #{crteId}


		and crte_name = #{crteName}


		and crte_time = #{crteTime}


		and code in

			#{item}

mapper.update:
update emr_classify
hosp_code = #{hospCode}, code = #{code}, name = #{name}, up_code = #{upCode}, page_print_code = #{pagePrintCode}, type_code = #{typeCode}, doc_code = #{docCode}, gender_code = #{genderCode}, pym = #{pym}, wbm = #{wbm}, seq = #{seq}, is_del_nullline = #{isDelNullline}, is_common = #{isCommon}, is_end = #{isEnd}, is_valid = #{isValid}, is_unique = #{isUnique}, is_page_print = #{isPagePrint}, is_audit = #{isAudit}, is_hosp = #{isHosp}, dept_id = #{deptId}, crte_id = #{crteId}, crte_name = #{crteName}, crte_time = #{crteTime}, record_time_code = #{recordTimeCode}
where id = #{id} and hosp_code = #{hospCode}

mapper.select:
select a.code as id,a.name as label,a.up_code as parentId ,if(a.is_end >0,true,false) as isEnd, a.valid_time from emr_classify as a left join emr_classify_template as b on a.hosp_code = b.hosp_code and a.code = b.classify_code and IFNULL(a.dept_id,'') = IFNULL(b.dept_id,'') where a.hosp_code = #{hospCode}
and a.dept_id = #{deptId}
and a.dept_id is null
order by a.seq asc

select
id, hosp_code, code, name, up_code, page_print_code, type_code, doc_code, gender_code, pym, wbm, is_del_nullline, is_common, is_end, is_valid, is_unique, is_page_print, is_audit, is_hosp, dept_id, crte_id, crte_name, crte_time
from emr_classify
where hosp_code=#{hospCode}

	and  up_code = #{upCode}

mapper.update:
update emr_classify set is_valid = #{isValid} where hosp_code = #{hospCode}

	and code in

		#{code}
