mapper.select:
select  CONCAT(
v.name,'/',
IFNULL((case v.gender_code when '1' then '男' when '2' then '女' when '0' then '未知' when '9' then '未说明的性别' else '' end),'-'),'/',
IFNULL(v.age,'-'),
IFNULL((case v.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' when '4' then '天' else '' end),'')
) AS patientInfo,v.*,if((select count(*) from emr_patient where hosp_code = v.hosp_code and visit_id = v.id and audit_code = '3') = 0,'否','是')  as isReject,
if((select count(*) from emr_patient where hosp_code = v.hosp_code and visit_id = v.id and audit_code = '1') = 0,'否','是')  as isAudit,
g.archive_state as isArchive

    , t.is_consule, t.apply_deptid

from inpt_visit v left join emr_archive_logging g on g.hosp_code = v.hosp_code and v.id = g.visit_id


    join (
    SELECT
    '1' as is_consule, ss.apply_deptid, ss.visit_id, ss.hosp_code
    FROM
    inpt_consultation_apply ss
    WHERE ss.hosp_code = #{hospCode}
    and ss.consul_state = '1'
    AND FIND_IN_SET(#{zgDoctorId}, ss.consul_userid_list)
    GROUP BY ss.visit_id, ss.hosp_code
    ) t on v.hosp_code = t.hosp_code and v.id = t.visit_id

where v.hosp_code = #{hospCode}


        and v.in_dept_id = #{inDeptId}



    and DATE_FORMAT(v.in_time,'%Y-%m-%d') >= #{ryStartTime} and DATE_FORMAT(v.in_time,'%Y-%m-%d') <= #{ryEndTime}



        and DATE_FORMAT(v.in_time, '%Y-%m-%d') <= #{ryEndTime}




        and DATE_FORMAT(v.in_time, '%Y-%m-%d') >= #{ryStartTime}



    and (v.name like concat('%', #{name,jdbcType=VARCHAR}, '%')
    or v.bed_name like concat('%', #{name,jdbcType=VARCHAR}, '%')
    or v.age like concat('%', #{name,jdbcType=VARCHAR}, '%') )


    and v.status_code = #{statusCode}


    and v.zg_doctor_id = #{zgDoctorId}



        order by v.out_time desc


        order by v.bed_name+0 asc,v.in_time desc

select v.*,if((select count(*) from emr_patient where visit_id = v.id and audit_code = '3') = 0,'否','是')  as isReject,
if((select count(*) from emr_patient where visit_id = v.id and audit_code = '1') = 0,'否','是')  as isAudit,
g.archive_state as isArchive, v.dept_name as inDeptName, v.crte_time as inTime
from outpt_visit v left join emr_archive_logging g on v.id = g.visit_id and g.hosp_code=v.hosp_code
where v.hosp_code = #{hospCode}

    and v.dept_id = #{deptId}



    and v.visit_time >= #{ryStartTime} and v.visit_time <= #{ryEndTime}



        and v.visit_time <= #{ryEndTime}




        and v.visit_time >= #{ryStartTime}



    and (v.name like concat('%', #{name,jdbcType=VARCHAR}, '%')
    or v.age like concat('%', #{name,jdbcType=VARCHAR}, '%') )

order by v.crte_time desc

select v.*,if((select count(*) from emr_patient where visit_id = v.id and audit_code = '3') = 0,'否','是')  as isReject,
if((select count(*) from emr_patient where visit_id = v.id and audit_code = '1') = 0,'否','是')  as isAudit,
g.archive_state as isArchive
from inpt_visit v left join emr_archive_logging g on v.id = g.visit_id
where v.hosp_code = #{hospCode} and v.id in (select distinct visit_id from oper_info_record where hosp_code = #{hospCode})

    and DATE_FORMAT(v.in_time,'%Y-%m-%d') between #{startDate} and #{endDate}



        and DATE_FORMAT(v.in_time, '%Y-%m-%d') <= #{endDate}




        and DATE_FORMAT(v.in_time, '%Y-%m-%d') >= #{startDate}



    and (v.name like concat('%', #{name,jdbcType=VARCHAR}, '%')
    or v.bed_name like concat('%', #{name,jdbcType=VARCHAR}, '%')
    or v.age like concat('%', #{name,jdbcType=VARCHAR}, '%') )

order by v.in_time desc

select record_time_code from  emr_classify where dept_id = #{deptId} and hosp_code = #{hospCode}


        and code = #{classifyCode}


       and code = (select classify_code from emr_classify_template where id = #{classifyTemplateId} and hosp_code = #{hospCode})

select e.code, e.type_code from emr_element e join emr_classify_element c on e.code = c.element_code
where e.hosp_code = #{hospCode} and c.hosp_code = #{hospCode} and c.classinfo_code = #{classifyCode}
    and c.dept_id is null and e.gender_code = '2'

select e.code, e.type_code from emr_element e join emr_classify_element c on e.code = c.element_code
where e.hosp_code = #{hospCode} and c.hosp_code = #{hospCode} and c.classinfo_code = #{classifyCode}
    and c.dept_id is null and e.gender_code = '1'

select  * from emr_patient_record a
left join emr_classify_template b on a.classify_template_id =b.id and a.hosp_code = b.hosp_code
LEFT JOIN emr_classify c on b.classify_code = c.`code` and b.hosp_code =c.hosp_code
where  a.hosp_code = #{hospCode}
and a.visit_id = #{visitId}
and c.name like '出院记录%'
and b.dept_id =#{deptId}
and c.dept_id =#{deptId}
and c.is_valid ='1'
limit 1

select count(1)
from emr_print
where hosp_code = #{hospCode}
and emr_id = #{emrId} and visit_id = #{visitId}

select * from (select DISTINCT v.name,v.age,v.gender_code as genderCode, v.in_dept_name as deptName, a.dept_id as deptId,c.`name` as emrName, (select name from emr_classify ec WHERE ec.`code` = c.up_code and ec.up_code = 'EMR' AND type_code = '2' AND is_valid = '1' AND is_end = '0' AND (dept_id = '' OR dept_id IS NULL) AND ec.hosp_code = #{hospCode} ) as upClassifyName, c.up_code as upCode,a.record_time as recordTime,a.crte_name as doctorName,a.review_name as reviewName,a.review_time as reviewTime,a.crte_time as crteTime, a.audit_code as auditCode, a.audit_time as auditTime,a.audit_name as auditName,ifnull(a.print_num,0) as printNum, ifnull(ar.archive_state,0) as archiveState, v.in_no as inNo, v.in_profile as inProfile from inpt_visit v LEFT JOIN emr_archive_logging ar on ar.visit_id = v.id and ar.hosp_code = v.hosp_code left join emr_patient a on a.visit_id = v.id and a.hosp_code = v.hosp_code left JOIN emr_classify_template b on a.classify_template_id = b.id and a.dept_id = b.dept_id and a.hosp_code = b.hosp_code left JOIN emr_classify c on b.classify_code =c.`code` and a.hosp_code = c.hosp_code where c.is_valid = '1' and c.is_del_nullline = '0' and c.type_code ='2' and v.hosp_code =#{hospCode}
and v.in_dept_id =#{deptId}
and c.up_code =#{classifyCode}
and a.crte_id=#{doctorId}
and (v.name like concat('%', #{keyword,jdbcType=VARCHAR}, '%') or c.name like concat('%', #{keyword,jdbcType=VARCHAR}, '%'))
and a.record_time <= #{endDate}
and a.record_time >= #{startDate}
GROUP BY a.id ) f order by f.name,f.deptId,f.recordTime desc

select id,hosp_code,emr_code,rules_name,rules_sql,tips,is_valid,crte_id,crte_name,crte_time,remark from emr_quality_data_rules where hosp_code = #{hospCode} and is_valid ='1'
and emr_code = #{classifyCode}
and emr_code = (select classify_code from emr_classify_template where id = #{classifyTemplateId} and hosp_code = #{hospCode})

select GROUP_CONCAT(consul_userid_list)
from inpt_consultation_apply
where visit_id=#{visitId} and hosp_code =#{hospCode}
