mapper.select:
select
id, hosp_code, visit_id, baby_id, record_time, in_num, temperature_code, temperature, temperature_retest, pulse,
heart_rate, blood_sugar, is_ventilator, breath, forty_up_code, forty_up_remark, thirty_five_down_code,
thirty_five_down_remark, intake, output, other_output, skin_code, remark, excrement_code, pee_code, height, weight, girth, am_bp, pm_bp, is_operation,
operation_days, operation_cnt, crte_id, crte_name, crte_time , drug_allergy
from inpt_nurse_third


        and id = #{id}

    and hosp_code = #{hospCode}

        and visit_id = #{visitId}


        and baby_id = #{babyId}


        and record_time = #{recordTime}



        and in_num = #{inNum}


        and temperature_code = #{temperatureCode}


        and temperature = #{temperature}


        and temperature_retest = #{temperatureRetest}


        and pulse = #{pulse}


        and heart_rate = #{heartRate}


        and blood_sugar = #{bloodSugar}


        and is_ventilator = #{isVentilator}


        and breath = #{breath}


        and forty_up_code = #{fortyUpCode}


        and forty_up_remark = #{fortyUpRemark}


        and thirty_five_down_code = #{thirtyFiveDownCode}


        and thirty_five_down_remark = #{thirtyFiveDownRemark}


        and intake = #{intake}


        and output = #{output}


        and other_output = #{otherOutput}


        and skin_code = #{skinCode}


        and remark = #{remark}


        and drug_allergy = #{drugAllergy}


        and excrement_code = #{excrementCode}


        and pee_code = #{peeCode}


        and height = #{height}


        and weight = #{weight}


        and girth = #{girth}


        and am_bp = #{amBp}


        and pm_bp = #{pmBp}


        and is_operation = #{isOperation}


        and operation_days = #{operationDays}


        and operation_cnt = #{operationCnt}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


       and record_time between #{startTime} and #{endTime}

select
id, hosp_code, visit_id, baby_id, record_time, in_num, temperature_code, temperature, temperature_retest, pulse,
heart_rate, blood_sugar, is_ventilator, breath, forty_up_code, forty_up_remark, thirty_five_down_code,
thirty_five_down_remark, intake, output, other_output, skin_code, remark, excrement_code, pee_code, height, weight, girth, am_bp, pm_bp, is_operation,
operation_days, operation_cnt, crte_id, crte_name, crte_time, drug_allergy
from inpt_nurse_third

    and hosp_code = #{hospCode}

        and visit_id = #{visitId}


        and baby_id = #{babyId}


        and record_time like concat(#{queryTime},'%')

mapper.delete:
delete
from inpt_nurse_third
where  visit_id= #{visitId}
  and hosp_code = #{hospCode}
  and DATE_FORMAT(record_time,'%Y-%m-%d') = #{queryTime}

    and baby_id = #{babyId}

mapper.select:
SELECT SJ.time_slot, SJ.time_slot as start_day, SJ.time_slot as record_time, SCD.hosp_code, SCD.visit_id, SCD.baby_id, SCD.record_time, SCD.temperature_code, SCD.in_num, IF(SCD.temperature = 0, NULL, SCD.temperature ) as temperature, IF(SCD.temperature_retest = 0, NULL, SCD.temperature_retest ) as temperature_retest, IF(SCD.pulse = 0, NULL, SCD.pulse ) as pulse, IF(SCD.heart_rate = 0, NULL, SCD.heart_rate ) as heart_rate, IF(SCD.blood_sugar = 0, NULL, SCD.blood_sugar ) as blood_sugar, SCD.is_ventilator, IF(SCD.breath = 0, NULL, SCD.breath ) as breath, SCD.forty_up_code, SCD.forty_up_remark, SCD.thirty_five_down_code, SCD.thirty_five_down_remark, SCD.intake, SCD.output, SCD.other_output, SCD.skin_code, SCD.remark, SCD.excrement_code, SCD.pee_code, SCD.height, SCD.weight, SCD.girth, SCD.am_bp, SCD.pm_bp, SCD.is_operation, SCD.operation_days, SCD.operation_cnt, SCD.crte_id, SCD.crte_name, SCD.crte_time, SCD.drug_allergy FROM ( SELECT dates.date, T.sjd, CONCAT( dates.date, ' ', T.sjd, ':00' ) time_slot FROM ( SELECT a.date FROM ( SELECT DATE_FORMAT(#{endDate},'%Y-%m-%d') - INTERVAL ( a.a + ( 10 * b.a ) + ( 100 * c.a ) ) DAY AS date FROM ( SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 ) AS a CROSS JOIN ( SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 ) AS b CROSS JOIN ( SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 ) AS c ) a WHERE
a.Date BETWEEN DATE_FORMAT(#{startDate},'%Y-%m-%d') and DATE_FORMAT(#{endDate},'%Y-%m-%d') ) dates LEFT JOIN( SELECT DISTINCT CONCAT( SUBSTRING_INDEX( SUBSTRING_INDEX( a.path, ',', b.help_topic_id + 1 ), ',',- 1 ), ':00' ) AS sjd FROM ( SELECT ( SELECT VALUE AS sjd FROM sys_parameter WHERE CODE = 'HLSCD_KSSJ' AND hosp_code = #{hospCode} ) AS path FROM DUAL ) a JOIN mysql.help_topic b ON b.help_topic_id < ( LENGTH( a.path ) - LENGTH( REPLACE ( a.path, ',', '' )) + 1 ) ) T ON 1 = 1 ) SJ LEFT JOIN (select * from inpt_nurse_third WHERE 1=1 and visit_id = #{visitId} and hosp_code= #{hospCode}
and baby_id = #{babyId}
and (baby_id is null or LENGTH(TRIM(baby_id)) < 1)
) SCD ON SCD.record_time = SJ.time_slot
ORDER BY SJ.time_slot,SCD.visit_id

select
    id, hosp_code, visit_id, baby_id, in_no, advice_id, name, gender_code, age, age_unit_code, blood_code, dept_id, oper_dept_id, bed_id, bed_name, oper_disease_id, oper_disease_icd9, oper_disease_name, in_disease_id, in_disease_name, in_disease_icd10, doctor_id, doctor_name, assistant_id1, assistant_name1, assistant_id2, assistant_name2, assistant_id3, assistant_name3, special_request, content, audit_id, audit_name, audit_time, audit_remark, oper_plan_time, oper_room_Id, oper_room_name, xh_nurse_id, xh_nurse_name, qx_nurse_id, qx_nurse_name, ana_code, ana_id1, ana_name1, ana_id2, ana_name2, ana_id3, ana_name3, oper_end_time, notched_code, heal_code, oper_time_hour, status_code, is_cost, total_price, cost_time, cost_id, cost_name, remark, crte_id, crte_name, crte_time, rank, ward_id
from oper_info_record
where hosp_code = #{hospCode}
    and visit_id = #{visitId}
    and status_code in ('3','4')

SELECT info.time_slot, info.sjd, info.date, info.bed_name, info.name, info.in_profile, info.in_no, info.visit_id, info.in_time as in_time_batch, info.out_time, it.* FROM ( SELECT sj.time_slot, sj.sjd, sj.date, iv.id AS visit_id, iv.hosp_code, iv.in_time, iv.out_time, iv.bed_name, iv.name, iv.in_profile, iv.in_no, bb.seq_no FROM ( SELECT dates.date, T.sjd, CONCAT( dates.date, ' ', T.sjd, ':00' ) time_slot FROM ( SELECT a.date FROM ( SELECT DATE_FORMAT(#{endDate},'%Y-%m-%d') - INTERVAL ( a.a + ( 10 * b.a ) + ( 100 * c.a ) ) DAY AS date FROM ( SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 ) AS a CROSS JOIN ( SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 ) AS b CROSS JOIN ( SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 ) AS c ) a WHERE a.Date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(#{endDate}, '%Y-%m-%d') ) dates LEFT JOIN ( SELECT DISTINCT CONCAT( SUBSTRING_INDEX( SUBSTRING_INDEX( a.path, ',', b.help_topic_id + 1 ), ',',- 1 ), ':00' ) AS sjd FROM ( SELECT ( SELECT VALUE AS sjd FROM sys_parameter WHERE CODE = 'HLSCD_KSSJ' AND hosp_code = #{hospCode} ) AS path FROM DUAL ) a JOIN mysql.help_topic b ON b.help_topic_id < ( LENGTH( a.path ) - LENGTH( REPLACE ( a.path, ',', '' )) + 1 ) ) T ON 1 = 1 ) sj LEFT JOIN inpt_visit iv ON 1 = 1 left join base_bed bb on iv.hosp_code = bb.hosp_code and iv.bed_id = bb.id WHERE iv.hosp_code = #{hospCode} AND iv.status_code = '2'
AND iv.in_dept_id = #{deptId}
AND sj.time_slot IN ( CONCAT( DATE_FORMAT(#{startDate}, '%Y-%m-%d') , ' ', #{sjd}, ':00:00' ) )
and iv.id in
#{visitId}
ORDER BY iv.in_time DESC ) info LEFT JOIN inpt_nurse_third it ON info.visit_id = it.visit_id AND info.hosp_code = it.hosp_code AND info.time_slot = it.record_time and it.baby_id is null order by info.seq_no

SELECT info.time_slot, info.sjd, info.date, info.bed_name, info.name, info.in_profile, info.in_no, info.visit_id, info.baby_id, info.baby_name, info.in_time as in_time_batch, info.out_time, it.* FROM ( SELECT sj.time_slot, sj.sjd, sj.date, iv.id AS visit_id, ib.id as baby_id, ib.name as baby_name, iv.hosp_code, iv.in_time, iv.out_time, iv.bed_name, iv.name, iv.in_profile, iv.in_no, bb.seq_no FROM ( SELECT dates.date, T.sjd, CONCAT( dates.date, ' ', T.sjd, ':00' ) time_slot FROM ( SELECT a.date FROM ( SELECT DATE_FORMAT(#{endDate},'%Y-%m-%d') - INTERVAL ( a.a + ( 10 * b.a ) + ( 100 * c.a ) ) DAY AS date FROM ( SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 ) AS a CROSS JOIN ( SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 ) AS b CROSS JOIN ( SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 ) AS c ) a WHERE a.Date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(#{endDate}, '%Y-%m-%d') ) dates LEFT JOIN ( SELECT DISTINCT CONCAT( SUBSTRING_INDEX( SUBSTRING_INDEX( a.path, ',', b.help_topic_id + 1 ), ',',- 1 ), ':00' ) AS sjd FROM ( SELECT ( SELECT VALUE AS sjd FROM sys_parameter WHERE CODE = 'HLSCD_KSSJ' AND hosp_code = #{hospCode} ) AS path FROM DUAL ) a JOIN mysql.help_topic b ON b.help_topic_id < ( LENGTH( a.path ) - LENGTH( REPLACE ( a.path, ',', '' )) + 1 ) ) T ON 1 = 1 ) sj JOIN inpt_visit iv ON 1 = 1 RIGHT JOIN inpt_baby ib on iv.hosp_code = ib.hosp_code and iv.id = ib.visit_id join base_bed bb on iv.hosp_code = bb.hosp_code and iv.bed_id = bb.id WHERE iv.hosp_code = #{hospCode} AND iv.status_code = '2'
AND iv.in_dept_id = #{deptId}
AND sj.time_slot IN ( CONCAT( DATE_FORMAT(#{startDate}, '%Y-%m-%d') , ' ', #{sjd}, ':00:00' ) )
and iv.id in
#{visitId}
and ib.id in
#{babyId}
ORDER BY iv.in_time DESC ) info LEFT JOIN inpt_nurse_third it ON info.visit_id = it.visit_id AND info.hosp_code = it.hosp_code AND info.time_slot = it.record_time and info.baby_id = it.baby_id order by info.seq_no

update.foreach:
update inpt_nurse_third
visit_id = #{dto.visitId}, record_time = #{dto.recordTime}, in_num = #{dto.inNum}, temperature_code = #{dto.temperatureCode}, temperature = #{dto.temperature}, temperature_retest = #{dto.temperatureRetest}, pulse = #{dto.pulse}, heart_rate = #{dto.heartRate}, blood_sugar = #{dto.bloodSugar}, is_ventilator = #{dto.isVentilator}, breath = #{dto.breath}, forty_up_code = #{dto.fortyUpCode}, forty_up_remark = #{dto.fortyUpRemark}, thirty_five_down_code = #{dto.thirtyFiveDownCode}, thirty_five_down_remark = #{dto.thirtyFiveDownRemark}, intake = #{dto.intake}, output = #{dto.output}, other_output = #{dto.otherOutput}, remark = #{dto.remark}, skin_code = #{dto.skinCode}, excrement_code = #{dto.excrementCode}, pee_code = #{dto.peeCode}, height = #{dto.height}, weight = #{dto.weight}, girth = #{dto.girth}, am_bp = #{dto.amBp}, pm_bp = #{dto.pmBp}, is_operation = #{dto.isOperation}, operation_days = #{dto.operationDays}, operation_cnt = #{dto.operationCnt}, crte_id = #{dto.crteId}, crte_name = #{dto.crteName}, crte_time = #{dto.crteTime}, drug_allergy = #{dto.drugAllergy},
where id = #{dto.id} and hosp_code = #{dto.hospCode}
