mapper.select:
select ic.*,
(select min(DATE_FORMAT(cost_time,'%Y-%m-%d')) from inpt_cost where hosp_code = #{hospCode} and visit_id = #{visitId}) as startCostDate,
(select max(DATE_FORMAT(cost_time,'%Y-%m-%d')) from inpt_cost where hosp_code = #{hospCode} and visit_id = #{visitId}) as endCostDate,
(select c.name from base_finance_classify c where c.id = ic.bfc_id and c.hosp_code = #{hospCode}) as bfcName,
iic.guest_ratio as guestRatio
from inpt_cost ic left join insure_individual_cost iic on ic.id = iic.cost_id
where ic.hosp_code = #{hospCode} and ic.cost_time is not null

        AND ic.visit_id = #{visitId}


        AND ic.status_code = #{statusCode}


        and DATE_FORMAT(ic.cost_time,'%Y-%m-%d %H:%i:%s') >= #{costStartTime} and DATE_FORMAT(ic.cost_time,'%Y-%m-%d %H:%i:%s') <= #{costStopTime}



            and DATE_FORMAT(ic.cost_time, '%Y-%m-%d %H:%i:%s') <= #{costStopTime}




            and DATE_FORMAT(ic.cost_time, '%Y-%m-%d %H:%i:%s') >= #{costStartTime}



    and (ic.baby_id is null or ic.baby_id = '')


    AND ic.baby_id = #{babyId}

    order by ic.cost_time, ic.bfc_id

select id,hosp_code,visit_id,baby_id,iat_id,iatd_group_no,iatd_seq_no,advice_exec_id,source_code,source_id,old_cost_id,source_dept_id, in_dept_id,bfc_id,item_code,item_id,item_name,price,spec,prep_code,dosage,dosage_unit_code,usage_code, rate_id,speed_code,use_days,total_num as num,total_num_unit_code as numUnitCode,herb_note_code,use_code,herb_num,total_price, preferential_price,reality_price as amountMoney,back_num,back_code,doctor_id,doctor_name,dept_id,phar_id,is_dist,is_give, is_ok,ok_id,ok_name,ok_time,settle_code,settle_id,is_check,check_id,check_name,check_time,zz_doctor_id,zz_doctor_name,jz_doctor_id, jz_doctor_name,zg_doctor_id,zg_doctor_name,exec_id,exec_name,exec_time,exec_dept_id,plan_exec_time,status_code,is_cost, cost_time,DATE_FORMAT(cost_time,'%Y-%m-%d') as cost_date,crte_id,crte_name,crte_time, (select min(DATE_FORMAT(ic.cost_time,'%Y-%m-%d')) from inpt_cost ic where ic.hosp_code = hosp_code and ic.visit_id = visit_id) as startCostDate, (select max(DATE_FORMAT(ic.cost_time,'%Y-%m-%d')) from inpt_cost ic where ic.hosp_code = hosp_code and ic.visit_id = visit_id) as endCostDate, (select c.name from base_finance_classify c where c.id = bfc_id and c.hosp_code = hosp_code) as bfcName from inpt_cost where hosp_code = #{hospCode} and cost_time is not null
AND visit_id in
#{visitId}
AND status_code = #{statusCode}
order by cost_time,bfc_id

select max(ic.id) as id, max(ic.hosp_code) as hospCode, max(ic.visit_id) as visitId, max(ic.bfc_id) as bfcId, max(ic.item_code) as itemCode, ic.item_id, max(ic.item_name) as itemName, ic.price as price, sum(ic.total_num) as num, max(ic.spec) as spec, max(ic.dosage_unit_code) as dosageUnitCode, max(ic.usage_code) as usageCode, max(ic.total_num_unit_code) as numUnitCode , sum(ic.total_price) as amountMoney, max(ic.cost_time) as costTime, DATE_FORMAT(max(ic.cost_time),'%Y-%m-%d') as costDate, max(ic.crte_time), (select min(DATE_FORMAT(cost_time,'%Y-%m-%d')) from inpt_cost where hosp_code = #{hospCode} and visit_id = #{visitId}) as startCostDate, (select max(DATE_FORMAT(cost_time,'%Y-%m-%d')) from inpt_cost where hosp_code = #{hospCode} and visit_id = #{visitId}) as endCostDate, (select c.name from base_finance_classify c where c.id = max(bfc_id) and c.hosp_code = #{hospCode}) as bfcName ,bd.code as drugItemCode,bi.code as itemItemCode,bm.code as mateItemCode from inpt_cost ic left join base_drug bd on bd.id = ic.item_id and bd.hosp_code = ic.hosp_code left join base_item bi on bi.id = ic.item_id and bi.hosp_code = ic.hosp_code left join base_material bm on bm.id = ic.item_id and bm.hosp_code = ic.hosp_code where ic.hosp_code = #{hospCode} and ic.cost_time is not null and ic.status_code ='0'
AND ic.visit_id = #{visitId}
AND ic.status_code = #{statusCode}
and (ic.baby_id is null or ic.baby_id = '')
AND ic.baby_id = #{babyId}
and DATE_FORMAT(ic.cost_time,'%Y-%m-%d %H:%i:%s') >= #{costStartTime} and DATE_FORMAT(ic.cost_time,'%Y-%m-%d') <= #{costStopTime}
and DATE_FORMAT(ic.cost_time, '%Y-%m-%d %H:%i:%s') <= #{costStopTime}
and DATE_FORMAT(ic.cost_time, '%Y-%m-%d %H:%i:%s') >= #{costStartTime}
group by ic.item_id,ic.price order by max(ic.crte_time),max(ic.bfc_id)

select max(id) as id, max(hosp_code) as hospCode, max(visit_id) as visitId, max(bfc_id) as bfcId, max(item_code) as itemCode, item_id, max(item_name) as itemName, price as price, sum(total_num) as num, max(spec) as spec, max(dosage_unit_code) as dosageUnitCode, max(usage_code) as usageCode, max(total_num_unit_code) as numUnitCode , sum(total_price) as amountMoney, max(cost_time) as costTime, DATE_FORMAT(max(crte_time),'%Y-%m-%d') as costDate, max(crte_time), (select min(DATE_FORMAT(ic.crte_time,'%Y-%m-%d')) from inpt_cost ic where ic.hosp_code = hosp_code and ic.visit_id = visit_id) as startCostDate, (select max(DATE_FORMAT(ic.crte_time,'%Y-%m-%d')) from inpt_cost ic where ic.hosp_code = hosp_code and ic.visit_id = visit_id) as endCostDate, (select c.name from base_finance_classify c where c.id = max(bfc_id) and c.hosp_code = hosp_code) as bfcName from inpt_cost where hosp_code = #{hospCode} and cost_time is not null and status_code ='0'
AND visit_id in
#{visitId}
AND status_code = #{statusCode}
group by item_id,price order by max(crte_time),max(bfc_id)

select id, hosp_code, visit_id, ia_id, order_no, group_no, group_seq_no, content, usage_code, rate_id, speed_code, is_skin, is_positive, exec_id, exec_name, second_exec_id, second_exec_name, last_exec_time, check_id, check_name, check_time, stop_doctor_id, stop_doctor_name, stop_time, stop_check_id, stop_check_name, stop_check_time, is_long, crte_id, crte_name, crte_time, is_in_print, is_stop_print, is_exec_print, is_skin_print, is_in_check_print, is_stop_check_print,seq_no,cancel_id,cancel_name,cancel_time,DATE_FORMAT(last_exec_time,'%Y-%m-%d %H:%i') as execTimeNoM, long_start_time,DATE_FORMAT(long_start_time,'%Y-%m-%d') as startOnlyDate,DATE_FORMAT(long_start_time,'%H:%i') as startOnlyTime, DATE_FORMAT(stop_time,'%Y-%m-%d') as stopOnlyDate,DATE_FORMAT(stop_time,'%H:%i') as stopOnlyTime from inpt_advice_print where hosp_code = #{hospCode} and visit_id = #{visitId}
and find_in_set(id,#{ids1})
and is_long = #{isLong}
and is_valid = #{isValid}

select
a.id, a.hosp_code, a.visit_id, a.ia_id, a.order_no, a.group_no, a.group_seq_no, a.content, a.usage_code, a.rate_id, a.speed_code,
a.is_skin, a.is_positive, a.exec_id, a.exec_name, a.second_exec_id, a.second_exec_name, a.last_exec_time, a.check_id, a.check_name,
a.check_time, a.stop_doctor_id, a.stop_doctor_name, a.stop_time,ia.teach_doctor_id,ia.teach_doctor_name, a.stop_check_id, a.stop_check_name, a.stop_check_time,
a.is_long, a.crte_id, a.crte_name, a.crte_time, a.is_in_print, a.is_stop_print, a.is_exec_print, a.is_skin_print, a.is_in_check_print,
a.is_stop_check_print,a.seq_no,a.cancel_id,a.cancel_name,a.cancel_time,a.skin_result_code,
DATE_FORMAT(a.last_exec_time,'%Y-%m-%d %H:%i') as execTimeNoM,ia.item_name,
ia.item_id,ia.item_code,ia.yylx,ia.type_code,ia.total_num,ia.total_num_unit_code,ia.use_code,ia.dosage,ia.dosage_unit_code,
a.long_start_time,DATE_FORMAT(a.long_start_time,'%Y-%m-%d') as startOnlyDate,DATE_FORMAT(a.long_start_time,'%H:%i') as startOnlyTime,
DATE_FORMAT(a.stop_time,'%Y-%m-%d') as stopOnlyDate,DATE_FORMAT(a.stop_time,'%H:%i') as stopOnlyTime,
(select d.name from base_rate d where d.id = a.rate_id and d.hosp_code = #{hospCode}) as rateName, bd.big_type_code,
ba.is_stop_myself
from inpt_advice_print a
join inpt_advice ia on ia.id = a.ia_id and ia.hosp_code = a.hosp_code
left JOIN base_drug bd on ia.hosp_code = bd.hosp_code and ia.item_id = bd.id
left join base_advice ba on ia.hosp_code = ba.hosp_code and ia.item_id = ba.id
where a.hosp_code = #{hospCode}

    and a.id = #{id}


    and a.visit_id = #{visitId}


    and a.order_no = #{orderNo}


    and a.group_no = #{groupNo}


    and a.group_seq_no = #{groupSeqNo}


    and a.content = #{content}


    and a.usage_code = #{usageCode}


    and a.rate_id = #{rateId}


    and a.speed_code = #{speedCode}


    and a.is_skin = #{isSkin}


    and a.is_positive = #{isPositive}


    and a.exec_id = #{execId}


    and a.exec_name = #{execName}


    and a.second_exec_id = #{secondExecId}


    and a.second_exec_name = #{secondExecName}


    and a.last_exec_time = #{lastExecTime}


    and a.check_id = #{checkId}


    and a.check_name = #{checkName}


    and a.check_time = #{checkTime}


    and a.stop_doctor_id = #{stopDoctorId}


    and a.stop_doctor_name = #{stopDoctorName}


    and a.stop_time = #{stopTime}


    and a.stop_check_id = #{stopCheckId}


    and a.stop_check_name = #{stopCheckName}


    and a.stop_check_time = #{stopCheckTime}


    and a.is_long = #{isLong}


    and a.crte_id = #{crteId}


    and a.crte_name = #{crteName}


    and a.crte_time = #{crteTime}


    and a.is_in_print = #{isInPrint}


    and a.is_stop_print = #{isStopPrint}


    and a.is_exec_print = #{isExecPrint}


    and a.is_skin_print = #{isSkinPrint}


    and a.is_in_check_print = #{isInCheckPrint}


    and a.is_stop_check_print = #{isStopCheckPrint}


  and a.is_valid = #{isValid}


      and ia.baby_id =#{babyId}


      and (ia.baby_id is null or LENGTH(TRIM(ia.baby_id)) < 1)


      and (ia.baby_id is null or ia.baby_id = '')

  order by a.long_start_time,a.seq_no

mapper.update:
update inpt_advice_print
is_in_print = '0', is_stop_print = '0', is_exec_print = '0', is_skin_print = '0', is_in_check_print = '0', is_stop_check_print = '0',
where hosp_code = #{hospCode} and visit_id = #{visitId}
and is_long = #{isLong}

update inpt_advice_print set is_valid = "0" where hosp_code = #{hospCode} and visit_id = #{visitId}
and find_in_set(id,#{deleteIds})
and find_in_set(id,#{ids1})
