mapper.select:
SELECT iv.in_no AS id, iv.id as visit_id, concat( iv.NAME, '[ ', iv.in_no, ' ]', '(', ifnull( iv.bed_name, '未安床' ), ')' ) AS label, bd.`code` AS parentId FROM base_dept bd inner JOIN inpt_visit iv ON bd.id = iv.in_dept_id WHERE bd.CODE in
#{dcode}
AND bd.CODE != 'B30'
and DATE_FORMAT(in_time, '%Y-%m-%d') >= #{startTime}
and DATE_FORMAT(in_time, '%Y-%m-%d') <= #{endTime}
and iv.`name` like concat('%',#{patientName},'%')
and in_dept_id = #{deptId}
union all SELECT ov.visit_no AS id, ov.id AS visit_id, concat( ov.NAME, '[ ', ov.visit_no, ' ]', '(', '', ')' ) AS label, bd.`code` AS parentId FROM base_dept bd inner JOIN outpt_visit ov ON bd.id = ov.dept_id WHERE bd.CODE in
#{dcode}
AND bd.CODE != 'B30' and ov.tran_in_code = '0' AND ov.is_visit = '1'
and DATE_FORMAT(ov.crte_time, '%Y-%m-%d') >= #{startTime}
and DATE_FORMAT(ov.crte_time, '%Y-%m-%d') <= #{endTime}
and ov.`name` like concat('%',#{patientName},'%')
and ov.`dept_id` = #{deptId}

SELECT
    `apply_no` AS applyNo,
    `visit_id` AS visitId,
    `in_no` AS inNo,
    `order_no` AS orderNo,
    `result_id` AS resultId,
    `opd_id` AS opdId,
    content,
    `crte_time` AS crteTime

FROM
    medic_apply
WHERE
    hosp_code=#{hospCode}
  and type_code= #{typeCode}
  and	in_no = #{inNo}

    and crte_Time > #{startTime}


    and crte_Time < #{endTime}


    and type_code = #{typeCode}


    and baby_id = #{babyId}


    and (baby_id is null or LENGTH(TRIM(baby_id)) < 1)
