mapper.select:
SELECT iv.in_no AS id, iv.id as visit_id, concat( iv.NAME, '[ ', iv.in_no, ' ]', '(', ifnull( iv.bed_name, '未安床' ), ')' ) AS label, bd.`code` AS parentId FROM base_dept bd inner JOIN inpt_visit iv ON bd.id = iv.in_dept_id WHERE bd.CODE in
#{dcode}
AND bd.CODE != 'B30'
and DATE_FORMAT(in_time, '%Y-%m-%d') >= #{startTime}
and DATE_FORMAT(in_time, '%Y-%m-%d') <= #{endTime}
and iv.`name` like concat('%',#{patientName},'%')
and in_dept_id = #{deptId}
union all SELECT ov.visit_no AS id, ov.id AS visit_id, concat( ov.NAME, '[ ', ov.visit_no, ' ]', '(', '', ')' ) AS label, bd.`code` AS parentId FROM base_dept bd inner JOIN outpt_visit ov ON bd.id = ov.dept_id WHERE bd.CODE in
#{dcode}
AND bd.CODE != 'B30' and ov.tran_in_code = '0' AND ov.is_visit = '1'
and DATE_FORMAT(ov.crte_time, '%Y-%m-%d') >= #{startTime}
and DATE_FORMAT(ov.crte_time, '%Y-%m-%d') <= #{endTime}
and ov.`name` like concat('%',#{patientName},'%')
and ov.`dept_id` = #{deptId}

SELECT a.apply_no AS applyNo, a.visit_id AS visitId, a.in_no AS inNo, a.order_no AS orderNo, a.result_id AS resultId, a.opd_id as opdId, case when b.id is not null then CONCAT (a.bar_code,c.code) else CONCAT (a.bar_code,e.code) end AS zhId, a.content, a.crte_time AS crteTime from medic_apply a left join inpt_advice_detail b on a.hosp_code = b.hosp_code and a.opd_id = b.id left join base_advice c on a.hosp_code = b.hosp_code and b.ba_id =c.id left join outpt_prescribe_detail d on a.hosp_code = d.hosp_code and a.opd_id = d.id left join base_advice e on d.hosp_code = e.hosp_code and d.item_id =e.id WHERE a.hosp_code=#{hospCode} and a.type_code= #{typeCode} and a.in_no = #{inNo}
and a.crte_Time > #{startTime}
and a.crte_Time < #{endTime}
and a.type_code = #{typeCode}
and a.baby_id = #{babyId}
and (a.baby_id is null or LENGTH(TRIM(baby_id)) < 1)
