mapper.update:
update inpt_long_cost set last_exec_time = #{lastExecTime}
where id in

    #{id}

mapper.select:
select iap.visit_id, sum(price) total_price from inpt_advance_pay iap where iap.hosp_code = #{hospCode} and iap.is_settle = '0' and iap.status_code = '0' and iap.visit_id in
#{item}
group by iap.visit_id

select ic.visit_id, sum(reality_price) total_price from inpt_cost ic where ic.hosp_code = #{hospCode} and ic.visit_id in
#{item}
group by ic.visit_id

update.foreach:
update inpt_visit
set total_cost = #{item.totalCost}, total_balance = #{item.totalBalance}, total_advance = #{item.totalAdvance}
where id = #{item.id}

mapper.select:
select a.id, a.hosp_code AS hospCode, a.visit_id AS visitId, a.baby_id AS babyId, a.dept_id AS deptId, a.item_code AS itemCode, a.item_id AS itemId, a.item_name AS itemName, (case when a.item_code = '1' then (select bd.nation_code from base_drug bd where a.item_id = bd.id and bd.hosp_code =a.hosp_code ) when a.item_code = '2' then (select bm.nation_code from base_material bm where a.item_id = bm.id and bm.hosp_code = a.hosp_code ) when a.item_code = '3' then (select bi.nation_code from base_item bi where a.item_id = bi.id and bi.hosp_code = a.hosp_code ) end ) as nationCode, (case when a.item_code = '1' then (select bd.nation_name from base_drug bd where a.item_id = bd.id and bd.hosp_code =a.hosp_code ) when a.item_code = '2' then (select bm.nation_name from base_material bm where a.item_id = bm.id and bm.hosp_code = a.hosp_code ) when a.item_code = '3' then (select bi.nation_name from base_item bi where a.item_id = bi.id and bi.hosp_code = a.hosp_code ) end ) as nationName, (case when a.item_code ='2' then e.bfc_id when a.item_code = '3' then d.bfc_id else '' end) as bfcId , (case when a.item_code ='2' then e.bfc_name when a.item_code = '3' then d.bfc_name else '' end) as bfcName , a.price, a.num, a.num as totalNum, a.unit_code AS unitCode, a.total_price AS totalPrice, a.use_code as useCode, a.phar_id as pharId, a.last_exec_time AS lastExecTime, a.charge_time AS chargeTime, a.start_time AS startTime, a.end_time AS endTime, a.source_type_code AS sourceTypeCode, a.source_id AS sourceId, a.is_cancel AS isCancel, a.cancel_id AS cancelId, a.cancel_name AS cancelName, a.cancel_time AS cancelTime, a.cancel_remark AS cancelRemark, a.crte_id AS crteId, a.crte_name AS crteName, a.crte_time AS crteTime from inpt_long_cost a left join (select b.id as item_id,f.id as bfc_id,f.name as bfc_name,b.hosp_code from base_item b ,base_finance_classify f where b.bfc_code = f.code and b.hosp_code = f.hosp_code ) d on a.item_id = d.item_id and a.hosp_code = d.hosp_code left join (select b.id as item_id,f.id as bfc_id,f.name as bfc_name,b.hosp_code from base_material b ,base_finance_classify f where b.bfc_code = f.code and b.hosp_code = f.hosp_code) e on a.item_id = e.item_id and a.hosp_code = e.hosp_code where a.visit_id =#{visitId} and a.source_type_code = #{sourceCode}
and (a.baby_id is null or a.baby_id = '')
AND a.baby_id = #{babyId}

mapper.update:
update inpt_long_cost
set end_time = #{endTime},
is_cancel = '1',
cancel_id = #{cancelId},
cancel_name = #{cancelName},
cancel_time = #{cancelTime},
cancel_remark = #{cancelRemark}
where id = #{id}
and hosp_code = #{hospCode}
