mapper.select:
select (a.price*a.num) as sumPrice,a.*,ib.name as babyName,ib.gender_code as babyGenderCode from ( SELECT c.id AS costId, c.item_id AS itemId, c.item_name AS itemName, c.price, c.num, v.NAME, v.age, v.age_unit_code, v.bed_name, v.in_no, d.NAME AS sourceDeptName, c.crte_time, v.gender_code, c.baby_id, c.hosp_code, CASE WHEN c.is_ok = '1' THEN '确费' WHEN c.is_ok = '0' THEN '未确费' WHEN (c.is_ok = '2') THEN '已申请退费' WHEN c.is_ok = '3' THEN '已退费' ELSE '未确费' END AS isOk FROM medic_apply ma, inpt_advice b, inpt_cost c, inpt_visit v, base_dept d WHERE ma.apply_no = b.technology_no AND ma.visit_id = v.id and ma.hosp_code = c.hosp_code and ma.hosp_code = v.hosp_code and ma.hosp_code = d.hosp_code and ma.visit_id = b.visit_id AND b.id = c.iat_id AND d.id = c.dept_id AND ma.hosp_code = #{hospCode} AND ma.type_code = #{typeCode}
and c.exec_dept_id = #{execDeptId}
and c.source_dept_id = #{sourceDeptId}
and ma.medic_type = #{technologyCode}
and c.crte_time >= #{startTime}
and c.crte_time <= #{endTime}
and v.name like concat('%', #{keyword}, '%' )
and (c.is_ok = #{isOk} or c.is_ok is null) and c.status_code = '0'
/*查询已退费状态的医技*/ and c.status_code = '1'
and c.is_ok = #{isOk} and c.status_code = '0'
) a left join inpt_baby ib on a.baby_id = ib.id and a.hosp_code = ib.hosp_code

mapper.update:
update inpt_cost set is_ok = '1', ok_id = #{crteId}, ok_name = #{crteName}, ok_time = #{crteTime}
where hosp_code = #{hospCode} and id in in

    #{item}

update outpt_cost set is_technology_ok = '1', technology_ok_id = #{crteId}, technology_ok_name = #{crteName}, technology_ok_time = #{crteTime}
where hosp_code = #{hospCode} and id in

    #{item}

update inpt_cost set is_ok = '0', ok_id = null, ok_name = null, ok_time = null
where hosp_code = #{hospCode} and id in

    #{item}

update outpt_cost set is_technology_ok = '0', technology_ok_id = null, technology_ok_name = null, technology_ok_time = null
where hosp_code = #{hospCode} and id in

    #{item}

mapper.select:
SELECT c.id AS costId, c.item_id AS itemId, c.item_name AS itemName, c.price, c.num, (c.price*c.num) as sumPrice, v.NAME, v.age, v.age_unit_code, v.visit_no as inNo, d.NAME AS sourceDeptName, c.crte_time, v.gender_code, c.hosp_code, C.technology_ok_name AS technologyOkName, C.technology_ok_time AS technologyOkTime, CASE WHEN (c.is_technology_ok = '1'and c.status_code = '0') THEN '确费' WHEN (c.is_technology_ok = '0'and c.status_code = '0') THEN '未确费' WHEN (c.is_technology_ok = '2' and c.status_code = '0') THEN '已申请退费' WHEN c.status_code = '1' THEN '已退费' WHEN c.is_technology_ok = '3' THEN '已退费' ELSE '未确费' END AS isOk FROM medic_apply ma, outpt_prescribe_detail b, outpt_cost c, outpt_visit v, base_dept d WHERE ma.opd_id = B.id AND ma.visit_id = v.id and ma.hosp_code = c.hosp_code and ma.hosp_code = v.hosp_code and ma.hosp_code = d.hosp_code and ma.visit_id = b.visit_id AND b.id = c.opd_id AND d.id = c.dept_id AND ma.hosp_code = #{hospCode} AND ma.type_code = #{typeCode} AND ma.is_inpt = '0'
and c.exec_dept_id = #{execDeptId}
and c.source_dept_id = #{sourceDeptId}
and ma.medic_type = #{technologyCode}
and c.crte_time >= #{startTime}
and c.crte_time <= #{endTime}
and v.name like concat('%', #{keyword}, '%' )
and (c.is_technology_ok = #{isOk} or c.is_technology_ok is null) and c.status_code = '0'
/*查询已退费状态的医技*/ and c.status_code = '1'
and c.is_technology_ok = #{isOk} and c.status_code = '0'

select (a.price*a.num) as sumPrice,a.*,ib.name as babyName,ib.gender_code as babyGenderCode from ( SELECT c.id AS costId, c.item_id AS itemId, c.item_name AS itemName, c.price, c.num, v.NAME, v.age, v.age_unit_code, v.bed_name, v.in_no, d.NAME AS sourceDeptName, c.crte_time, v.gender_code, c.baby_id, c.hosp_code, CASE WHEN c.is_ok = '1' THEN '确费' WHEN c.is_ok = '0' THEN '未确费' ELSE '未确费' END AS isOk FROM inpt_cost c left join inpt_advice b on c.visit_id = b.visit_id and c.iat_id = b.id and c.hosp_code = b.hosp_code left join inpt_visit v on c.visit_id = v.id and c.hosp_code = v.hosp_code left join base_dept d on d.id = c.dept_id and c.hosp_code = d.hosp_code
WHERE c.hosp_code = #{hospCode} AND b.type_code = '19' AND c.status_code = '0'
and c.exec_dept_id = #{execDeptId}
and c.source_dept_id = #{sourceDeptId}
and c.crte_time >= #{startTime}
and c.crte_time <= #{endTime}
and v.name like concat('%', #{keyword}, '%' )
and c.is_ok = #{isOk}
) a left join inpt_baby ib on a.baby_id = ib.id and a.hosp_code = ib.hosp_code
