mapper.select:
select (a.price*a.num) as sumPrice,a.*,ib.name as babyName,ib.gender_code as babyGenderCode from ( SELECT c.id AS costId, c.item_id AS itemId, c.item_name AS itemName, c.price, c.num, v.NAME, v.age, v.age_unit_code, v.bed_name, v.in_no, d.NAME AS sourceDeptName, c.crte_time, v.gender_code, c.baby_id, c.hosp_code, CASE WHEN c.is_ok = '1' THEN '确费' WHEN c.is_ok = '0' THEN '未确费' ELSE '未确费' END AS isOk FROM medic_apply ma, inpt_advice b, inpt_cost c, inpt_visit v, base_dept d WHERE ma.apply_no = b.technology_no AND ma.visit_id = v.id and ma.hosp_code = c.hosp_code and ma.hosp_code = v.hosp_code and ma.hosp_code = d.hosp_code and ma.visit_id = b.visit_id AND b.id = c.iat_id AND d.id = c.dept_id AND ma.hosp_code = #{hospCode} AND ma.type_code = #{typeCode} AND c.status_code = '0'
and c.exec_dept_id = #{execDeptId}
and c.source_dept_id = #{sourceDeptId}
and ma.medic_type = #{technologyCode}
and c.crte_time >= #{startTime}
and c.crte_time <= #{endTime}
and v.name like concat('%', #{keyword}, '%' )
and c.is_ok = #{isOk}
) a left join inpt_baby ib on a.baby_id = ib.id and a.hosp_code = ib.hosp_code

SELECT c.id AS costId, c.item_id AS itemId, c.item_name AS itemName, c.price, c.num, (c.price*c.num) as sumPrice, v.NAME, v.age, v.age_unit_code, v.visit_no as inNo, d.NAME AS sourceDeptName, c.crte_time, v.gender_code, c.hosp_code, C.technology_ok_name AS technologyOkName, C.technology_ok_time AS technologyOkTime, CASE WHEN c.is_technology_ok = '1' THEN '确费' WHEN c.is_technology_ok = '0' THEN '未确费' ELSE '未确费' END AS isOk FROM medic_apply ma, outpt_prescribe_detail b, outpt_cost c, outpt_visit v, base_dept d WHERE ma.opd_id = B.id AND ma.visit_id = v.id and ma.hosp_code = c.hosp_code and ma.hosp_code = v.hosp_code and ma.hosp_code = d.hosp_code and ma.visit_id = b.visit_id AND b.id = c.opd_id AND d.id = c.dept_id AND ma.hosp_code = #{hospCode} AND ma.type_code = #{typeCode} AND ma.is_inpt = '0' AND c.status_code = '0'
and c.exec_dept_id = #{execDeptId}
and c.source_dept_id = #{sourceDeptId}
and ma.medic_type = #{technologyCode}
and c.crte_time >= #{startTime}
and c.crte_time <= #{endTime}
and v.name like concat('%', #{keyword}, '%' )
and (c.is_technology_ok IS NULL OR c.is_technology_ok = #{isOk})
and c.is_technology_ok = #{isOk}
