update.foreach:
update inpt_nurse_record
set is_valid = '0'
where hosp_code = #{item.hospCode}
  and visit_id = #{item.visitId}
  and group_no = #{item.groupNo}

    and baby_id = #{item.babyId}

mapper.select:
select count(1)
from base_nurse_order
where hosp_code = #{hospCode}
  and id = #{bnoId}
  and is_valid = '1'

SELECT a.id, a.bed_id, a.bed_name, a.name, a.gender_code, a.age, a.age_unit_code, a.status_code, a.in_no, a.in_dept_id, a.in_dept_name, a.in_time, a.in_disease_id, a.in_disease_name, a.out_time, a.total_advance, a.total_cost, a.total_balance, a.zz_doctor_name, a.outpt_visit_no, a.in_disease_icd10, a.in_ward_id, a.Illness_code, a.outpt_visit_no FROM inpt_visit a LEFT JOIN base_bed b on a.hosp_code = b.hosp_code and a.bed_id = b.id WHERE a.hosp_code = #{hospCode}
and a.status_code = #{statusCode}
and a.in_dept_id = #{inDeptId}
and (a.name like CONCAT('%',#{keyword},'%') or a.bed_name like CONCAT('%',#{keyword},'%') or a.in_no like CONCAT('%', #{keyword}, '%') or a.in_dept_name like CONCAT('%', #{keyword}, '%'))
and a.in_time >= STR_TO_DATE(#{startTime},'%Y-%m-%d')
and a.in_time <= DATE_ADD(STR_TO_DATE(#{endTime},'%Y-%m-%d'),INTERVAL 1 day)
order by b.seq_no asc

SELECT iv.id, iv.cert_no, iv.bed_id, iv.bed_name, iv.name, iv.gender_code, iv.age, iv.age_unit_code, iv.status_code, iv.in_no, iv.in_dept_id, iv.in_dept_name, iv.in_time, iv.in_disease_id, iv.in_disease_name, iv.out_time, iv.total_advance, iv.total_cost, iv.total_balance, iv.zz_doctor_name, iv.outpt_visit_no, iv.in_disease_icd10, iv.in_ward_id, iv.Illness_code, iv.outpt_doctor_name, iap.is_settle, iap.pay_code, iap.ap_order_no, iap.price, iap.crte_time, iap.crte_name FROM inpt_visit iv, inpt_advance_pay iap WHERE iap.visit_id=iv.id and iv.hosp_code = #{hospCode}
and iv.status_code = #{statusCode}
and iv.in_dept_id = #{inDeptId}
and (name like CONCAT('%',#{keyword},'%') or iv.bed_name like CONCAT('%',#{keyword},'%') or iv.in_no like CONCAT('%', #{keyword}, '%') or iv.in_dept_name like CONCAT('%', #{keyword}, '%'))
and DATE_FORMAT(iv.in_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%s')
and DATE_FORMAT(iv.in_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%s')
and DATE_FORMAT(iap.crte_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%s')
and DATE_FORMAT(iap.crte_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%s')
and iap.is_settle =#{isSettle}
and iap.pay_code =#{payCode}
order by iap.crte_time desc,iv.name asc

select
    value
from sys_parameter
where hosp_code = #{hospCode}
and is_valid = '1'
and code = #{code}

