and ( iv.bed_name like concat('%',#{keyword},'%') or iv.name like concat('%',#{keyword},'%') )
/*开始日期*/
and DATE_FORMAT(iv.in_time,'%Y-%m-%d') >=#{startTime}
/*结束日期*/
and DATE_FORMAT(iv.in_time,'%Y-%m-%d') <=#{endTime}
and iv.name like concat('%',#{name},'%')
and iv.insure_org_code = #{insureOrgCode}
and iv.in_dept_name = #{inDeptName}
and iv.id in
#{item}
order by iv.crte_time desc

select ia.id, ia.hosp_code, ia.visit_id, ia.group_no,ia.group_seq_no, ia.type_code, ia.end_exec_num, ia.item_code, ia.use_code, ia.use_days, ia.total_num, ia.total_num_unit_code, ia.item_name, ia.content, ia.spec, ia.prep_code,ia.dosage, ia.dosage_unit_code, ia.usage_code, ia.speed_code, ia.num, ia.unit_code, ia.price, ia.technology_no,ia.long_start_time,ia.is_check, ia.check_name,ia.check_time,ia.is_stop_check, ia.stop_doctor_name, ia.stop_time, ia.stop_check_name, ia.stop_check_time, ia.is_long, ia.is_stop,ia.crte_id, ia.crte_name,ia.crte_time,ia.remark, if(ia.is_long='0','长嘱','临时') as adviceType, br.name as rateName,br.daily_times, iv.gender_code,iv.age_unit_code, iv.name ,iv.age,iv.in_no as inNo,iv.in_profile,iv.in_dept_name,iv.bed_id,iv.bed_name as bedName,iv.nursing_code,iv.Illness_code as illnessCode,iv.diet_type as dietType, ba.container_code as containerCode , ba.specimen_code as specimenCode, MIN(iae.is_print) as isPrint, MIN(DATE_FORMAT( iae.plan_exec_time, '%Y-%m-%d' )) as execPlaneTime, MIN(iae.plan_exec_time) as execPlaneTime1, MIN(iae.id) as inptAdviceExecId, bd.seq_no as bedSeqNo, CONCAT( iv.NAME,'/', (CASE iv.gender_code WHEN '0' THEN '未知的性别' WHEN '1' THEN '男' WHEN '2' THEN '女' WHEN '9' THEN '未说明的性别' ELSE '' END ), '/',IFNULL( iv.age, '-' ), (CASE iv.age_unit_code WHEN '1' THEN '岁' WHEN '2' THEN '月' WHEN '3' THEN '周' WHEN '4' THEN '天' ELSE '' END ), if(ia.baby_id is null or ia.baby_id = '', '', concat('/', (select ib.name from inpt_baby ib where ib.id = ia.baby_id and ib.hosp_code = ia.hosp_code))) ) AS patientInfo from inpt_advice ia inner join inpt_visit iv on ia.hosp_code = iv.hosp_code and ia.visit_id = iv.id and iv.in_dept_id = #{inDeptId} and iv.hosp_code = ia.hosp_code and iv.status_code = '2' left join base_bed bd on iv.bed_id = bd.id and iv.hosp_code = bd.hosp_code and bd.hosp_code = #{hospCode} left join base_rate br on ia.rate_id= br.id and ia.hosp_code= br.hosp_code left join base_advice ba on ia.item_id = ba.id and ia.hosp_code = ba.hosp_code join inpt_advice_exec iae on iae.hosp_code = ia.hosp_code and iae.advice_id =ia.id and iae.visit_id = ia.visit_id
left join phar_in_wait_receive piwr on piwr.hosp_code = ia.hosp_code and piwr.visit_id = ia.visit_id and piwr.advice_id = ia.id
where ia.hosp_code=#{hospCode}
and (ia.use_code in ('2','3') or ( ia.item_code = '1' and piwr.status_code > '0' and ia.use_code ='1') or (ia.item_code != '1'))
and ia.use_code != '4'
and ia.is_check='1'
and (iv.name like concat('%',#{keyword},'%') or iv.in_no like concat('%',#{keyword},'%') or iv.bed_name like concat('%',#{keyword},'%') or iv.cert_no like concat('%',#{keyword},'%'))
and date_format(iae.plan_exec_time, '%Y-%m-%d') = date_format(#{queryDate}, '%Y-%m-%d')
and (!JSON_CONTAINS(iae.is_print, JSON_OBJECT(#{printType}, '1')) or iae.is_print = #{printFlag})
and (JSON_CONTAINS(iae.is_print, JSON_OBJECT(#{printType}, #{printFlag})))
and iae.is_print = #{printFlag}
and ia.is_stop = #{isStop}
and ia.visit_id in
#{item}
and find_in_set(ia.visit_id, #{visitIds})
and ia.usage_code in
#{item}
and ia.type_code in
#{item}
and ba.biz_code is not null and ba.biz_code != '' and ia.is_stop = '0'
and ia.is_long=#{adviceType}
and ia.type_code=#{typeCode}
GROUP BY ia.id order by bd.seq_no, MIN(iae.plan_exec_time) asc, ia.group_no asc,ia.group_seq_no asc

