and ia.use_code != '4'
and ia.is_check='1'
and (iv.name like concat('%',#{keyword},'%') or iv.in_no like concat('%',#{keyword},'%') or iv.bed_name like concat('%',#{keyword},'%') or iv.cert_no like concat('%',#{keyword},'%'))
and date_format(iae.plan_exec_time, '%Y-%m-%d') = date_format(#{queryDate}, '%Y-%m-%d')
and (!JSON_CONTAINS(iae.is_print, JSON_OBJECT(#{printType}, '1')) or iae.is_print = #{printFlag})
