mapper.update:
update inpt_visit
set preferential_type_id = #{preferentialTypeId},
    total_cost = #{totalcost},
    total_balance = #{totalBalance}
where hosp_code = #{hospCode} and id = #{visitId}

update.foreach:
update inpt_cost
set preferential_price = #{item.preferentialPrice},
    reality_price = #{item.realityPrice}
where hosp_code =  #{item.hospCode} and id = #{item.id}

mapper.select:
select id, hosp_code, visit_id, baby_id, order_no, iat_id, iatd_group_no, iatd_group_seq_no, iatd_id, in_dept_id, exec_dept_id, dept_id, group_no, group_seq_no, type_code, sign_code, start_exec_num, end_exec_num, bfc_id, item_code, item_id, item_name, content, spec, prep_code, dosage, dosage_unit_code, usage_code, rate_id, speed_code, num, unit_code, price, total_price, total_num, total_num_unit_code, herb_num, use_days, use_code, is_skin, is_positive, herb_note_code, phar_id, remark, plan_stop_time, technology_no, herb_use_code, is_give, exec_id, exec_name, second_exec_id, second_exec_name, long_start_time, last_exec_time, teach_doctor_id, teach_doctor_name, is_check, check_id, check_name, check_time, is_stop_check, stop_doctor_id, stop_doctor_name, stop_time, stop_check_id, stop_check_name, stop_check_time, is_long, is_stop, is_reject, reject_remark, crte_id, crte_name, crte_time from inpt_advice where hosp_code = #{hospCode} and visit_id = #{visitId}
and content like concat('%',#{keyword},'%')
and dept_id = #{deptId}
and baby_id =#{babyId}
and (baby_id is null or baby_id = '')

select a.*,b.preferential_price,b.reality_price,(b.price * b.num) as valuationPrice, (case a.item_code when '1' then bd.nation_code when '2' then bm.nation_code when '3' then bi.nation_code end ) as nationCode, (case a.item_code when '1' then bd.nation_name when '2' then bm.nation_name when '3' then bi.nation_name end ) as nationName from inpt_advice_detail a left join inpt_cost b on b.source_id = a.id and b.hosp_code = a.hosp_code and b.visit_id = #{visitId} left join base_drug bd on a.item_code = '1'and bd.id = a.item_id and bd.hosp_code = a.hosp_code left join base_material bm on a.item_code = '2' and bm.id = a.item_id and bm.hosp_code = a.hosp_code left join base_item bi on a.item_code = '3' and bi.id = a.item_id and bi.hosp_code = a.hosp_code where a.hosp_code = #{hospCode} and a.ia_id = #{iaId} and a.visit_id = #{visitId}
and b.baby_id =#{babyId}
and (b.baby_id is null or b.baby_id = '')

select drug.good_name as name, drug.code as code, drug.spec as spec, drug.nation_code as nationCode, drug.nation_name as nationName, cost.numUnitCode as unitCode, ( select name from base_finance_classify where id = cost.bfcId and hosp_code = #{hospCode} ) as bfcName, cost.price, cost.num, cost.realityPrice, cost.valuationPrice, cost.preferentialPrice, cost.crteName, ( select name from base_dept where id = cost.sourceDeptId and hosp_code = #{hospCode} ) as sourceDeptName, cost.sourceDeptId, cost.itemCode, cost.itemId from base_drug drug inner join ( select max(inpt_cost.source_dept_id) as sourceDeptId, max(inpt_cost.visit_id) as visitId, max(inpt_cost.bfc_id) as bfcId, inpt_cost.item_id as itemId, inpt_cost.hosp_code, max(inpt_cost.price) as price, max(inpt_cost.crte_name) as crteName, sum(inpt_cost.total_num ) as num, max(inpt_cost.total_num_unit_code) as numUnitCode, sum(inpt_cost.total_price ) as valuationPrice, sum(inpt_cost.preferential_price ) as preferentialPrice, sum(inpt_cost.reality_price ) as realityPrice, max(inpt_cost.item_code) as itemCode from inpt_cost where hosp_code = #{hospCode} and item_code = '1' and visit_id = #{visitId}
and source_dept_id = #{sourceDeptId}
and (iat_id is not null or iat_id != '')
and (iat_id is null or iat_id = '')
and baby_id =#{babyId}
and (baby_id is null or baby_id = '')
AND attribution_code = #{attributionCode}
AND (attribution_code = '0' or attribution_code is null or attribution_code = '')
group by itemId ) cost on drug.id = cost.itemId and drug.hosp_code = cost.hosp_code where drug.hosp_code = #{hospCode}
and drug.good_name like concat('%',#{keyword},'%')
union select item.name as name, item.code, item.spec, item.nation_code as nationCode, item.nation_name as nationName, cost.numUnitCode as unitCode, ( select name from base_finance_classify where id = cost.bfcId and hosp_code = #{hospCode} ) as bfcName, cost.price, cost.num, cost.realityPrice, cost.valuationPrice, cost.preferentialPrice, cost.crteName, ( select name from base_dept where id = cost.sourceDeptId and hosp_code = #{hospCode} ) as sourceDeptName, cost.sourceDeptId, cost.itemCode, cost.itemId from base_item item inner join ( select max(inpt_cost.source_dept_id) as sourceDeptId, max(inpt_cost.visit_id) as visitId, max(inpt_cost.bfc_id) as bfcId, inpt_cost.item_id as itemId, inpt_cost.hosp_code, max(inpt_cost.price) as price, max(inpt_cost.crte_name) as crteName, sum(inpt_cost.total_num ) as num, max(inpt_cost.total_num_unit_code) as numUnitCode, sum(inpt_cost.total_price ) as valuationPrice, sum(inpt_cost.preferential_price ) as preferentialPrice, sum(inpt_cost.reality_price ) as realityPrice, max(inpt_cost.item_code) as itemCode from inpt_cost where hosp_code = #{hospCode} and item_code = '3' and visit_id = #{visitId}
and source_dept_id = #{sourceDeptId}
and (iat_id is not null or iat_id != '')
and (iat_id is null or iat_id = '')
and baby_id =#{babyId}
and (baby_id is null or baby_id = '')
AND attribution_code = #{attributionCode}
AND (attribution_code = '0' or attribution_code is null or attribution_code = '')
group by itemId ) cost on item.id = cost.itemId and item.hosp_code = cost.hosp_code where item.hosp_code = #{hospCode}
and item.name like concat('%',#{keyword},'%')
union select material.name as name, material.code, material.spec, material.nation_code as nationCode, material.nation_name as nationName, cost.numUnitCode as unitCode, ( select name from base_finance_classify where id = cost.bfcId and hosp_code = #{hospCode} ) as bfcName, cost.price, cost.num, cost.realityPrice, cost.valuationPrice, cost.preferentialPrice, cost.crteName, ( select name from base_dept where id = cost.sourceDeptId and hosp_code = #{hospCode} ) as sourceDeptName, cost.sourceDeptId, cost.itemCode, cost.itemId from base_material material inner join ( select max(inpt_cost.source_dept_id) as sourceDeptId, max(inpt_cost.visit_id) as visitId, max(inpt_cost.bfc_id) as bfcId, inpt_cost.item_id as itemId, inpt_cost.hosp_code, max(inpt_cost.price) as price, max(inpt_cost.crte_name) as crteName, sum(inpt_cost.total_num ) as num, max(inpt_cost.total_num_unit_code) as numUnitCode, sum(inpt_cost.total_price ) as valuationPrice, sum(inpt_cost.preferential_price ) as preferentialPrice, sum(inpt_cost.reality_price ) as realityPrice, max(inpt_cost.item_code) as itemCode FROM inpt_cost where hosp_code = #{hospCode} and item_code = '2' and visit_id = #{visitId}
and source_dept_id = #{sourceDeptId}
and (iat_id is not null or iat_id != '')
and (iat_id is null or iat_id = '')
and baby_id =#{babyId}
and (baby_id is null or baby_id = '')
AND attribution_code = #{attributionCode}
AND (attribution_code = '0' or attribution_code is null or attribution_code = '')
group by itemId ) cost on material.id = cost.itemId and material.hosp_code = cost.hosp_code where material.hosp_code = #{hospCode}
and material.name like concat('%',#{keyword},'%')
order by bfcName

select a.id, a.source_dept_id as sourceDeptId, a.visit_id as visitId, a.bfc_id as bfcId, a.item_id as itemId, a.item_name as name, a.cost_time as costTime, a.hosp_code, a.price as price, a.total_num_unit_code as numUnitCode, a.crte_name as crteName, a.total_num as num, a.total_price as valuationPrice, a.preferential_price as preferentialPrice, a.reality_price as realityPrice, ( select name from base_dept where id = a.source_dept_id and hosp_code = #{hospCode} ) as sourceDeptName, a.item_code as itemCode, (case a.item_code when '1' then bd.nation_code when '2' then bm.nation_code when '3' then bi.nation_code end ) as nationCode, (case a.item_code when '1' then bd.nation_name when '2' then bm.nation_name when '3' then bi.nation_name end ) as nationName from inpt_cost a left join base_drug bd on a.item_code = '1'and bd.id = a.item_id and bd.hosp_code = a.hosp_code left join base_material bm on a.item_code = '2' and bm.id = a.item_id and bm.hosp_code = a.hosp_code left join base_item bi on a.item_code = '3' and bi.id = a.item_id and bi.hosp_code = a.hosp_code where a.hosp_code = #{hospCode} and item_code = #{itemCode} and visit_id = #{visitId} and item_id = #{itemId}
and source_dept_id = #{sourceDeptId}
and (iat_id is not null or iat_id != '')
and (iat_id is null or iat_id = '')
order by a.cost_time asc

select bfc.name as bfcName, cost.bfc_id as bfcId, sum(cost.total_price) as valuationPrice , sum(cost.preferential_price) as preferentialPrice, sum(cost.reality_price) as realityPrice from inpt_cost cost left join base_finance_classify bfc on cost.bfc_id = bfc.id and bfc.hosp_code = cost.hosp_code where cost.hosp_code = #{hospCode} and cost.visit_id = #{visitId}
and cost.baby_id =#{babyId}
and (cost.baby_id is null or cost.baby_id = '')
group by bfcName,bfcId

select bfc.name as bfcName, (case cost.item_code when '1' then bd.spec when '2' then bm.spec when '3' then bi.spec end ) as spec, (case cost.item_code when '1' then bd.code when '2' then bm.code when '3' then bi.code end ) as code, (case cost.item_code when '1' then bd.nation_code when '2' then bm.nation_code when '3' then bi.nation_code end ) as nationCode, (case cost.item_code when '1' then bd.nation_name when '2' then bm.nation_name when '3' then bi.nation_name end ) as nationName, cost.total_num_unit_code as unitCode, cost.item_name as name, cost.total_num as num, cost.price, ( cost.total_price ) as valuationPrice, cost.preferential_price as preferentialPrice, cost.reality_price as realityPrice, dept.name as sourceDeptName, cost.status_code as statusCode, cost.cost_time as costTime from inpt_cost cost left join base_finance_classify bfc on cost.bfc_id = bfc.id and bfc.hosp_code = cost.hosp_code left join base_dept dept on cost.source_dept_id = dept.id and bfc.hosp_code = cost.hosp_code left join base_drug bd on cost.item_code = '1'and bd.id = cost.item_id and bd.hosp_code = cost.hosp_code left join base_material bm on cost.item_code = '2' and bm.id = cost.item_id and bm.hosp_code = cost.hosp_code left join base_item bi on cost.item_code = '3' and bi.id = cost.item_id and bi.hosp_code = cost.hosp_code where cost.hosp_code =#{hospCode,jdbcType=VARCHAR} and cost.visit_id = #{visitId} and cost.bfc_id = #{bfcId,jdbcType=VARCHAR}
and cost.item_name like "%"#{keyword,jdbcType=VARCHAR}"%"
and cost.source_dept_id =#{sourceDeptId,jdbcType=VARCHAR}
and cost.baby_id =#{babyId}
and (cost.baby_id is null or cost.baby_id = '')

select DATE_FORMAT(cost.cost_time, '%Y-%m-%d') as costTime, bfc.name as bfcName, bfc_id as bfcId, sum(cost.total_price) as valuationPrice, sum( cost.preferential_price ) as preferentialPrice, sum( cost.reality_price ) as realityPrice from inpt_cost cost, base_finance_classify bfc where cost.bfc_id = bfc.id and cost_time is not null and cost_time != '' and cost.visit_id = #{visitId}
and cost.baby_id =#{babyId}
and (cost.baby_id is null or cost.baby_id = '')
group by bfc.name, cost.bfc_id, DATE_FORMAT(cost.cost_time, '%Y-%m-%d') order by DATE_FORMAT(cost.cost_time, '%Y-%m-%d')

select bfc.name as bfcName, base_dept.name as sourceDeptName, inpt_cost.item_name as name, inpt_cost.total_num as num, inpt_cost.total_num_unit_code as numUnitCode, inpt_cost.price, (inpt_cost.total_price) as valuationPrice, inpt_cost.preferential_price as preferentialPrice, inpt_cost.reality_price as realityPrice, (case inpt_cost.item_code when '1' then bd.nation_code when '2' then bm.nation_code when '3' then bi.nation_code end ) as nationCode, (case inpt_cost.item_code when '1' then bd.nation_name when '2' then bm.nation_name when '3' then bi.nation_name end ) as nationName from inpt_cost left join base_finance_classify bfc ON inpt_cost.bfc_id = bfc.id left join base_dept ON base_dept.id = inpt_cost.source_dept_id left join base_drug bd on inpt_cost.item_code = '1'and bd.id = inpt_cost.item_id and bd.hosp_code = inpt_cost.hosp_code left join base_material bm on inpt_cost.item_code = '2' and bm.id = inpt_cost.item_id and bm.hosp_code = inpt_cost.hosp_code left join base_item bi on inpt_cost.item_code = '3' and bi.id = inpt_cost.item_id and bi.hosp_code = inpt_cost.hosp_code
inpt_cost.bfc_id = #{bfcId} and inpt_cost.cost_time like concat(DATE_FORMAT(#{costTime}, '%Y-%m-%d'),'%') and inpt_cost.visit_id = #{visitId}
and inpt_cost.baby_id =#{babyId}
and (inpt_cost.baby_id is null or inpt_cost.baby_id = '')

SELECT a.settle_no, e.name, e.age, e.age_unit_code, e.gender_code, ( case when a.baby_id is null or LENGTH(trim(a.baby_id)) < 1 then (CONCAT( e.name,'/', (case when e.gender_code = '1' then '男' when e.gender_code = '2' then '女' else '-' end) , '/' , e.age , (case when e.age_unit_code = '1' then '岁' when e.age_unit_code = '2' then '月' when e.age_unit_code = '3' then '周' when e.age_unit_code = '4' then '天' else '-' end ))) else ( CONCAT( e.name,'/', (case when e.gender_code = '1' then '男' when e.gender_code = '2' then '女' else '-' end) , '/' , e.age , (case when e.age_unit_code = '1' then '岁' when e.age_unit_code = '2' then '月' when e.age_unit_code = '3' then '周' when e.age_unit_code = '4' then '天' else '-' end ), '/' , t1.name )) end ) as patient_info, a.type_code, a.patient_code, a.start_time, a.end_time, a.total_price, a.reality_price, a.out_settle_code, a.actual_price, a.trunc_price, (select ifnull(sum(pp.price), 0) from inpt_pay pp where pp.settle_id = a.id ) as self_price, (select ib.name from inpt_baby ib where ib.hosp_code = a.hosp_code and ib.id = a.baby_id) as babyName, a.ap_total_price, a.ap_offset_price, a.settle_take_price, a.settle_back_price, a.acct_pay as acctPay, a.settle_time, a.is_settle, a.status_code, a.is_print, (a.mi_price-a.acct_pay) as miPrice, a.crte_id, a.crte_name, a.crte_time, a.baby_id, d.total_price as insurePayTotalPrice, b.total_price as invoiceTotalPrice, c.reality_price as preferentialPrice, a.source_pay_code, b.invoice_no, f.hosp_price hospPrice, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '1' ) xjzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '2' ) skzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '3' ) zpzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '4' ) zzzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '5' ) wxzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '6' ) zfbzf, e.outpt_doctor_name,-- 门诊医生 e.jz_doctor_name,-- 经治医生 e.zg_doctor_name,-- 主管医生 e.out_time as outTime, e.cert_no as certNo, ifnull(f.hosp_exem_amount, 0.00) as hospExemAmount, ifnull(a.credit_price, 0) as creditPrice FROM inpt_settle a left join inpt_settle_invoice b on a.id = b.settle_id and a.hosp_code = b.hosp_code left join inpt_settle_invoice_content c on c.settle_invoice_id = b.id and a.hosp_code = c.hosp_code INNER JOIN inpt_visit e ON a.visit_id = e.id and a.hosp_code = e.hosp_code left join inpt_insure_pay d on d.settle_id = a.id and a.hosp_code = d.hosp_code left join insure_individual_settle f on a.id = f.settle_id and a.hosp_code = f.hosp_code and a.status_code = f.state left join inpt_baby t1 on a.baby_id = t1.id and a.hosp_code = t1.hosp_code where a.hosp_code = #{hospCode} and a.is_settle = '1'
and a.visit_id = #{visitId}
and (e.name like concat('%',#{keyword},'%') or b.invoice_no like concat('%',#{keyword},'%') or a.settle_no like concat('%',#{keyword},'%'))
and DATE_FORMAT(a.crte_time,'%Y-%m-%d') >= DATE_FORMAT(DATE_ADD(#{startTimeYMD},INTERVAL 1 DAY),'%Y-%m-%d')
and DATE_FORMAT(a.crte_time,'%Y-%m-%d') <= DATE_FORMAT(DATE_ADD(#{endTimeYMD},INTERVAL 1 DAY),'%Y-%m-%d')
and a.crte_id = #{crteId}
and a.patient_code = #{patientCode}
and a.baby_id =#{babyId}
and f.aka130=#{ywlx}
and (a.baby_id is null or a.baby_id = '')
order by a.settle_no desc, a.crte_time desc

SELECT e.id as visitId, a.settle_no, e.name, e.age, e.age_unit_code, e.gender_code, a.type_code, a.patient_code, a.start_time, a.end_time, a.total_price, a.reality_price, a.out_settle_code, a.actual_price, a.trunc_price, (select ifnull(sum(pp.price), 0) from inpt_pay pp where pp.settle_id = a.id ) as self_price, a.ap_total_price, a.ap_offset_price, a.settle_take_price, a.settle_back_price, a.settle_time, a.is_settle, a.status_code, a.is_print, a.mi_price, a.crte_id, a.crte_name, a.crte_time, d.total_price as insurePayTotalPrice, b.total_price as invoiceTotalPrice, c.reality_price as preferentialPrice, a.source_pay_code, b.invoice_no, f.hosp_price hospPrice, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '1' ) xjzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '2' ) skzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '3' ) zpzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '4' ) zzzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '5' ) wxzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '6' ) zfbzf FROM inpt_visit e left join inpt_settle a ON a.visit_id = e.id and a.hosp_code = e.hosp_code left join inpt_settle_invoice b on a.id = b.settle_id and a.hosp_code = b.hosp_code left join inpt_settle_invoice_content c on c.settle_invoice_id = b.id and a.hosp_code = c.hosp_code left join inpt_insure_pay d on d.settle_id = a.id and a.hosp_code = d.hosp_code left join insure_individual_settle f on a.id = f.settle_id and a.hosp_code = f.hosp_code and a.status_code = f.state where e.hosp_code = #{hospCode}
and e.id = #{visitId}
and e.name like concat('%',#{keyword},'%') or b.invoice_no like concat('%',#{keyword},'%') or a.settle_no like concat('%',#{keyword},'%')
and DATE_FORMAT(a.crte_time,'%Y-%m-%d') >= DATE_FORMAT(DATE_ADD(#{startTimeYMD},INTERVAL 1 DAY),'%Y-%m-%d')
and DATE_FORMAT(a.crte_time,'%Y-%m-%d') <= DATE_FORMAT(DATE_ADD(#{endTimeYMD},INTERVAL 1 DAY),'%Y-%m-%d')
and a.crte_id = #{crteId}
and e.patient_code = #{patientCode}
order by e.crte_time desc

SELECT e.id as visitId, a.settle_no, e.name, e.age, e.age_unit_code, e.gender_code, e.status_code brzt_Status_Code, -- 病人状态 a.type_code, a.patient_code, a.start_time, a.end_time, a.total_price, a.reality_price, a.out_settle_code, a.actual_price, a.trunc_price, (select ifnull(sum(pp.price), 0) from inpt_pay pp where pp.settle_id = a.id ) as self_price, a.ap_total_price, a.ap_offset_price, a.settle_take_price, a.settle_back_price, a.settle_time, a.is_settle, a.status_code, a.is_print, a.mi_price, a.crte_id, a.crte_name, a.crte_time, d.total_price as insurePayTotalPrice, b.total_price as invoiceTotalPrice, c.reality_price as preferentialPrice, a.source_pay_code, b.invoice_no, f.hosp_price hospPrice, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '1' ) xjzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '2' ) skzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '3' ) zpzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '4' ) zzzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '5' ) wxzf, (select ifnull(sum(price),0) price from inpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '6' ) zfbzf FROM inpt_visit e left join inpt_settle a ON a.visit_id = e.id and a.hosp_code = e.hosp_code left join inpt_settle_invoice b on a.id = b.settle_id and a.hosp_code = b.hosp_code left join inpt_settle_invoice_content c on c.settle_invoice_id = b.id and a.hosp_code = c.hosp_code left join inpt_insure_pay d on d.settle_id = a.id and a.hosp_code = d.hosp_code left join insure_individual_settle f on a.id = f.settle_id and a.hosp_code = f.hosp_code and a.status_code = f.state where e.hosp_code = #{hospCode} and e.status_code in ('1','2','3','4','5','6')
and e.id = #{visitId}
and e.name like concat('%',#{keyword},'%') or b.invoice_no like concat('%',#{keyword},'%') or a.settle_no like concat('%',#{keyword},'%')
and DATE_FORMAT(a.crte_time,'%Y-%m-%d') >= DATE_FORMAT(DATE_ADD(#{startTimeYMD},INTERVAL 1 DAY),'%Y-%m-%d')
and DATE_FORMAT(a.crte_time,'%Y-%m-%d') <= DATE_FORMAT(DATE_ADD(#{endTimeYMD},INTERVAL 1 DAY),'%Y-%m-%d')
and a.crte_id = #{crteId}
and e.patient_code = #{patientCode}
order by e.crte_time desc

select visit_id, change_code, before_bed_name, after_bed_name, before_dept_name, after_dept_name, before_ward_name, after_ward_name, before_zz_doctor_name, after_zz_doctor_name, end_time, crte_name, crte_time from inpt_bed_change
hosp_code = #{hospCode}
and visit_id = #{visitId}

select b.visit_id, b.total_price, b.reality_price, b.trunc_price, b.actual_price, b.self_price, b.mi_price, b.hosp_jm_price, b.type_code, a.invoice_no, a.total_price as invoiceTotalPrice, a.print_time, a.print_num from inpt_settle_invoice a inner join inpt_settle b on b.id = a.settle_id and a.hosp_code = b.hosp_code
a.hosp_code = #{hospCode}
and a.visit_id = #{visitId}
and b.baby_id =#{babyId}
and (b.baby_id is null or b.baby_id = '')

select id, hosp_code, visit_id, baby_id, iat_id, iatd_group_no, iatd_seq_no, advice_exec_id, source_code, source_id, old_cost_id, source_dept_id, in_dept_id, bfc_id, item_code, (case when item_code ='1' then (select bd.code from base_drug bd where item_id = bd.id and bd.hosp_code =hosp_code ) when item_code='2' then (select bm.code from base_material bm where item_id = bm.id and bm.hosp_code = hosp_code ) when item_code = '4' then (select ba.code from base_advice ba where item_id = ba.id and ba.hosp_code = hosp_code) else (select bi.code from base_item bi where item_id = bi.id and bi.hosp_code = hosp_code) end ) as hospItemCode, (case when item_code ='1' then (select bd.nation_code from base_drug bd where item_id = bd.id and bd.hosp_code =hosp_code ) when item_code='2' then (select bm.nation_code from base_material bm where item_id = bm.id and bm.hosp_code = hosp_code ) when item_code = '4' then (select ba.union_nation_code from base_advice ba where item_id = ba.id and ba.hosp_code = hosp_code) else (select bi.nation_code from base_item bi where item_id = bi.id and bi.hosp_code = hosp_code) end ) as nationCode, (case when item_code ='1' then (select bd.nation_name from base_drug bd where item_id = bd.id and bd.hosp_code =hosp_code ) when item_code='2' then (select bm.nation_name from base_material bm where item_id = bm.id and bm.hosp_code = hosp_code ) when item_code = '4' then (select ba.union_nation_name from base_advice ba where item_id = ba.id and ba.hosp_code = hosp_code) else (select bi.nation_name from base_item bi where item_id = bi.id and bi.hosp_code = hosp_code) end ) as nationName, item_id, item_name, price, num, spec, prep_code, dosage, dosage_unit_code, usage_code, rate_id, speed_code, use_days, num_unit_code, total_num, total_num_unit_code, herb_note_code, use_code, herb_num, total_price, preferential_price, reality_price, back_num, back_code, doctor_id, doctor_name, dept_id, phar_id, is_dist, is_give, is_ok, ok_id, ok_name, ok_time, settle_code, settle_id, is_check, check_id, check_name, check_time, zz_doctor_id, zz_doctor_name, jz_doctor_id, jz_doctor_name, zg_doctor_id, zg_doctor_name, exec_id, exec_name, exec_time, exec_dept_id, plan_exec_time, status_code, is_cost, cost_time, crte_id, crte_name, crte_time from inpt_cost
hosp_code = #{hospCode} and visit_id = #{visitId}
and (iat_id is not null or iat_id != '')
and (iat_id is null or iat_id = '')
and source_dept_id = #{sourceDeptId}
and item_name like concat('%',#{keyword},'%')
and baby_id =#{babyId}
and (baby_id is null or baby_id = '')
order by cost_time desc

