mapper.select:
select   *
from drg_dip_result
where visit_id = #{visitId} and valid_flag = 1

    AND type = #{type}


    AND business_type = #{businessType}

mapper.update:
update drg_dip_result
set valid_flag = '0'
where visit_id = #{visitId} and type = #{type} and business_type = #{businessType}

