select count(*) from inpt_settle is2
where is2.is_settle ='1' and is2.status_code ='0' and CONVERT(patient_code,SIGNED) <= 0

    and DATE_FORMAT(is2.crte_time, "%Y-%m-%d") >= #{startDate}


