select * from (select m.* from (select iib.aac003 as psn_name, iib.aac004 as gend, iv.age, iv.in_profile as medcasno, iv.zg_doctor_id as doctor_id, iv.zg_doctor_name as doctor_name, if(isi.settle_no is null, '0', '1') as is_uplod , iis.crte_time, iis.visit_id, iis.insureSettleId , iis.settle_id, iis.is_hospital, iis.insure_reg_code, iis.medicalRegNo, iiv.visit_icd_code , iiv.visit_icd_name , iiv.visit_drpt_id as dept_id, iiv.visit_drpt_name as dept_name, iiv.visit_no, DATE_FORMAT(iis.crte_time, '%Y-%m-%d %H:%i:%S') as setl_time, iis.total_price as total_fee, ddr.id, ddr.stand_fee, ddr.profit, ddr.type, if(ddr.states is null, '0', ddr.states) as states, if(ddr.suspicious_num > 0, '1', '0') as suspicious_states, if(ddr.violation_num > 0, '1', '0') as violation_states, if(ddr.group_result is null, '0', ddr.group_result) as group_result from insure_individual_settle iis left join insure_individual_visit iiv on iis.visit_id = iiv.visit_id and iis.hosp_code = iiv.hosp_code and iis.medicalRegNo = iiv.medical_reg_no left join insure_individual_basic iib on iiv.mib_id = iib.id and iiv.hosp_code = iib.hosp_code left join insure_setl_info isi on iis.insureSettleId = isi.insure_settle_id and iis.hosp_code = isi.hosp_code and iis.visit_id = iiv.visit_id left join drg_dip_result ddr on ddr.visit_id = isi.visit_id and ddr.valid_flag ='1' and ddr.business_type ='1' and ddr.type=#{type} left join inpt_visit iv on iis.visit_id = iv.id where iis.state = '0' and iis.settle_state = '1' and iis.hosp_code = #{hospCode} and iis.insureSettleId is not null and iis.is_hospital = '1') m union all select n.* from (select iib.aac003 as psn_name, iib.aac004 as gend, ov.age, ov.out_profile as medcasno , ov.doctor_id as doctor_id, ov.doctor_name as doctor_name, if(isi.settle_no is null, '0', '1') as is_uplod , iis.crte_time, iis.visit_id, iis.insureSettleId , iis.settle_id, iis.is_hospital, iis.insure_reg_code, iis.medicalRegNo, iiv.visit_icd_code , iiv.visit_icd_name , iiv.visit_drpt_id as dept_id, iiv.visit_drpt_name as dept_name, iiv.visit_no, DATE_FORMAT(iis.crte_time, '%Y-%m-%d %H:%i:%S') as setl_time, iis.total_price as total_fee, ddr.id, ddr.stand_fee, ddr.profit, ddr.type, if(ddr.states is null, '0', ddr.states) as states, if(ddr.suspicious_num > 0, '1', '0') as suspicious_states, if(ddr.violation_num > 0, '1', '0') as violation_states, if(ddr.group_result is null, '0', ddr.group_result) as group_result from insure_individual_settle iis left join insure_individual_visit iiv on iis.visit_id = iiv.visit_id and iis.hosp_code = iiv.hosp_code and iis.medicalRegNo = iiv.medical_reg_no left join insure_individual_basic iib on iiv.mib_id = iib.id and iiv.hosp_code = iib.hosp_code left join insure_setl_info isi on iis.insureSettleId = isi.insure_settle_id and iis.hosp_code = isi.hosp_code and iis.visit_id = iiv.visit_id left join drg_dip_result ddr on ddr.visit_id = isi.visit_id and ddr.valid_flag ='1' and ddr.business_type ='1' and ddr.type=#{type} left join outpt_visit ov on iis.visit_id = ov.id where iis.state = '0' and iis.settle_state = '1' and iis.hosp_code = #{hospCode} and iis.insureSettleId is not null and iis.is_hospital = '0' and iiv.aka130 = '14') n) A
and A.is_hospital = #{isHospital}
and A.doctor_id = #{doctorId}
and A.dept_Id = #{deptId}
and A.is_uplod = #{isUplod}
and A.states = #{states}
and A.suspicious_states = #{suspiciousStates}
and A.violation_states = #{violationStates}
and A.group_result = #{groupResult}
and (A.violation_states = '1' or A.suspicious_states = '1')
and ( A.psn_name like concat('%',#{keyword},'%') or A.visit_no like concat('%',#{keyword},'%') or A.medcasno like concat('%',#{keyword},'%') )
and DATE_FORMAT(A.crte_time, '%Y') = #{year}
and DATE_FORMAT(A.crte_time, "%Y-%m-%d") >= #{startDate}
and DATE_FORMAT(A.crte_time, "%Y-%m-%d") <= #{endDate}
and DATE_FORMAT(A.crte_time, "%Y-%m") >= #{startYearMonth}
and DATE_FORMAT(A.crte_time, "%Y-%m") <= #{endYearMonth}
and A.dept_Id in
#{deptId}
and A.doctor_id in
#{doctorId}
and A.is_uplod in
#{isUplod}
and A.states in
#{states}
and A.group_result in
#{groupResult}
and A.is_hospital in
#{isHospital}

select * from (select
mbi.name as psn_name, mbi.gender_code as gend, iv.age, mbi.in_profile as medcasno, mbi.zz_doctor_id as doctor_id, mbi.zz_doctor_name as doctor_name,
mbi.is_upload_insure as is_uplod ,mbi.visit_id, mbi.disease_icd10 as visit_icd_code , mbi.disease_icd10_name as visit_icd_name ,
iv.in_dept_id as dept_id, iv.in_dept_name as dept_name,iv.in_no as visit_no,iv.status_code,
ie.settle_time, DATE_FORMAT(ie.settle_time, '%Y-%m-%d %H:%i:%S') as setl_time, ie.total_price as total_fee, ddr.id, ddr.stand_fee, ddr.profit,ddr.type,
if(ddr.states is null,'0',ddr.states) as states,
if(ddr.suspicious_num > 0,'1','0') as suspicious_states,
if(ddr.violation_num > 0,'1','0') as violation_states,
if(ddr.group_result is null,'0',ddr.group_result) as group_result
from mris_base_info mbi left join inpt_visit iv on mbi.visit_id =iv.id and iv.status_code = '7'
left join inpt_settle ie on ie.visit_id = mbi.visit_id and ie.is_settle = '1' and ie.status_code = '0'
left join drg_dip_result ddr on ddr.visit_id = mbi.visit_id  and ddr.valid_flag ='1' and ddr.business_type ='2'  and ddr.type=#{type})A


        and A.doctor_id = #{doctorId}


        and A.dept_Id = #{deptId}


        and A.is_uplod = #{isUplod}


        and A.states = #{states}


        and A.suspicious_states = #{suspiciousStates}


        and A.violation_states = #{violationStates}





        and A.group_result = #{groupResult}


        and (A.violation_states = '1' or A.suspicious_states = '1')


        and ( A.psn_name like concat('%',#{keyword},'%')
        or A.visit_no like concat('%',#{keyword},'%')
        or A.medcasno like concat('%',#{keyword},'%')
        )


        and DATE_FORMAT(A.settle_time, '%Y') = #{year}


        and DATE_FORMAT(A.settle_time, "%Y-%m-%d") >= #{startDate}


        and DATE_FORMAT(A.settle_time, "%Y-%m-%d") <= #{endDate}


        and DATE_FORMAT(A.settle_time, "%Y-%m") >= #{startYearMonth}


        and DATE_FORMAT(A.settle_time, "%Y-%m") <= #{endYearMonth}


        and A.dept_Id in

            #{deptId}



        and A.doctor_id in

            #{doctorId}



        and A.is_uplod in

            #{isUplod}



        and A.states in

            #{states}



        and A.group_result in

            #{groupResult}



        and A.status_code in

            #{statusCode}

