mapper.select:
select ddr.id, ddr.visit_id, ddr.dept_name , ddr.dept_id, ddr.doctor_id, ddr.doctor_name , ddrd .rulet_type , ddrd.check_filed, ddrd.result_msg , ddr.psn_name , ddr.visit_no , mbi.in_profile as medcasno , ddr.icd10 as inptDiagnose , ddr.total_fee , ddr.in_time , ddr.out_time, ddr.states, ddr.group_result, mbi.is_upload_insure from drg_dip_result_detail ddrd left join drg_dip_result ddr on ddrd.result_id = ddr.id left join mris_base_info mbi on ddr.visit_id = mbi.visit_id left join inpt_visit iv on mbi.visit_id =iv.id where ddr.business_type='2'
and ddr.`type` =#{type}
and ddrd.result_msg like concat('%',#{keyword},'%')
and year(ddrd.crt_time )=date_format(#{year},'yyyy')
and year(ddrd.crt_time ) between date_format(#{startYearMonth},'yyyy-mm') and date_format(#{endYearMonth},'yyyy-mm')
and ddr.dept_id in
#{deptId}
and ddr.doctor_id in
#{doctorId}
and mbi.is_upload_insure in
#{isUploadInsure}
and ddr.states=#{states}
and ddrd.rule_level in
#{level1}
and ddrd.rulet_type =#{ruletType}
and ddr.group_result in
#{groupResult}
and iv.status_code in
#{statusCode}

select ddr.id, ddr.visit_id, ddr.dept_name , ddr.dept_id, ddr.doctor_id, ddr.doctor_name , ddrd .rulet_type , ddrd.check_filed, ddrd.result_msg , ddr.psn_name , ddr.visit_no , ddr.icd10 as inptDiagnose , ddr.total_fee , ddr.in_time , ddr.out_time, ddr.states, ddr.group_result, isi.medcasno, DATE_FORMAT(iis.crte_time, '%Y-%m-%d %H:%i:%S') as setl_time from drg_dip_result_detail ddrd left join drg_dip_result ddr on ddrd.result_id = ddr.id left join insure_setl_info isi on ddr.visit_id = isi.visit_id left join insure_individual_settle iis on iis.insureSettleId = isi.insure_settle_id left join inpt_visit iv on isi.visit_id =iv.id where ddr.business_type='1'
and ddr.`type` =#{type}
and ddrd.result_msg like concat('%',#{keyword},'%')
and year(ddrd.crt_time )=date_format(#{year},'yyyy')
and year(ddrd.crt_time ) between date_format(#{startYearMonth},'yyyy-mm') and date_format(#{endYearMonth},'yyyy-mm')
and ddr.dept_id in
#{deptId}
and ddr.doctor_id in
#{doctorId}
and isi.settle_no is not null
and isi.settle_no is null
and ddr.states=#{states}
and ddrd.rule_level in
#{level1}
and ddrd.rulet_type =#{ruletType}
and ddr.group_result in
#{groupResult}
and iv.status_code in
#{statusCode}
