mapper.delete:
delete
from insure_record
where hosp_code = #{hospCode}
  and trt_dcla_detl_sn = #{trtDclaDetlSn}

delete
from insure_fix_record
where hosp_code = #{hospCode}
  and psn_no = #{psnNo}

delete
from insure_inpt_record
where hosp_code = #{hospCode}
  and trt_dcla_detl_sn = #{trtDclaDetlSn}

mapper.select:
select ov.name,ov.visit_id,ov.dept_name,ov.register_no,ov.cert_no, ir.id, ir.hosp_code, ir.visit_id, ir.psn_no, ( case ir.insutype when '310' then '职工基本医疗保险' when '320' then '公务员医疗补助' when '330' then '大额医疗费用补助' when '340' then '离休人员医疗保障' when '390' then '城乡居民基本医疗保险' when '392' then '城乡居民大病医疗保险' when '510' then '生育保险' end) as insutype, ir.opsp_dise_code, ir.opsp_dise_name, ir.tel, ir.addr, ir.insu_optins, ir.ide_fixmedins_no, ir.ide_fixmedins_name, ir.hosp_ide_date, ir.diag_dr_codg, ir.diag_dr_name,ir. begndate, ir.enddate, ir.trt_dcla_detl_sn, ir.memo from outpt_register ov left join insure_record ir on ov.visit_id = ir.visit_id and ov.hosp_code = ir.hosp_code where ov.hosp_code =#{hospCode}
and (ov.name like '%${keyword}%' or ov.register_no like '%${keyword}%' or ir.opsp_dise_code like '%${keyword}%' or ir.ide_fixmedins_name like '%${keyword}%')
/*开始日期*/
and date(ov.register_time) >= date(#{startDate})
/*结束日期*/
and date(ov.register_time) <= date(#{endDate})
order by ir.crte_time desc

select * from insure_fix_record where hosp_code =#{hospCode}

    and (name like '%${keyword}%')









order by crte_time desc

select iir.*, iiv.crte_time as regsiterTime
from insure_inpt_record iir
         join insure_individual_visit iiv on iir.visit_id = iiv.visit_id and iir.hosp_code = iiv.hosp_code
where iir.hosp_code = #{hospCode}

      and iir.visit_id = #{visitId}

  and iir.trt_dcla_detl_sn = #{trtDclaDetlSn}

select fix_srt_no
from insure_fix_record
where hosp_code = #{hospCode}
  and psn_no = #{psnNo}
order by crte_time desc
limit 1

select ov.name,ov.visit_id,ov.dept_name,ov.register_no,ov.cert_no, ir.id, ir.hosp_code, ir.visit_id, ir.psn_no, ir.reg_code, ir.hosp_opnn, ir.appyer_tel, ir.appyer_addr, ir.opsp_dise_code, ir.opsp_dise_name, ir.begndate, ir.enddate, ir.trtDclaDetlSn, ir.memo from outpt_register ov left join insure_outpt_twodise ir on ov.visit_id = ir.visit_id and ov.hosp_code = ir.hosp_code where ov.hosp_code =#{hospCode}
and (ov.name like '%${keyword}%' or ov.register_no like '%${keyword}%' or ir.opsp_dise_code like '%${keyword}%')
/*开始日期*/
and date(ov.register_time) >= date(#{startDate})
/*结束日期*/
and date(ov.register_time) <= date(#{endDate})
order by ir.crte_time desc

mapper.delete:
delete
from insure_outpt_twodise
where hosp_code = #{hospCode}
  and trtDclaDetlSn = #{trtDclaDetlSn}

delete
from insure_special_record
where serv_matt_inst_id = #{serv_matt_inst_id}
  and hosp_code = #{hospCode}

mapper.select:
select id, hosp_code, reg_code, psn_no, trtDclaDetlSn
from insure_outpt_twodise
where hosp_code = #{hospCode}


    and visit_id = #{visitId}

  and trtDclaDetlSn = #{trtDclaDetlSn}
