mapper.select:
select ifnull(fund_payamt, 0) as fund_payamt, fund_pay_type
from insure_individual_fund
where insure_settle_id = #{insureSettleId}

select disease_id, b.name, c.oper_disease_icd9, c.oper_disease_name
from (outpt_diagnose a, base_disease b)
         left join oper_info_record c on a.visit_id = c.visit_id and c.hosp_code = a.hosp_code
where a.visit_id = #{visitId}
  and a.disease_id = b.id
  and a.hosp_code = #{hospCode}
  and b.hosp_code = #{hospCode}

select A.* from ( select m.* from ( select id,'1'as lx,in_no as visitNo, name, gender_code, age, age_unit_code,hosp_code, nation_code, cert_no,cert_code, birthday, patient_code, crte_time from inpt_visit where patient_code = '0' and status_code in ('2','5','6') and hosp_code =#{hospCode}
and ( name like concat('%',#{name},'%') or in_no like concat('%',#{name},'%'))
order by crte_time ) m union all select n.* from ( select ov.id,'0' as lx,ov.register_no as visitNo, ov.name, ov.gender_code, ov.age, ov.age_unit_code,ov.hosp_code, ov.nation_code, ov.cert_no,cert_code, ov.birthday, ov.patient_code, ov.crte_time from outpt_visit ov left join outpt_settle os on os.visit_id = ov.id and os.hosp_code = ov.hosp_code and os.status_code ='0' where ov.patient_code = '0' and os.id is null and ov.hosp_code =#{hospCode}
and ( ov.name like concat('%',#{name},'%') or ov.register_no like concat('%',#{name},'%'))
order by crte_time ) n )A where A.lx = #{lx} and A.hosp_code = #{hospCode}

select id
from insure_setl_info
where insure_settle_id = #{settleId}

SELECT ic.id,ic.hosp_code,ic.visit_id, ic.item_code, ic.item_name, ic.item_id, ic.price, ic.num,ic.exec_time,ic.crte_name, ic.spec, ic.prep_code, ic.dosage,ic.dosage_unit_code, ic.total_num,ic.total_num_unit_code,ic.total_price, ic.preferential_price,ic.reality_price,ic.back_num, ic.back_code,ic.doctor_id, ic.doctor_name, (select iv.name from inpt_visit iv where iv.id = ic.visit_id and iv.hosp_code = ic.hosp_code and iv.id = #{id}) as name, ic.settle_code, ic.status_code, ic.crte_time, iim.deductible, iim.is_trans, iim.insure_item_code, iim.insure_item_name, iim.hosp_item_code, iim.hosp_item_name, ( CASE WHEN iic.transmit_code IS NULL OR iic.transmit_code = '' THEN '未传输' WHEN iic.transmit_code = '1' THEN '已传输' WHEN iim.is_trans = '0' THEN '未传输' END ) isTransmit FROM inpt_cost ic JOIN insure_item_match iim ON (ic.`item_id` = iim.`hosp_item_id` AND ic.`item_code` = iim.`hosp_item_type` AND ic.hosp_code = iim.hosp_code) LEFT JOIN insure_individual_cost iic ON ic.id = iic.cost_id AND ic.hosp_code = iic.hosp_code where 1=1 and ic.hosp_code = #{hospCode} and ic.visit_id = #{id} and IFNULL(iim.is_trans,'0') = #{isMatch} and IFNULL(iim.audit_code,'0') =#{isMatch} and IFNULL(iim.is_valid,'0') =#{isMatch} and IFNULL(iim.is_match,'0') =#{isMatch} and ic.settle_code in ('0','1') and iim.insure_reg_code = #{orgCode}
and IFNULL(iic.`transmit_code`,'0') = #{isTransmit}
and iim.audit_code='1' and iim.is_valid ='1'

SELECT ic.id,ic.hosp_code,ic.visit_id, ic.item_code, ic.item_name, ic.item_id, ic.price, ic.num,ic.crte_name, ic.spec, ic.prep_code, ic.dosage,ic.dosage_unit_code, ic.total_num,ic.total_price, ic.preferential_price,ic.reality_price,ic.back_num,ic.doctor_id, ic.doctor_name, (select iv.name from outpt_visit iv where iv.id = ic.visit_id and iv.hosp_code = ic.hosp_code and iv.id = #{id}) as name, ic.settle_code, ic.status_code, ic.crte_time, iim.deductible, iim.is_trans, iim.insure_item_code, iim.insure_item_name, iim.hosp_item_code, iim.hosp_item_name, ( CASE WHEN iic.transmit_code IS NULL OR iic.transmit_code = '' THEN '未传输' WHEN iic.transmit_code = '1' THEN '已传输' WHEN iim.is_trans = '0' THEN '未传输' END ) isTransmit FROM outpt_cost ic JOIN insure_item_match iim ON (ic.`item_id` = iim.`hosp_item_id` AND ic.`item_code` = iim.`hosp_item_type` AND ic.hosp_code = iim.hosp_code) LEFT JOIN insure_individual_cost iic ON ic.id = iic.cost_id AND ic.hosp_code = iic.hosp_code where 1=1 and ic.hosp_code = #{hospCode} and ic.visit_id = #{id} and IFNULL(iim.is_trans,'0') = #{isMatch} and IFNULL(iim.audit_code,'0') =#{isMatch} and IFNULL(iim.is_valid,'0') =#{isMatch} and IFNULL(iim.is_match,'0') =#{isMatch} and ic.settle_code in ('0','1') and iim.insure_reg_code = #{orgCode}
and IFNULL(iic.`transmit_code`,'0') = #{isTransmit}
and iim.audit_code='1' and iim.is_valid ='1'

select count(1)
from insure_setl_info
where insure_settle_id = #{insureSettleId}
  and visit_id = #{visitId}
  and hosp_code = #{hospCode}

select settle_no
from insure_setl_info
where visit_id = #{visitId}
  and hosp_code = #{hospCode}

select med_chrgitm,
       claa_sumfee,
       amt,
       clab_amt,
       fulamt_ownpay_amt,
       oth_amt
from insure_iteminfo
where visit_id = #{visitId}
  and hosp_code = #{hospCode}
  and insure_settle_id = #{insureSettleId}

select a.*
from insure_setl_info a
WHERE a.visit_id = #{visitId}
  and a.hosp_code = #{hospCode}
  and insure_settle_id = #{insureSettleId}

select isi.invoice_no, oi.prefix
from inpt_settle_invoice isi
         join outin_invoice oi
              on isi.hosp_code = oi.hosp_code and isi.invoice_id = oi.id
where isi.settle_id = #{settleId}
  and isi.visit_id = #{visitId}
  and isi.hosp_code = #{hospCode}
order by isi.print_time desc limit 1

select isi.invoice_no, oi.prefix
from outpt_settle_invoice isi
         join outin_invoice oi
              on isi.hosp_code = oi.hosp_code and isi.invoice_id = oi.id
where isi.settle_id = #{settleId}
  and isi.visit_id = #{visitId}
  and isi.hosp_code = #{hospCode}
    order by isi.print_time desc limit 1

select settle_no
from insure_setl_info
where visit_id = #{visitId}
  and hosp_code = #{hospCode}
  and insure_settle_id = #{insureSettleId}

select bd.nation_code as reflDeptCode
from inpt_bed_change ibc
         join base_dept bd on bd.id = ibc.after_dept_id
where ibc.visit_id = #{visitId}
  and ibc.hosp_code = #{hospCode}
  and ibc.change_code = '3'
order by ibc.crte_time desc
limit 1;

