mapper.select:
select iiv.id,iiv.medical_reg_no,iiv.aac001,iv.cert_code,iv.cert_no,iib.aac003,iib.aac004,iiv.visit_id,iiv.crte_time,iiv.visit_no,iiv.insuplc_admdvs,iv.name, iiv.mdtrt_cert_type,iiv.mdtrt_cert_no,iib.ipt_psn_sp_flag_type,iib.ipt_psn_sp_flag,iiv.aka130,iiv.aka130_name, iiv.insure_org_code,iiv.insure_reg_code,iiv.medicine_org_code,iib.ipt_psn_sp_flag_detl_id from insure_individual_visit iiv left join inpt_visit iv on iiv.hosp_code = iv.hosp_code and iiv.visit_id = iv.id left join insure_individual_basic iib on iiv.hosp_code = iib.hosp_code and iiv.mib_id = iib.id where iiv.hosp_code = #{hospCode} and iiv.is_hospital = '1'
and iiv.insure_org_code = #{insureOrgCode}
and iiv.crte_time >= #{startDate}
and iiv.crte_time <= #{endDate}
and ( iib.aac003 like concat('%',#{keyword},'%') or iiv.medical_reg_no like concat('%',#{keyword},'%') )

select id, hosp_code as hospCode, platform_type as platformType, platform_dictionary_name as platformDictionaryName, platform_dictionary_code as platformDictionaryCode, platform_code_name as platformCodeName, platform_code_value as platformCodeValue, his_dictionary_name as hisDictionaryName, his_dictionary_code as hisDictionaryCode, his_code_name as hisCodeName, his_code_value as hisCodeValue from settle_clock_match where hosp_code = #{hospCode}
and platform_type = #{platformType}
and platform_dictionary_code = #{platformDictionaryCode}
and his_code_value = #{hisCodeValue}
LIMIT 1

mapper.update:
update
    insure_individual_basic
set ipt_psn_sp_flag_type = #{iptPsnSpFlagType},
    ipt_psn_sp_flag =#{iptPsnSpFlag},
    ipt_psn_sp_flag_detl_id = #{iptPsnSpFlagDetlId}
where hosp_code = #{hospCode} and aac001 = #{aac001} and aac002 = #{mdtrtCertNo}

mapper.select:
select ifnull(insure_price, 0) as insurePrice, ifnull(personal_price, 0) as personalPrice, ifnull(person_price, 0) as personPrice, ifnull(rests_price, 0) as restsPrice, iis.insureSettleId, iis.medicalRegNo, iiv.aac001 as psnNo from insure_individual_settle iis left join insure_individual_visit iiv on iis.visit_id = iiv.visit_id and iis.medicalRegNo = iiv.medical_reg_no where iis.hosp_code = #{hospCode} and state ='0' and iis.settle_state ='1'
and iis.visit_id = #{visitId}
and iis.insureSettleId = #{insureSettleId}

