mapper.update:
update insure_individual_cost
set transmit_code ='1'
where hosp_code = #{hospCode}
  and visit_id = #{id}

mapper.select:
SELECT ic.id,ic.hosp_code,ic.visit_id, ic.item_code, ic.item_name, ic.item_id, ic.price, ic.num,ic.exec_time,ic.crte_name, ic.spec, ic.prep_code, ic.dosage,ic.dosage_unit_code, ic.total_num,ic.total_num_unit_code,ic.total_price, ic.preferential_price,ic.reality_price,ic.back_num, ic.back_code,ic.doctor_id, ic.doctor_name, (select iv.name from inpt_visit iv where iv.id = ic.visit_id and iv.hosp_code = ic.hosp_code and iv.id = #{id}) as name, ic.settle_code, ic.status_code, ic.crte_time, iim.deductible, iim.is_trans, iim.insure_item_code, iim.insure_item_name, iim.hosp_item_code, iim.hosp_item_name, ( CASE WHEN iic.transmit_code IS NULL OR iic.transmit_code = '' THEN '未传输' WHEN iic.transmit_code = '1' THEN '已传输' WHEN iim.is_trans = '0' THEN '未传输' END ) isTransmit FROM inpt_cost ic JOIN insure_item_match iim ON (ic.`item_id` = iim.`hosp_item_id` AND ic.`item_code` = iim.`hosp_item_type` AND ic.hosp_code = iim.hosp_code) LEFT JOIN insure_individual_cost iic ON ic.id = iic.cost_id AND ic.hosp_code = iic.hosp_code where 1=1 and ic.hosp_code = #{hospCode} and ic.visit_id = #{id} and IFNULL(iim.is_trans,'0') = #{isMatch} and IFNULL(iim.audit_code,'0') =#{isMatch} and IFNULL(iim.is_valid,'0') =#{isMatch} and IFNULL(iim.is_match,'0') =#{isMatch} and ic.settle_code in ('0','1') and iim.insure_reg_code = #{orgCode}
and IFNULL(iic.`transmit_code`,'0') = #{isTransmit}
and iim.audit_code='1' and iim.is_valid ='1'
and ic.item_name like concat('%',#{itemName},'%')

select distinct iv.id, iv.hosp_code, iv.profile_id, iv.in_profile, iv.in_no, iv.name, iv.gender_code,iv.bed_name, iv.age, iv.age_unit_code,iv.status_code,b.aka130_name as aka130Name,iv.in_disease_name,iv.in_disease_icd10, in_dept_name,iv.insure_org_code,iv.zz_doctor_name,iv.crte_time,iv.crte_name,b.insure_reg_code,b.medical_reg_no, b.is_half_settle,b.aac001 as psn_no, date_format( IFNULL( DATE_ADD((select iic.fee_end_time from insure_individual_cost iic where iic.visit_id = iv.id and iic.hosp_code = iv.hosp_code and iic.insure_settle_id is not null order by crte_time desc limit 1 ),INTERVAL 1 DAY), iv.in_time),"%Y-%m-%d") AS startDate, DATE_FORMAT(now(),"%Y-%m-%d") as endDate, b.aac001,b.aka130,b.medicine_org_code,b.visit_icd_code as visitIcdCode ,b.visit_icd_name as visitIcdName from inpt_visit iv inner join insure_individual_visit b on iv.id = b.visit_id and iv.hosp_code = b.hosp_code where iv.hosp_code=#{hospCode} and b.insure_settle_id is null and iv.patient_code !='0'
and iv.name like concat('%',#{name},'%')
and iv.status_code = #{statusCode}
and b.insure_org_code = #{regCode}
and b.medicine_org_code = #{orgCode}
and b.medicine_org_code = #{medicineOrgCode}
and iv.in_dept_id = #{inDeptId}
and ( iv.name like concat('%',#{keyword},'%'))
order by iv.crte_time desc

SELECT ic.id id, ic.hosp_code hospCode, ic.visit_id visitId, ic.baby_id babyId, ic.iat_id iatId, ic.iatd_group_no iatdGroupNo, ic.iatd_seq_no iatdSeqNo, ic.advice_exec_id adviceExecId, ic.source_code sourceCode, ic.source_id sourceId, ic.old_cost_id oldCostId, ic.source_dept_id sourceDeptId, ic.in_dept_id inDeptId, ic.bfc_id bfcId, ic.item_code itemCode, ic.item_id itemId, ic.item_name itemName, ic.price price, ic.num num, ic.spec spec, ic.prep_code prepCode, ic.dosage dosage, ic.dosage_unit_code dosageUnitCode, ic.usage_code usageCode, ic.rate_id rateId, ic.speed_code speedCode, ic.use_days useDays, ic.num_unit_code numUnitCode, ic.total_num totalNum, ic.total_num_unit_code totalNumUnitCode, ic.herb_note_code herbNoteCode, ic.use_code useCode, ic.herb_num herbNum, ic.total_price totalPrice, ic.preferential_price preferentialPrice, ic.reality_price realityPrice, ic.back_num backNum, ic.back_code backCode, ic.doctor_id doctorId, (select su.prac_certi_no from sys_user su where su.hosp_code = ic.hosp_code and su.id = ic.doctor_id ) as pracCertiNo, ic.doctor_name doctorName, ic.dept_id deptId, (select bd.name from base_dept bd where bd.hosp_code = ic.hosp_code and bd.id = ic.dept_id) as deptName, ic.phar_id pharId, ic.is_dist isDist, ic.is_give isGive, ic.is_ok isOk, ic.ok_id okId, ic.ok_name okName, ic.ok_time okTime, ic.crte_time crteTime, ic.cost_time costTime, ic.status_code statusCode, iic.cost_id costId, iic.feedetl_sn feedetlSn, iim.item_code insureMatchItemCode, iim.insure_reg_code insureRegCode, iim.molss_item_id molssItemId, iim.pqcc_item_id pqccItemId, iim.hosp_item_id hospItemId, iim.hosp_item_name hospItemName, iim.hosp_item_code hospItemCode, iim.hosp_item_type hospItemType, iim.hosp_item_spec hospItemSpec, iim.hosp_item_unit_code hospItemUnitCode, iim.hosp_item_prep_code hospItemPrepCode, iim.hosp_item_price hospItemPrice, iim.insure_item_name insureItemName, iim.insure_item_code insureItemCode, iim.insure_item_type insureItemType, iim.insure_item_spec insureItemSpec, iim.insure_item_unit_code insureItemUnitCode, iim.insure_item_prep_code insureItemPrepCode, iim.insure_item_price insureItemPrice, iim.deductible deductible, iim.standard_code standardCode, iim.check_price checkCode, iim.manufacturer manufacturer, iim.tcmdrug_used_way tcmdrugUsedWay, iim.lmt_user_flag lmtUserFlag, ic.is_reimburse isReimburse, iim.hilist_lv hilistLv,-- 医保目录等级 iim.chrg_type chrgType,-- 收费等级 iic.fulamt_ownpay_amt fulamtOwnpayAmt,-- 自费金额 iic.preselfpay_amt preselfpayAmt,-- 自付金额 ia.iatd_group_no iatdGroupNo,-- 医嘱组号 ia.exec_dept_id execDeptId,-- 执行科室 ia.long_start_time longStartTime,-- 医嘱开始时间 ia.crte_id crteId,-- 开嘱医生id ia.crte_name crteName,-- 开嘱医生姓名 ia.is_long isLong,-- 是否临时医嘱 (select bd.name from base_dept bd where bd.hosp_code = ic.hosp_code and bd.id = ia.exec_dept_id) as execDeptName-- 执行科室名称 FROM inpt_cost ic JOIN insure_item_match iim ON ic.`item_id` = iim.`hosp_item_id` AND ic.`item_code` = iim.`hosp_item_type` AND iim.`is_valid` = '1' AND ic.`hosp_code` = iim.`hosp_code` LEFT JOIN insure_individual_cost iic ON ic.`id` = iic.`cost_id` AND ic.`hosp_code` = iic.`hosp_code` left join inpt_advice ia on ic.iat_id = ia.id WHERE ic.`hosp_code` = #{hospCode}
AND ic.`settle_code` IN ('0', '1','2')
AND ic.`settle_code` IN ('0', '1')
AND ic.`visit_id` = #{visitId} AND IFNULL(iim.`is_match`, '0') = #{isMatch}
AND IFNULL(iic.transmit_code, '0') = #{transmitCode}
AND iim.insure_reg_code = #{insureRegCode}
and (ic.baby_id is null or trim(ic.baby_id) = '')
and date(ic.cost_time) >= STR_TO_DATE(#{feeStartDate},'%Y-%m-%d')
/*结束日期*/
and date(ic.cost_time) <= STR_TO_DATE(#{feeEndDate},'%Y-%m-%d')
AND ic.attribution_code = #{attributionCode}
AND (ic.attribution_code = '0' or ic.attribution_code is null or ic.attribution_code = '')
order by ic.total_price desc

select order_no
from insure_individual_cost
where hosp_code = #{hospCode}
  and visit_id = #{id}
order by crte_time desc
limit 1

