mapper.select:
SELECT ic.id id, ic.hosp_code hospCode, ic.visit_id visitId, ic.op_id opId, op.order_no rxNo, (select su.work_type_code from sys_user su where su.hosp_code = ic.hosp_code and ic.doctor_id = su.id) workTypeCode, ic.source_code sourceCode, ic.source_id sourceId, ic.old_cost_id oldCostId, ic.source_dept_id sourceDeptId, ic.bfc_id bfcId, ic.item_code itemCode, ic.item_id itemId, ic.item_name itemName, ic.price price, ic.num num, ic.spec spec, ic.prep_code prepCode, ic.dosage dosage, ic.dosage_unit_code dosageUnitCode, ic.usage_code usageCode, ic.rate_id rateId, ic.use_days useDays, ic.num_unit_code numUnitCode, ic.total_num totalNum, ic.herb_note_code herbNoteCode, ic.use_code useCode, ic.herb_num herbNum, ic.total_price totalPrice, ic.preferential_price preferentialPrice, ic.reality_price realityPrice, ic.back_num backNum, ic.doctor_id doctorId, ic.doctor_name doctorName, ic.dept_id deptId, (select bd.name from base_dept bd where bd.hosp_code = ic.hosp_code and bd.id = ic.dept_id) as deptName, ic.phar_id pharId, ic.is_dist isDist, ic.crte_time crteTime, ic.status_code statusCode, iim.item_code insureMatchItemCode, iim.insure_reg_code insureRegCode, iim.molss_item_id molssItemId, iim.pqcc_item_id pqccItemId, iim.hosp_item_id hospItemId, iim.hosp_item_name hospItemName, iim.hosp_item_code hospItemCode, iim.hosp_item_type hospItemType, iim.hosp_item_spec hospItemSpec, iim.hosp_item_unit_code hospItemUnitCode, iim.hosp_item_prep_code hospItemPrepCode, iim.hosp_item_price hospItemPrice, iim.insure_item_name insureItemName, iim.insure_item_code insureItemCode, iim.insure_item_type insureItemType, iim.insure_item_spec insureItemSpec, iim.insure_item_unit_code insureItemUnitCode, iim.insure_item_prep_code insureItemPrepCode, iim.insure_item_price insureItemPrice, iim.deductible deductible, iim.standard_code standardCode, iim.check_price checkCode, iim.lmt_user_flag lmtUserFlag, ic.is_reimburse isReimburse, iim.manufacturer manufacturer, iim.tcmdrug_used_way tcmdrugUsedWay, iim.hilist_price hilistPrice, iim.hilist_lv hilistLv, iim.chrg_type chrgType, (select su.prac_certi_no from sys_user su where su.hosp_code = ic.hosp_code and su.id = ic.doctor_id ) as pracCertiNo FROM outpt_cost ic left join outpt_prescribe op on op.id = ic.op_id and op.hosp_code = ic.hosp_code and ic.visit_id = op.visit_id JOIN insure_item_match iim ON ic.`item_id` = iim.`hosp_item_id` AND ic.`item_id` = iim.`hosp_item_id` AND iim.`is_match` = '1' AND ic.`hosp_code` = iim.`hosp_code` LEFT JOIN insure_individual_cost iic ON ic.`id` = iic.`cost_id` AND ic.`hosp_code` = iic.`hosp_code`
WHERE ic.`hosp_code` = #{hospCode} and op.is_submit='1'
AND ic.`settle_code` IN ('0', '1','2')
AND ic.`settle_code` IN ('0', '1')
AND ic.`visit_id` = #{visitId} AND IFNULL(iim.`is_match`, '0') = #{isMatch}
AND IFNULL(iic.transmit_code, '0') = #{transmitCode}
AND iim.insure_reg_code = #{insureRegCode}
and (ic.op_id in
#{item}
or ic.op_id is null )
order by ic.crte_time desc

