mapper.select:
select * from( select iic.id, iic.hosp_code, iic.visit_id, iic.settle_id, iic.is_hospital, iic.visit_no, iic.discharge_dn_code, iic.insure_org_code,iic.insure_reg_code,iic.hosp_exem_amount,iic.maf_pay, iic.medicine_org_code,iic.discharge_dn_name, discharged_date, iic.discharged_case, '普通结算' as settleway, iic.total_price, iic.plan_price, iic.serious_price, iic.civil_price, iic.retire_price, iic.personal_price, iic.person_price, iic.hosp_price, iic.before_settle, iic.last_settle, iic.rests_price, iic.assign_price, iic.starting_price, iic.top_price, iic.plan_account_price, iic.portion_price, iic.state, (IF(iic.settle_state = '0', '试算', '结算')) as settleState, iic.costbatch, iic.aka130,iic.injury_borth_sn, iic.is_account, iic.remark, iic.crte_id,iic.insureSettleId, iic.crte_name, iic.crte_time,iiv.visit_drpt_name as visitDrptName, iiv.aka130_name as aka130Name,iiv.aac001,iiv.bka006, iiv.bka006_name, iiv.visit_icd_code as visitIcdCode,iiv.visit_icd_name as visitIcdName, iib.aac003,iib.aac002, CASE iib.aac004 WHEN '男' THEN 2 WHEN '女' THEN 1 ELSE iib.aac004 END aac004,iib.aae005, ( case iic.is_hospital when '1' then (select patient_code from inpt_visit iv where iv.hosp_code = iic.hosp_code and iv.id = iic.visit_id) when '0' then (select patient_code from outpt_visit ov where ov.hosp_code = iic.hosp_code and ov.id = iic.visit_id) end ) as patientCode, iv.in_time, iv.out_time, iib.bka035, iib.bka035_name, iiv.insuplc_admdvs as insuplcAdmdvs from insure_individual_settle iic left join insure_individual_visit iiv on iic.visit_id = iiv.visit_id and iic.hosp_code = iiv.hosp_code join insure_individual_basic iib on iiv.mib_id = iib.id and iiv.hosp_code = iib.hosp_code left join inpt_visit iv on iiv.visit_id = iv.id and iiv.hosp_code = iv.hosp_code where iic.hosp_code = #{hospCode} and iic.is_cancel ='0'
and ( iiv.visit_no like concat('%',#{keyWord},'%') or iib.aac003 like concat('%',#{keyWord},'%') or iib.aac002 like concat('%',#{keyWord},'%') )
and iic.state = '0' and iib.aac003 is not null
and iic.is_hospital =#{isHospital}
and iic.aka130 =#{aka130}
/*开始日期*/
and date(iic.crte_time) >= date(#{startDate})
/*结束日期*/
and date(iic.crte_time) <= date(#{endDate})
order by iic.crte_time )a where 1=1
and a.isUpload = #{isUpload}
order by a.crte_time

mapper.delete:
delete
from insure_individual_settle
where hosp_code = #{hospCode}
  AND visit_id = #{visitId}
  AND settle_state = #{settleState}

mapper.select:
SELECT

FROM insure_individual_settle iis
where
iis.hosp_code = #{hospCode} and iis.is_cancel ='0'

    AND iis.visit_id = #{visitId}


    AND iis.settle_id = #{settleId}


    AND iis.state = #{state}

update.foreach:
update insure_individual_settle

    remote_msg_id=#{item.remoteMsgId}

where id = #{item.id} and hosp_code=#{item.hospCode}

mapper.select:
SELECT

FROM insure_individual_settle iis
where
iis.hosp_code = #{hospCode}
AND iis.visit_id = #{visitId}
AND iis.settle_id = #{settleId}
  and iis.is_cancel ='0'

    and iis.state = #{state}

select * from insure_individual_settle where hosp_code =#{hospCode}  and is_cancel='0'

    and visit_id = #{visitId}


    and insureSettleId = #{insureSettleId}


    and state = #{state}


    and settle_state = #{settleState}

mapper.update:
update insure_individual_settle set personal_price =#{personalPrice},
                                    before_settle =#{beforeSettle},
                                    last_settle =#{beforeSettle} -#{personalPrice}
where id =#{id} and hosp_code =#{hospCode}

update outpt_settle set acct_pay =#{personalPrice},
                       self_price =self_price - #{personalPrice}
where id =#{settleId} and hosp_code =#{hospCode}

update inpt_settle set acct_pay =#{personalPrice},
                       self_price =self_price - #{personalPrice},
settle_take_price =#{settleTakePrice},
                       settle_back_price =#{settleBackPrice}
where id =#{settleId} and hosp_code =#{hospCode}
