mapper.update:
update insure_individual_visit
set insure_settle_id =null
where hosp_code = #{hospCode}
  and visit_id = #{visitId}
  and medical_reg_no = #{medicalRegNo}

mapper.select:
SELECT (select iib.baa301 from insure_individual_basic iib where iiv.mib_id = iib.id and iiv.hosp_code = iib.hosp_code) as baa301, (select iib.akc252 from insure_individual_basic iib where iiv.mib_id = iib.id and iiv.hosp_code = iib.hosp_code) as akc252, (select iib.card_iden from insure_individual_basic iib where iiv.mib_id = iib.id and iiv.hosp_code = iib.hosp_code) as cardIden, (select iv.out_dept_id from inpt_visit iv where iv.id = iiv.visit_id and iv.hosp_code = iiv.hosp_code) as outDeptId, (select iv.out_dept_name from inpt_visit iv where iv.id = iiv.visit_id and iv.hosp_code = iiv.hosp_code) as outDeptName, (select iib.aae140 from insure_individual_basic iib where iiv.mib_id = iib.id and iiv.hosp_code = iib.hosp_code) as aae140,iiv.is_out as isOut,
FROM insure_individual_visit iiv where iiv.hosp_code = #{hospCode}
AND iiv.visit_id = #{visitId}
AND iiv.mib_id = #{mibId}
AND iiv.is_out = #{isOut}
AND iiv.medical_reg_no = #{medicalRegNo}

SELECT *
FROM insure_individual_visit iiv
where
iiv.hosp_code = #{hospCode}

    AND iiv.visit_No = #{visitNo}

SELECT iiv.*, ov.doctor_id as doctorId ,ov.doctor_name as doctorName,iv.in_profile, ov.phone as outPhone,ov.visit_time as visitTime,iib.aac004 as gend,iib.aac002,iib.bka035 ,iib.bka008, iv.out_dept_id as outDeptId,iv.out_dept_name as outDeptName,iv.out_mode_code as outModeCode,iv.address,iv.phone, iv.phone as inptPhone ,iv.out_time as outTime ,iv.in_time as inTime,iv.in_disease_name as inDiseaseName,iv.out_disease_name as outDiseaseName,iv.out_disease_icd10 as outDiseaseIcd10,iv.total_in_days, (IF(iiv.is_hospital ='1', (select su.prac_certi_no from sys_user su where su.hosp_code = iv.hosp_code and su.id = iv.zz_doctor_id ), (select su.prac_certi_no from sys_user su where su.hosp_code = ov.hosp_code and su.id = ov.doctor_id )) ) as pracCertiNo, iv.zz_doctor_id as zzDoctorId ,iv.zz_doctor_name as zzDoctorName, iv.patient_code as patientType, iib.baa301,iib.aae140,iib.card_iden as cardIden,iib.aac001,iib.aac003,iib.aac006,iib.aac004, if((case when iiv.is_hospital ='1' then DATEDIFF(date_format( iv.out_time, '%Y-%m-%d' ),date_format( iv.in_time, '%Y-%m-%d' )) END) = 0,1,(case when iiv.is_hospital ='1' then DATEDIFF(date_format( iv.out_time, '%Y-%m-%d' ),date_format( iv.in_time, '%Y-%m-%d' )) END)) as hospitalDay,iiv.settle_count,iiv.is_half_settle,ic.mdtrtarea_admvs,ic.is_remote FROM insure_individual_visit iiv left join insure_individual_basic iib on iib.id = iiv.mib_id and iib.hosp_code = iiv.hosp_code left join inpt_visit iv on iv.id = iiv.visit_id and iv.hosp_code = iiv.hosp_code left join outpt_visit ov on ov.id = iiv.visit_id and ov.hosp_code = iiv.hosp_code join insure_configuration ic on ic.code = iiv.insure_reg_code and ic.hosp_code = iiv.hosp_code WHERE iiv.visit_id = #{id} and iiv.hosp_code =#{hospCode}
and iiv.medical_reg_no = #{medicalRegNo}
and iiv.is_half_settle = #{inHalfSettleId}
and iiv.insure_settle_id is null
and ( iv.in_no like concat('%',#{keyword},'%') or iv.name like concat('%',#{keyword},'%') )

SELECT iiv.*, ov.doctor_id as doctorId ,ov.doctor_name as doctorName,iv.in_profile, ov.phone as outPhone, iv.phone as inptPhone ,iv.out_time as outTime ,iv.in_time as inTime,iv.in_disease_name as inDiseaseName,iv.out_disease_name as outDiseaseName, iv.zz_doctor_id as zzDoctorId ,iv.zz_doctor_name as zzDoctorName, iv.patient_code as patientType, iib.baa301,iib.aae140,iib.card_iden as cardIden,iib.aac001,iib.aac003, (case when iiv.is_hospital ='1' then timestampdiff(day,iv.in_time,iv.out_time) END) as hospitalDay FROM insure_individual_visit iiv left join insure_individual_basic iib on iib.id = iiv.mib_id and iib.hosp_code = iiv.hosp_code left join inpt_visit iv on iv.id = iiv.visit_id and iv.hosp_code = iiv.hosp_code left join outpt_visit ov on ov.id = iiv.visit_id and ov.hosp_code = iiv.hosp_code WHERE iiv.hosp_code =#{hospCode} and iiv.insure_settle_id is null and iiv.visit_id=#{id}
and iiv.medical_reg_no = #{medicalRegNo}
and ( iv.in_no like concat('%',#{keyword},'%') or iv.name like concat('%',#{keyword},'%') )

select c.mdtrtarea_admvs, v.insuplc_admdvs, v.aac001, v.medical_reg_no, v.insure_org_code
from insure_configuration c
         join insure_individual_visit v on c.hosp_code = v.hosp_code
where v.hosp_code = #{hospCode}
  and v.visit_id = #{visitId}

mapper.delete:
delete
from insure_individual_visit
where visit_id = #{visitId}
  and hosp_code = #{hospCode}

mapper.select:
select id, hosp_code, visit_id, register_no, name, gender_code, age, birthday, cert_code, cert_no, phone, source_bz_code, visit_code, source_tj_code, source_tj_remark, register_time, cf_id, cq_id, dq_id, dr_id, dept_id, dept_name, doctor_id, doctor_name, is_cancel, is_first_visit, is_add, crte_id, crte_name, crte_time from outpt_register where hosp_code = #{hospCode}
and id = #{id}
and visit_id = #{visitId}

select id, hosp_code, visit_id, mib_id, insure_org_code, insure_reg_code, medicine_org_code, is_hospital, visit_no, aac001, medical_reg_no, aka130, aka130_name, bka006, bka006_name, injury_borth_sn, visit_icd_code, visit_icd_name, visit_time, crte_id, crte_name, crte_time, insuplc_admdvs, mdtrt_cert_type, mdtrt_cert_no, omsgid, oinfno, is_out, insure_settle_id, settle_count, is_half_settle from insure_individual_visit where hosp_code = #{hospCode} and visit_id = #{visitId} and insure_settle_id is null
and medical_reg_no = #{medicalRegNo}
order by crte_time desc limit 1

select settle_count
from insure_individual_visit
where hosp_code = #{hospCode}
  and visit_id = #{visitId}
  and medical_reg_no = #{medicalRegNo}
  and insure_settle_id is not null
order by crte_time desc
limit 1

select *
from insure_individual_visit
where hosp_code = #{hospCode}
  and medical_reg_no = #{mdtrt_id}
  and aac001 = #{psn_no}

select max(crte_time) as maxRegister, min(crte_time) as minRegister
from insure_individual_visit
where hosp_code = #{hospCode}
  and visit_id = #{id}
group by crte_time

select a.*,b.name,b.status_code,b.age,b.gender_code,b.in_dept_name,b.bed_name,b.in_time,b.patient_code

from insure_individual_visit a join  inpt_visit b on a.visit_id = b.id
where a.hosp_code = #{hospCode}

    and a.medicine_org_code = #{medicineOrgCode}


    and b.status_code = #{statusCode}


    and (
    b.name like concat('%',#{keyWord},'%')
    or b.in_no like concat('%',#{keyWord},'%')
    )

order by a.crte_time desc

select a.*,b.name,b.age,b.gender_code,b.dept_name,b.visit_time,b.patient_code
from insure_individual_visit a join  outpt_visit b on a.visit_id = b.id
where a.hosp_code = #{hospCode}

    and a.medicine_org_code = #{medicineOrgCode}



    and (
    b.name like concat('%',#{keyWord},'%')
    or b.visit_no like concat('%',#{keyWord},'%')
    )

order by a.crte_time desc

select id, hosp_code, medis_code, psn_no, visit_id, medical_reg_no,
       insutype, year, cum_type_code, cum_ym, crte_id, cum, crte_name, crte_time
from insure_patient_sum where hosp_code = #{hospCode} and psn_no =#{aac001} and visit_id =#{visitId}
and medical_reg_no = #{medicalRegNo}

    and insure_settle_id =#{insureSettleId}
