mapper.select:
select id, hosp_code, insure_reg_code, item_mark, item_code, item_name, item_type, item_dosage, item_spec, item_price, item_unit_code, prod, deductible, check_price, directory, take_date, lose_date, pym, wbm, is_valid, crte_id, crte_name, crte_time, ver, ver_name, down_load_type sp_drug_flag, sp_lmtpric_drug_flag, lmt_user_flag,hosp_item_name,hosp_item_code from insure_item where hosp_code =#{hospCode}
and ( item_code like concat('%',#{keyword},'%') or item_name like concat('%',#{keyword},'%') or pym like concat('%',#{keyword},'%') or wbm like concat('%',#{keyword},'%') or hosp_item_name like concat('%',#{keyword},'%') or hosp_item_code like concat('%',#{keyword},'%') )
and insure_reg_code =#{insureRegCode}
and down_load_type =#{downLoadType}
and down_load_type in
#{item}

select ID, HOSP_CODE, INSURE_REG_CODE, ITEM_MARK, ITEM_CODE, ITEM_NAME,ITEM_TYPE,
ITEM_DOSAGE, ITEM_SPEC, ITEM_PRICE, ITEM_UNIT_CODE, PROD, DEDUCTIBLE, CHECK_PRICE,
DIRECTORY, TAKE_DATE, LOSE_DATE, PYM, WBM, IS_VALID, CRTE_ID, CRTE_NAME, CRTE_TIME from insure_item
where hosp_code =#{hospCode}

    and ( item_code like concat('%',#{keyWord},'%')
    or item_name like concat('%',#{keyWord},'%')
    or pym like concat('%',#{keyWord},'%')
    or wbm like concat('%',#{keyWord},'%')
    )


    and insure_reg_code =#{insureRegCode}


    and down_load_type =#{downLoadType}

select *
from insure_item
where hosp_code = #{hospCode}
  and id = #{id}

mapper.delete:
delete from insure_item
where hosp_code = #{hospCode}
  and insure_reg_code = #{insureRegCode}
  and down_load_type = #{downLoadType}
