mapper.select:
select t1.queryType, t1.visitId, t1.hospCode, t1.name, t1.patientInfo, t1.profileNo, t1.visitNo, t1.certCode, t1.certNo, t1.visitDeptName, t1.visitDeptId, t1.visitTime, t1.bedName, t1.patientCode from ( SELECT '1' as queryType, c.id as visitId, c.hosp_code as hospCode, c.name, CONCAT( c.name, '/', (case c.gender_code when '1' then '男' when '2' then '女' else '' end), '/', c.age, (case c.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' else '天' end) ) as patientInfo, c.out_profile as profileNo, c.visit_no as visitNo, c.cert_code as certCode, c.cert_no certNo, c.dept_id as visitDeptId, c.dept_name as visitDeptName, date_format(c.visit_time, '%Y-%m-%d') as visitTime, '' as bedName, c.patient_code as patientCode FROM outpt_cost a /*LEFT JOIN base_drug b on a.hosp_code = b.hosp_code and a.item_id = b.id*/ LEFT JOIN outpt_visit c on a.hosp_code = c.hosp_code and a.visit_id = c.id LEFT JOIN insure_item_match d on a.hosp_code = d.hosp_code and a.item_id = d.hosp_item_id WHERE a.hosp_code = #{hospCode} and a.item_code = #{itemCode} /*药品/材料*/ and a.status_code = '0' /*正常状态*/ and a.settle_code = '2' /*已结算*/ and a.is_upload = '0' /*未上传*/ and c.is_visit = '1' /*已接诊*/ and d.audit_code = '1' /*医保项目已审核*/ and d.is_match = '1' /*医保项目已匹配*/ and d.is_trans = '1' /*医保项目已传输*/ and d.is_valid = '1' /*医保项目有效*/ and d.insure_reg_code = #{insureRegCode} /*医保机构编码*/ GROUP BY a.visit_id
UNION ALL
SELECT '2' as queryType, c.id as visitId, c.hosp_code as hospCode, c.name, CONCAT( c.name, '/', (case c.gender_code when '1' then '男' when '2' then '女' else '' end), '/', c.age, (case c.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' else '天' end) ) as patientInfo, c.in_profile as profileNo, c.in_no as visitNo, c.cert_code as certCode, c.cert_no certNo, c.in_dept_id as visitDeptId, c.in_dept_name as visitDeptName, date_format(c.in_time, '%Y-%m-%d') as visitTime, c.bed_name as bedName, c.patient_code as patientCode FROM inpt_cost a /*LEFT JOIN base_drug b on a.hosp_code = b.hosp_code and a.item_id = b.id*/ LEFT JOIN inpt_visit c on a.hosp_code = c.hosp_code and a.visit_id = c.id LEFT JOIN insure_item_match d on a.hosp_code = d.hosp_code and a.item_id = d.hosp_item_id WHERE a.hosp_code = #{hospCode} and a.item_code = #{itemCode} /*药品/材料*/ and a.status_code = '0' /*正常状态*/ and a.settle_code = '2' /*已结算*/ and a.is_upload = '0' /*未上传*/ and d.audit_code = '1' /*医保项目已审核*/ and d.is_match = '1' /*医保项目已匹配*/ and d.is_trans = '1' /*医保项目已传输*/ and d.is_valid = '1' /*医保项目有效*/ and d.insure_reg_code = #{insureRegCode} /*医保机构编码*/ and (c.status_code = '2' or c.status_code = '5' or c.status_code = '6' or c.status_code = '7') /*在院病人*/ GROUP BY a.visit_id ) t1 where 1=1
/*页面搜索条件：姓名、床号、就诊号、证件号*/ and (t1.name like concat('%', #{keyword}, '%') or t1.bedName = #{keyword} or t1.visitNo = #{keyword} or t1.certNo = #{keyword})
and t1.queryType = #{queryType}
and t1.queryType in ('1', '2')
and t1.visitTime >= #{startDate}
and t1.visitTime <= #{endDate}
order by t1.queryType, t1.visitNo, t1.bedName, t1.visitTime

mapper.update:
update outpt_cost
set is_upload = '1'
where hosp_code = #{hospCode}
 and id in

    #{id}

;
update inpt_cost
set is_upload = '1'
where hosp_code = #{hospCode}
and id in

    #{id}

;
