mapper.select:
SELECT e.insure_reg_code, t.aac001 as psnNo, (select aac003 from insure_individual_basic c where c.id = t.mib_id) as psnName, e.crte_time, e.omsgid, e.oinfno, l.name oinfname FROM insure_individual_settle e, insure_individual_visit t, sys_code_detail l WHERE e.hosp_code = t.hosp_code AND e.visit_id = t.visit_id AND e.hosp_code = l.hosp_code AND e.oinfno = l.value AND l.c_code = 'YBCZLX' AND e.hosp_code = #{hospCode}
AND t.aac001 like concat('%',#{psnNo},'%')
AND e.oinfno = #{oinfno}
and e.crte_time > STR_TO_DATE(#{startDate},'%Y-%m-%d')
and e.crte_time <= DATE_ADD(STR_TO_DATE(#{endDate},'%Y-%m-%d'),INTERVAL 1 day)
union all
select t.insure_reg_code, t.aac001 as psnNo, (select aac003 from insure_individual_basic c where c.id = t.mib_id) as psnName, t.crte_time, t.omsgid, t.oinfno, l.name oinfname from insure_individual_visit t, sys_code_detail l where t.hosp_code = l.hosp_code AND t.oinfno = l.value AND l.c_code = 'YBCZLX' AND t.hosp_code = #{hospCode}
AND t.aac001 like concat('%',#{psnNo},'%')
AND t.oinfno = #{oinfno}
and t.crte_time > STR_TO_DATE(#{startDate},'%Y-%m-%d')
and t.crte_time <= DATE_ADD(STR_TO_DATE(#{endDate},'%Y-%m-%d'),INTERVAL 1 day)

SELECT max(e.insure_reg_code) as insureRegCode, t.medicine_org_code fixmedins_code, e.clr_type, e.clr_way, e.clr_optins, sum(e.person_price) as person_price, sum(e.total_price) as medfee_sumamt, (sum(e.insure_price) - sum(e.personal_price)) as fund_pay_sumamt, sum(e.personal_price) as acct_pay, count(1) as fixmedins_setl_cnt FROM insure_individual_settle e, insure_individual_visit t WHERE e.hosp_code = t.hosp_code AND e.visit_id = t.visit_id AND e.settle_state = '1' and e.insureSettleId is not null and date(e.crte_time) >= date(#{startDate}) and date(e.crte_time) <= date(#{endDate}) and e.clr_type = #{clrType} and e.clr_way = #{clrWay} and e.insure_reg_code = #{insureRegCode}
and e.state = '0'
group by e.clr_type,e.clr_way,t.medicine_org_code

SELECT DISTINCT ( CONCAT( e.settle_id, e.visit_id ) ) se_vi_id, IF ( LEFT ( e.insure_reg_code, 4 ) = LEFT ( t.insuplc_admdvs, 4 ), '本市参保', IF ( LEFT ( e.insure_reg_code, 4 ) != LEFT ( t.insuplc_admdvs, 4 ) AND LEFT ( e.insure_reg_code, 2 ) = LEFT ( t.insuplc_admdvs, 2 ), '省内异地', '跨省异地' ) ) type, c.aac003 AS psnName, e.insure_reg_code AS insureRegCode, t.aac001 AS psn_no, e.settle_id, e.visit_id, t.medical_reg_no AS mdtrt_id, e.insureSettleId AS setl_id, c.aae140 AS insutype, t.is_hospital, e.clr_type, IF ( e.state = '0', '0', '1' ) AS refd_setl_flag, e.state, e.clr_optins, t.insure_reg_code AS setl_optins, e.total_price AS medfee_sumamt, case when t.aka130 = '2102' then e.insure_price - e.personal_price - e.rests_price when t.aka130 = '990101' then e.insure_price - e.personal_price - e.hosp_price when LEFT ( e.insure_reg_code, 2 )='43' then e.insure_price - e.personal_price - e.hosp_price else e.insure_price - e.personal_price end as fund_pay_sumamt, e.personal_price AS acct_pay,t.aka130 FROM insure_individual_settle e, insure_individual_visit t, insure_individual_basic c WHERE e.hosp_code = t.hosp_code AND e.visit_id = t.visit_id AND c.hosp_code = t.hosp_code AND c.id = t.mib_id AND c.aae140 IS NOT NULL AND e.settle_state = '1' AND e.is_cancel = '0' AND e.insureSettleId IS NOT NULL AND e.insure_reg_code = #{insureRegCode} AND e.hosp_code = #{hospCode} AND e.insure_price > 0
AND t.aac001 like concat('%',#{psnNo},'%')
AND e.insure_reg_code = #{insureRegCode}
/*开始日期*/
and date(e.crte_time) >= date(#{startDate})
/*结束日期*/
and date(e.crte_time) <= date(#{endDate})
and e.clr_type = #{clrType}
and e.state = '0'
and e.clr_optins = #{clrOptins}
GROUP BY se_vi_id

