e.insure_reg_code AS insureRegCode, e.settle_id, e.visit_id, e.insureSettleId AS setl_id, e.clr_type, IF ( e.state = '0', '0', '1' ) AS refd_setl_flag, e.state, e.clr_optins, e.total_price AS medfee_sumamt, ( e.insure_price - e.personal_price ) AS fund_pay_sumamt, e.personal_price AS acct_pay, e.person_price, e.plan_price, e.serious_price, e.civil_price, e.retire_price, e.hosp_price, e.rests_price, e.maf_pay, t.aac001 AS psn_no, t.medicine_org_code, t.aka130, t.aka130_name aka130Name, t.medical_reg_no AS mdtrt_id, t.is_hospital, t.insure_reg_code AS setl_optins, c.aac003 AS psnName, c.aae140 AS insutype FROM insure_individual_settle e, insure_individual_visit t, insure_individual_basic c WHERE e.hosp_code = t.hosp_code AND e.visit_id = t.visit_id AND c.hosp_code = t.hosp_code AND c.id = t.mib_id AND c.aae140 IS NOT NULL AND e.settle_state = '1' AND e.insureSettleId IS NOT NULL AND e.state = '0' AND e.insure_reg_code = #{insureRegCode} AND e.hosp_code = #{hospCode} -- AND ifnull(t.is_one_settle,0) != '1' AND e.insure_price > 0
and c.aae140 = #{aae140}
/*开始日期*/
and date(e.crte_time) >= date(#{startDate})
/*结束日期*/
and date(e.crte_time) <= date(#{endDate})
AND t.aka130 in
#{item}
GROUP BY se_vi_id ) A group by isRemote

